      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library LibReentrancyGuard {
    bytes32 internal constant REENTRANCY_GUARD_SLOT =
        keccak256("diamond.standard.reentrancyguard.reentrancyguard");

    uint256 internal constant _NOT_ENTERED = 1;
    uint256 internal constant _ENTERED = 2;

    struct ReentrancyGuardStorage {
        uint256 _status;
    }

    function getStorage()
        internal
        pure
        returns (ReentrancyGuardStorage storage rs)
    {
        bytes32 position = REENTRANCY_GUARD_SLOT;
        assembly {
            rs.slot := position
        }
    }

    function setInitStatus() internal {
        ReentrancyGuardStorage storage rs = getStorage();
        rs._status = _NOT_ENTERED;
    }

    function nonReentrantBefore() internal {
        LibReentrancyGuard.ReentrancyGuardStorage
            storage rs = LibReentrancyGuard.getStorage();
        // On the first call to nonReentrant, _status will be _NOT_ENTERED
        require(
            rs._status != LibReentrancyGuard._ENTERED,
            "ReentrancyGuard: reentrant call"
        );

        // Any calls to nonReentrant after this point will fail
        rs._status = LibReentrancyGuard._ENTERED;
    }

    function nonReentrantAfter() internal {
        LibReentrancyGuard.ReentrancyGuardStorage
            storage rs = LibReentrancyGuard.getStorage();
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        rs._status = LibReentrancyGuard._NOT_ENTERED;
    }
}
