    "contracts/Lottery.sol": {
      "content": "//SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";

import "./LotterySFT.sol";
import "./interface/IArithmetic.sol";
import "./interface/ILottery.sol";
import "./interface/IAgency.sol";
import "./access/Roles.sol";
import "./utils/Utils.sol";
import "./utils/Uint8a10.sol";

contract Lottery is VRFConsumerBaseV2, ILottery, Roles {

    using SafeMath for uint;
    using Uint8a10 for uint;

    IERC20 private immutable USDT;
    LotterySFT private SFT;
    IArithmetic private ARITHMETIC;
    IAgency private AGENCY;
    address public manager;

    struct UserAccount {
        bytes32 code;
        uint expend;
        uint award;
        uint agencyIncome;
        uint accAward;
        uint accAgencyIncome;
        uint accSharesIncome;
        bool topAgent;
        address[] recommenders;
    }

    struct VRFRequest {
        address player;
        bool single;
    }

    uint8 decimals = 6;
    address public constant OFFICIAL_AGENCY = address(1989);

    uint private ONE_UINTS = 1 * 10 ** decimals;
    uint private ONCE_PRICE = 2 * 10 ** decimals;
    uint private TEN_PRICE = 10 * ONCE_PRICE;

    uint public SHARES_SLOT_ID = 100;

    // pool
    uint public basePool;
    uint public gainPool;
    uint public sharesPool;
    uint public specialPool;
    uint public agencyPool;
    uint public idlePool;
    uint public totalPool;

    uint public accumulateBet = 0;
    uint private _p_stage = 100_000 * 10 ** decimals;
    uint private _p_goal = 100_000 * 10 ** decimals;

    // settings
    bool private _presale;
    bool private _pause;
    uint public totalSupplyShares;

    mapping(address => UserAccount) private _accounts;

    // whitelist
    bytes32 private _publicCode;
    mapping(bytes32 => address) private _codes;
    mapping(address => uint) private _whitelist;

    // request
    mapping(uint => bool) private _reqExists;
    mapping(uint => VRFRequest) private _requests;

    // VRF
    VRFCoordinatorV2Interface COORDINATOR;
    uint64 private subscriptionId = 539;
    uint32 private callbackGasLimit = 400000;
    uint16 private requestConfirmations = 3;
    uint32 private numWords = 1;
    bytes32 private keyHash = 0xcc294a196eeeb44da2888d17c0625cc88d70d9760a69d58d853ba6581a9ab0cd;
    address private coordinator = 0xAE975071Be8F8eE67addBC1A82488F1C24858067;

    modifier notPaused() {
        require(!_pause, "draw suspended");
        _;
    }

    constructor()
        VRFConsumerBaseV2(coordinator) 
        Roles(_msgSender())
    {
        USDT = IERC20(0xc2132D05D31c914a87C6611C10748AEb04B58e8F);

        // VRF
        COORDINATOR = VRFCoordinatorV2Interface(coordinator);
    }

    // user function

    function drawACard(string memory _code) external override notPaused {
        address player = _msgSender();
        address supplier = address(this);

        if (_presale) {
            uint quota = _whitelist[player];
            require(quota > 0, "Insufficient whitelist quota");
            require(quota >= ONCE_PRICE, "Insufficient whitelist quota");

            _updateWLQuota(player, quota - ONCE_PRICE);
        }

        require(USDT.allowance(player, supplier) >= ONCE_PRICE, "Insufficient approved balance");
        USDT.transferFrom(player, supplier, ONCE_PRICE);

        UserAccount storage account = _accounts[player];
        if (account.recommenders.length == 0 && !account.topAgent) {
            address recommender = _codes[Utils.stringToBytes32(_code)];
            if (recommender != player) {
                if (recommender == address(0)) {
                    account.topAgent = true;
                } else {
                    _updateRecommender(player, recommender);
                }
            }
        }

        _mintShares(1);
        _instillPool(ONCE_PRICE);

        _requestForDraw(true);
    }

    function draw10Cards(string memory _code) external override notPaused {
        address player = _msgSender();
        address supplier = address(this);

        if (_presale) {
            uint quota = _whitelist[player];
            require(quota > 0, "Insufficient whitelist quota");
            require(quota >= TEN_PRICE, "Insufficient whitelist quota");

            _updateWLQuota(player, quota - TEN_PRICE);
        }

        require(USDT.allowance(player, supplier) >= TEN_PRICE, "Insufficient approved balance");
        USDT.transferFrom(player, supplier, TEN_PRICE);

        UserAccount storage account = _accounts[player];
        if (account.recommenders.length == 0 && !account.topAgent) {
            address recommender = _codes[Utils.stringToBytes32(_code)];
            if (recommender != player) {
                if (recommender == address(0)) {
                    account.topAgent = true;
                } else {
                    _updateRecommender(player, recommender);
                }
            }
        }

        _mintShares(10);
        _instillPool(TEN_PRICE);

        _requestForDraw(false);
    }

    function burnShares(uint _value) external override {
        address sender = _msgSender();
        address self = address(this);

        SFT.burn(sender, SHARES_SLOT_ID, _value);

        uint amount = _value.mul(sharesPool).div(totalSupplyShares);
        require(amount <= sharesPool, "Burn failed");
        require(USDT.balanceOf(self) >= amount, "Insufficient pool balance");

        totalSupplyShares = totalSupplyShares.sub(_value);
        sharesPool = sharesPool.sub(amount);
        _accounts[sender].accSharesIncome += amount;
        USDT.transfer(sender, amount);

        emit BurnShares(sender, _value, amount);
    }

    function withdrawAward(uint _value) external override {
        address sender = _msgSender();
        address self = address(this);

        require(_accounts[sender].award >= _value, "Can not exceed your profits received");
        require(USDT.balanceOf(self) >= _value, "Insufficient pool balance");

        _accounts[sender].award -= _value;
        USDT.transfer(sender, _value);

        emit PlayerWithdrawAward(sender, _value);
    }

    function withdrawAgencyIncome(uint _value) external override {
        address sender = _msgSender();
        address self = address(this);

        require(agencyPool >= _value, "Insufficient pool balance");
        require(_accounts[sender].agencyIncome >= _value, "Can not exceed your divident received");
        require(USDT.balanceOf(self) >= _value, "Insufficient pool balance");

        agencyPool -= _value;
        _accounts[sender].agencyIncome -= _value;
        USDT.transfer(sender, _value);

        emit PlayerWithdrawAgencyIncome(sender, _value);
    }

    function setMyCode(string memory _code) external override {
        address sender = _msgSender();

        _setCode(sender, _code);
        UserAccount storage account = _accounts[sender];
        if (account.recommenders.length == 0 && !account.topAgent) {
            account.topAgent = true;
        }
    }

    // owner function

    function setSFT(address _address) external override onlyOwner {
        SFT = LotterySFT(_address);
    }

    function setArithmetic(address _address) external override onlyOwner {
        ARITHMETIC = IArithmetic(_address);
    }

    function setAgency(address _address) external override onlyOwner {
        AGENCY = IAgency(_address);
    }

    function instillBasePool(uint _value) external override onlyOwner returns (uint) {
        address owner = _msgSender();
        address supplier = address(this);
        require(USDT.allowance(owner, supplier) >= _value, "Insufficient allowancce");
        require(basePool == 0, "Already instill");

        USDT.transferFrom(owner, supplier, _value);

        basePool += _value;
        totalPool += _value;

        return basePool;
    }

    function withdrawBasePool() external override onlyOwner {
        bool low = totalPool < basePool;
        uint v1 = low ? totalPool : basePool;
        uint v2 = low ? 0 : totalPool - basePool;
        sharesPool += specialPool;
        specialPool = 0;
        totalPool = 0;
        basePool = 0;
        sharesPool += v2;

        USDT.transfer(_msgSender(), v1);
    }

    function withdrawGainPool() external override onlyOwner {
        USDT.transfer(_msgSender(), gainPool);
        gainPool = 0;
    }

    function setManager(address _address) external override onlyOwner {
        revokeRole(MANAGER_ROLE, manager);
        grantRole(MANAGER_ROLE, _address);
        manager = _address;
    }

    function sendWhitelist(address[] memory _players, uint[] memory _amounts) external override onlyManager {
        require(_players.length == _amounts.length, "cant send");
        for (uint i = 0; i < _players.length; i++) {
            address user = _players[i];
            uint amount = _amounts[i];
            _updateWLQuota(user, _whitelist[user] + amount);

            UserAccount storage account = _accounts[user];
            if (account.recommenders.length == 0) {
                account.topAgent = true;
            }

            emit SendWhitelist(user, amount, account.topAgent);
        }
    }

    function setWhitelistCode(address _agency, string memory _code) external override onlyManager returns (bool) {
        _setCode(_agency, _code);
        return true;
    }

    function setPublicWhitelist(string memory _code, uint _value) external override onlyManager {
        _publicCode = Utils.stringToBytes32(_code);
        require(_codes[_publicCode] == address(0), "Invalid code");
        _codes[_publicCode] = OFFICIAL_AGENCY;
        _updateWLQuota(OFFICIAL_AGENCY, _whitelist[OFFICIAL_AGENCY] + _value);

        emit SendWhitelist(OFFICIAL_AGENCY, _value, true);
        emit PlayerCodeChange(OFFICIAL_AGENCY, _code);
    }

    function takeWhitelist(address _to, string memory _code) external override onlyManager {
        address agency = _codes[Utils.stringToBytes32(_code)];
        uint value = _whitelist[agency];
        require(value >= TEN_PRICE, "Insufficient agency quota");

        _updateWLQuota(agency, value - TEN_PRICE);
        _updateWLQuota(_to, _whitelist[_to] + TEN_PRICE);

        if (agency != OFFICIAL_AGENCY) {
            _updateRecommender(_to, agency);
        } else {
            UserAccount storage account = _accounts[_to];
            if (account.recommenders.length == 0 && !account.topAgent) {
                account.topAgent = true;
            }
        }

        emit TakeWhitelist(agency, _to, TEN_PRICE);
    }

    function takePublicWhitelist(address _to, string memory _code) external override onlyManager {
        address recommender = _codes[Utils.stringToBytes32(_code)];
        uint value = _whitelist[OFFICIAL_AGENCY];
        require(value >= TEN_PRICE, "Insufficient agency quota");

        _updateWLQuota(OFFICIAL_AGENCY, value - TEN_PRICE);
        _updateWLQuota(_to, _whitelist[_to] + TEN_PRICE);

        if (recommender == address(0)) {
            _accounts[_to].topAgent = true;
        } else {
            _updateRecommender(_to, recommender);
        }

        emit TakeWhitelist(OFFICIAL_AGENCY, _to, TEN_PRICE);
    }

    function forceBindRecommender(address _player, string memory _code) external override onlyManager {
        address recommender = _codes[Utils.stringToBytes32(_code)];
        require(recommender != address(0), "bind failure");

        _updateRecommender(_player, recommender);
    }

    function setPresale(bool _off) external override onlyManager {
        _presale = _off;

        emit PresaleStatusChange(_off);
    }

    function setPause(bool _off) external override onlyManager {
        _pause = _off;

        emit PauseStatusChange(_off);
    }

    function allocateIdlePool(address _player, uint _value) external override onlyManager {
        require(idlePool >= _value, "Insufficient idle pool");
        require(_accounts[_player].accAgencyIncome >= 100 * 10 ** decimals, "Not qualified");
        idlePool -= _value;
        agencyPool += _value;
        _accounts[_player].agencyIncome += _value;
        _accounts[_player].accAgencyIncome += _value;
    }

    // call function

    function getSFTAddress() external view override returns (address) {
        return address(SFT);
    }

    function isPresale() external view override returns (bool) {
        return _presale;
    }

    function isPause() external view override returns (bool) {
        return _pause;
    }

    function getCurrentMintShare(uint _counts) external view override returns (uint) {
        return ONCE_PRICE.mul(10 ** 10).div(_p_stage).mul(_counts);
    }

    function transferWhitelist(address _to, uint _value) external override {
        address owner = _msgSender();
        require(_to != address(0), "Transfer failed");
        require(owner != _to, "Transfer failed");
        require(_value >= ONCE_PRICE, "Invalid quota");
        require(_value % ONCE_PRICE == 0, "Invalid quota");
        require(_whitelist[owner] >= _value, "Insufficient whitelist quota");

        _updateWLQuota(owner, _whitelist[owner] - _value);
        _updateWLQuota(_to, _whitelist[_to] + _value);
        _updateRecommender(_to, owner);

        emit TransferWhitelist(owner, _to, _value);
    }

    function isCodeExists(address _agency) external view override returns (bool) {
        if (_agency == OFFICIAL_AGENCY) return _publicCode != bytes32(0);
        return _accounts[_agency].code != bytes32(0);
    }

    function isInvalidCode(string memory _code) external view override returns (bool) {
        bytes32 key = Utils.stringToBytes32(_code);
        address owner = _codes[key];
        return owner != address(0);
    }

    function isTopAgency(address _agency) external view override returns (bool) {
        if (_agency == OFFICIAL_AGENCY) return true;
        return _accounts[_agency].topAgent;
    }

    function balanceOfWhitelist(address _player) external view override returns (uint) {
        return _whitelist[_player];
    }

    function balanceOfAwards(address _player) external view override returns (uint) {
        return _accounts[_player].award;
    }

    function balanceOfShares(address _player) external view override returns (uint) {
        return SFT.balanceOf(_player, SHARES_SLOT_ID);
    }

    function showAccountData(address _player) external view override returns (
        uint expend, 
        uint award, 
        uint agencyIncome, 
        uint accAward, 
        uint accAgencyIncome, 
        uint accSharesIncome,
        uint shares,
        uint whitelist
    ) {
        UserAccount memory account = _accounts[_player];
        expend = account.expend;
        award = account.award;
        agencyIncome = account.agencyIncome;
        accAward = account.accAward;
        accAgencyIncome = account.accAgencyIncome;
        accSharesIncome = account.accSharesIncome;
        shares = SFT.balanceOf(_player, SHARES_SLOT_ID);
        whitelist = _whitelist[_player];
    }

    function getRecommenders(address _player) external view override returns (address[] memory) {
        return _accounts[_player].recommenders;
    }

    function getPlayerCode(address _player) external view override returns (bytes32) {
        return _accounts[_player].code;
    }

    // VRF function

    function _requestForDraw(bool _single) private {
        address p = _msgSender();
        uint requestId = _requesetRanomWord();

        _requests[requestId] = VRFRequest({
            player: p,
            single: _single
        });
        _reqExists[requestId] = true;

        emit DrawRequest(requestId, p);
    }

    function _requesetRanomWord() private returns (uint requestId) {
        requestId = COORDINATOR.requestRandomWords(
            keyHash,
            subscriptionId,
            requestConfirmations,
            callbackGasLimit,
            numWords
        );

        // _requestCount++;
    }

    function fulfillRandomWords(uint256 _requestId, uint256[] memory _randomWords) internal override {
        require(_reqExists[_requestId], "request not found");

        uint random = _randomWords[0];
        // _lastRandomWord = random;

        VRFRequest memory request = _requests[_requestId];

        // draw
        address player = request.player;
        uint count = request.single ? 1 : 10;
        (uint result, uint total) = _settlement(player, random, count);

        emit DrawResult(_requestId, player, request.single, result, total);

        delete _reqExists[_requestId];
        delete _requests[_requestId];
    }

    function _settlement(address _player, uint _randomWord, uint _count) internal returns (uint result, uint total) {
        UserAccount storage account = _accounts[_player];
        result = ARITHMETIC.getResult(_randomWord, _count);

        if (_count == 1) {
            (uint index, uint award) = _convert(result.get(0));

            if (index == 13) {
                specialPool = specialPool.sub(award);
            } else {
                totalPool = totalPool.sub(award);
            }

            account.award += award;
            account.accAward += award;

            result = result.set(0, index);
            total = award;
        } else {
            for (uint i; i < _count; i++) {
                (uint index, uint award) = _convert(result.get(i));

                if (index == 13) {
                    specialPool = specialPool.sub(award);
                } else {
                    totalPool = totalPool.sub(award);
                }

                account.award += award;
                account.accAward += award;

                result = result.set(i, index);
                total += award;
            }
        }
    }

    function _convert(uint _index) internal view returns (uint, uint) {
        if (_index == 13) {
            uint value = specialPool.div(2);
            return (13, value);
        } else {
            uint award = ARITHMETIC.getAward(_index);
            if (totalPool < award) return (0, 0);
            return (_index, award);
        }
    }

    // internal function

    function _setCode(address _player, string memory _code) internal {
        require(Utils.strLen(_code) == 6, "Invalid code length");
        require(Utils.isValidCode(_code), "Invalid code contents");
        require(_accounts[_player].code == bytes32(0), "Player cant set code");
        bytes32 key = Utils.stringToBytes32(_code);
        require(_codes[key] == address(0), "set code failure");
        _codes[key] = _player;
        _accounts[_player].code = key;

        emit PlayerCodeChange(_player, _code);
    }

    function _updateWLQuota(address _player, uint _value) internal {
        _whitelist[_player] = _value;

        emit WhitelistChange(_player, _value);
    }

    function _updateRecommender(address _binder, address _recommender) internal {
        require(_binder != _recommender, "bind failure");
        if (_recommender == address(0)) return;
        if (_accounts[_binder].topAgent) return;
        if (_accounts[_binder].recommenders.length != 0) return;

        UserAccount storage alice = _accounts[_binder];
        if (_recommender != OFFICIAL_AGENCY) {
            address[] memory relations = AGENCY.getRelation(_binder, _recommender);
            if (relations.length > 4) return;
            alice.recommenders = relations;
        } else {
            alice.topAgent = true;
        }

        emit BindRecommender(_binder, _recommender, alice.recommenders);
    }

    function _mintShares(uint _counts) private {
        uint shares = ONCE_PRICE.mul(10 ** 10).div(_p_stage).mul(_counts);
        if (shares > 0) {
            totalSupplyShares += shares;
        }

        SFT.mint(_msgSender(), SHARES_SLOT_ID, shares);

        emit MintShares(_msgSender(), shares, ONCE_PRICE.mul(_counts));
    }

    function _instillPool(uint _value) private {
        uint per = _value / 100;

        gainPool += per * 10;
        sharesPool += per * 20;
        specialPool += per * 2;
        totalPool += per * 65;
        accumulateBet += _value;

        if (accumulateBet >= _p_stage) {
            uint i = _p_goal;
            _p_goal = _p_stage;
            _p_stage += i;
        }

        UserAccount storage account = _accounts[_msgSender()];
        account.expend += _value;

        uint agencyFee;
        uint len = account.recommenders.length;

        if (len != 0 && !account.topAgent) {
            uint[] memory income = AGENCY.calculateIncome(_msgSender(), _value);
            for (uint i = 0; i < Math.min(len, 4); i++) {
                uint profit = income[i];
                UserAccount storage ua = _accounts[account.recommenders[i]];
                ua.agencyIncome += profit;
                ua.accAgencyIncome += profit;
                agencyFee += profit;
            }
        }

        if (agencyFee != 0) {
            agencyPool += agencyFee;
        }

        idlePool += per.mul(3).sub(agencyFee);
    }
}
"
    },
    "contracts/LotterySFT.sol": {
      "content": "//SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";

import "./erc3525/ERC3525SlotEnumerable.sol";

contract LotterySFT is ERC3525SlotEnumerable, Ownable {

    address public manager;

    modifier onlyManager() {
        require(manager == _msgSender(), "no permission");
        _;
    }

    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        address _manager
    ) {
        ERC3525.__ERC3525_init(_name, _symbol, _decimals);
        manager = _manager;
    }

    function setMetadataDescriptor(address _descriptor) external onlyOwner {
        ERC3525._setMetadataDescriptor(_descriptor);
    }

    function mint(address _to, uint _slot, uint _value) external onlyManager {
        ERC3525._mintShares(_to, _slot, _value);
    }

    function burn(address _from, uint _slot, uint _value) external onlyManager {
        ERC3525._burnShares(_from, _slot, _value);
    }

    function getTokenIdBySlot(address _owner, uint _slot) external view returns (uint) {
        return ERC3525._getTokenIdBySlot(_owner, _slot);
    }

    function balanceOf(address _owner, uint _slot) external view returns (uint) {
        return ERC3525._getBalanceOfBySlot(_owner, _slot);
    }
}"
    },
    "contracts/interface/ILottery.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

interface ILottery {

    event DrawRequest(uint indexed requestId, address player);
    event DrawResult(uint indexed requestId, address player, bool single, uint result, uint total);
    event MintShares(address indexed player, uint shares, uint value);
    event BurnShares(address indexed player, uint shares, uint value);

    event PriceChange(uint value);
    event PresaleStatusChange(bool off);
    event PauseStatusChange(bool off);

    event WhitelistChange(address indexed player, uint value);
    event SendWhitelist(address indexed to, uint value, bool isTopAgency);
    event TakeWhitelist(address indexed agency, address indexed to, uint quota);
    event TransferWhitelist(address indexed from, address indexed to, uint value);
    event PlayerCodeChange(address indexed player, string code);
    event BindRecommender(address indexed player, address indexed target, address[] recommenders);

    event PlayerWithdrawAward(address indexed player, uint value);
    event PlayerWithdrawAgencyIncome(address indexed player, uint value);

    error NotWhitelisted();

    function drawACard(string memory _code) external;

    function draw10Cards(string memory _code) external;

    function burnShares(uint _value) external;

    function withdrawAward(uint _value) external;

    function withdrawAgencyIncome(uint _value) external;

    function setMyCode(string memory _code) external;

    function balanceOfAwards(address _player) external view returns (uint);

    function balanceOfShares(address _player) external view returns (uint);

    function showAccountData(address _player) external view returns (
        uint expend, 
        uint award, 
        uint agencyIncome, 
        uint accAward, 
        uint accAgencyIncome, 
        uint accSharesIncome,
        uint shares,
        uint whitelist
    );

    function getRecommenders(address _player) external view returns (address[] memory);

    function getPlayerCode(address _player) external view returns (bytes32);

    function instillBasePool(uint _value) external returns (uint);

    function withdrawBasePool() external;

    function withdrawGainPool() external;

    function setPresale(bool _off) external;

    function isPresale() external view returns (bool);

    function setPause(bool _off) external;

    function isPause() external view returns (bool);

    function getCurrentMintShare(uint _counts) external view returns (uint);

    function allocateIdlePool(address _player, uint _value) external;

    function sendWhitelist(address[] memory _players, uint[] memory _amounts) external;

    function balanceOfWhitelist(address _player) external view returns (uint);

    function transferWhitelist(address _to, uint _value) external;

    function takeWhitelist(address _to, string memory _code) external;

    function takePublicWhitelist(address _to, string memory _code) external;

    function setWhitelistCode(address _agency, string memory _code) external returns (bool);

    function setPublicWhitelist(string memory _code, uint _value) external;

    function forceBindRecommender(address _player, string memory _code) external;

    function setManager(address _address) external;

    function isCodeExists(address _agency) external view returns (bool);

    function isInvalidCode(string memory _code) external view returns (bool);

    function isTopAgency(address _agency) external view returns (bool);

    function setSFT(address _address) external;

    function setArithmetic(address _address) external;

    function setAgency(address _address) external;

    function basePool() external view returns (uint);

    function sharesPool() external view returns (uint);

    function specialPool() external view returns (uint);

    function agencyPool() external view returns (uint);

    function idlePool() external view returns (uint);

    function totalPool() external view returns (uint);

    function totalSupplyShares() external view returns (uint);

    function getSFTAddress() external view returns (address);
}"
    },
    "contracts/interface/IArithmetic.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

interface IArithmetic {

    function getResult(uint _seed, uint _count) external view returns (uint);

