    "contracts/access/Roles.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract Roles is AccessControl {

    error NotAuthorizedError(address sender);

    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    constructor(address _owner) {
        _setupRole(DEFAULT_ADMIN_ROLE, _owner);
    }

    modifier onlyOwner() {
        if (!hasRole(DEFAULT_ADMIN_ROLE, _msgSender())) revert NotAuthorizedError(_msgSender());
        _;
    }

    modifier onlyManager() {
        if (!hasRole(MANAGER_ROLE, _msgSender())) revert NotAuthorizedError(_msgSender());
        _;
    }
}"
