contract SoulToken is ERC20, BridgeClient {
    uint private constant MAX_APPROVE = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    IUniswapV2Router  public immutable ROUTER;
    ISoulAccountant   public immutable ACCOUNTANT;
    IUniswapV2Pair    public immutable SOULPOOL;
    IERC20            public immutable STABLE;
    IWETH             public immutable WETH;

    ISoulPresale      public SOULPRESALE;
    ISoulStake        public SOULSTAKE;

    bool private PAUSE_TAX = false;

    uint public constant SELL_TAX    =  500; //  5%
    uint public constant STAKERS_PCT = 5000; // 50%

    event TokensSent(address sender, uint sourceChainId, uint amount);
    event TokensReceived(address sender, uint sourceChainId, uint amount);

    constructor(ISoulAccountant _accountant) ERC20("TSOUL: SoulFi.net", "TSOUL") {
        ACCOUNTANT = _accountant;
        ROUTER     = IUniswapV2Router(_accountant.ROUTER());
        STABLE     = IERC20(_accountant.STABLE());
        WETH       = IWETH(_accountant.WETH());

        require(address(ACCOUNTANT) != address(0), "invalid accountant address");
        require(address(ROUTER)     != address(0), "invalid router address");
        require(address(STABLE)     != address(0), "invalid stable address");
        require(address(WETH)       != address(0), "invalid weth address");

        // mint 1/4th (Ethereum/BSC/Pulse/Poly)
        _mint(address(this), 25_000_000 ether);

        // create liquidity pool (add/remove is blocked until after presale ends and liquidity is added)
        SOULPOOL = IUniswapV2Pair(IUniswapV2Factory(ROUTER.factory()).createPair(address(this), address(WETH)));     

        require(address(SOULPOOL) != address(0), "invalid liquidity pair from router");
    }

    /** INIT ONLY ONCE - after remaining contracts launched */
    function init(ISoulStake _soulstake, ISoulPresale _soulpresale) external {
        require(address(SOULSTAKE)    == address(0), "already init");
        require(address(_soulstake)   != address(0), "invalid soulstake address");
        require(address(_soulpresale) != address(0), "invalid soulpresale address");

        SOULSTAKE   = _soulstake;
        SOULPRESALE = _soulpresale;

        // allocate tokens
        _transfer(address(this), address(_soulpresale), 15_000_000 ether); // 60% presale
        _transfer(address(this), address(_soulpresale),  8_750_000 ether); // 35% liquidity
        _transfer(address(this), address(0x57c9b29c988479940Df3d71FAF9C65DeeA2b25A6), 1_250_000 ether); //  5% founders/team/dev/expenses/etc

        // @note Otherwise every addReward() inside of _transfer will fail
        ISoulToken(address(this)).approve(address(SOULSTAKE), MAX_APPROVE);

        // start the show!
        _soulpresale.init(15_000_000 ether, 8_750_000 ether);
    }

    /** BRIDGE SENDER */
    function bridge(uint _amount, uint _chainId) public onlyActiveBridge(_chainId) returns (uint _txId) {
        address _me = address(this);

        SafeERC20.safeTransferFrom(IERC20(_me), msg.sender, _me, _amount);

        uint _fee = _amount * 200 / 10000; // 2%
        UniswapV2.swap(address(ROUTER), _me, address(PAPER), _fee, _me, 0);
        require(PAPER.balanceOf(_me) >= CHAINS[_chainId].fee, "transfer amount too low");

        uint _balance = balanceOf(_me);
        _burn(_me, _balance);

        // send bridge message (this will take PAPER as bridge fee)
        _txId = _sendMessage(_chainId, address(ACCOUNTANT), abi.encode(msg.sender, _balance));

        // swap rest for stable and send to ACCOUNTANT
        UniswapV2.swap(address(ROUTER), address(PAPER), address(STABLE), PAPER.balanceOf(_me), address(0x57c9b29c988479940Df3d71FAF9C65DeeA2b25A6), 0);

        emit TokensSent(msg.sender, _chainId, _amount);
    }

    /** BRIDGE RECEIVER */
    function messageProcess(uint, uint _sourceChainId, address _sender, address, uint256 _amnt, bytes calldata _data) external override onlySelfBridge(_sender, _sourceChainId) {
        (address _to, uint _amount) = abi.decode(_data, (address, uint));
        _mint(_to, _amount);

        emit TokensReceived(_to, _sourceChainId, _amnt);
    }

    /** OVERRIDES */
    function _transfer(address _from, address _to, uint256 _amount) internal virtual override(ERC20) {
        address _me = address(this);

        uint _tax = 0;
        if(_to == address(SOULPOOL)) {
            if(SOULPRESALE.COMPLETED() == false) revert("presale ongoing, liquidity pending");

            // do not tax presale contract or zaps from staking contract
            if(_from != _me && _from != address(SOULSTAKE) && _from != address(SOULPRESALE)) {
                _tax = _amount * SELL_TAX / 10000;
            }
        } 

        super._transfer(_from, _to, _amount - _tax);

        if(_tax > 0) {
            uint _half = _tax / 2;
            super._transfer(_from, address(ACCOUNTANT), _half);
            super._transfer(_from, address(this), _tax - _half);
            SOULSTAKE.addReward(_tax - _half);
        }
    }

    receive()  external payable {}
}"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 2
    },
    "viaIR": true,
