    "contracts/nft/DepositConsumption.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "../common/AccessController.sol";
import "../interface/IConsumptionItem.sol";

contract DepositConsumption is ERC1155Holder, AccessController, ReentrancyGuard {
  using ECDSA for bytes32;
  using Address for address;
 
  event EmergencyWithdraw(uint256 timestamp, address indexed _address);
  event ConsumptionDeposited(address indexed user,  string orderId, uint256[] consumptionType, uint256[] amount, uint256 nonce, uint256 timestamp, uint256 blockTime);
  event ConsumptionExported(address indexed user,  string orderId, uint256[] consumptionType, uint256[] amount, uint256 nonce, uint256 timestamp, uint256 blockTime);
  event CancelExport(string orderId, address indexed user, uint256 timestamp);

  mapping(address => uint256) public _nonceByAddress;
  address public consumptionManagement;
  IConsumptionItem public consumptionItem;
  address public verifyAddress;
  uint256 public timeout;
  bytes32 public constant CONSUMPTION_MANAGEMENT = keccak256("CONSUMPTION_MANAGEMENT");
  mapping(string => bool) public _executedOrderIds;
  mapping(address => uint256) public lastExportByUser;
  mapping(string => bool) private _cancelledOrderIds;

  constructor(IConsumptionItem _consumptionItem, address _consumptionManagement){
    _validateContractAddress(address(_consumptionItem));
    consumptionItem = _consumptionItem;
    consumptionManagement = _consumptionManagement;
    _setupRole(CONSUMPTION_MANAGEMENT, consumptionManagement);
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }
 
  modifier onlyConsumptionManagement() {
    require(hasRole(CONSUMPTION_MANAGEMENT, _msgSender()), "Caller is not the consumption management");
    _;
  }

  function setConsumptionManagement(address _consumptionManagement) external onlyAdmin {
    revokeRole(CONSUMPTION_MANAGEMENT, consumptionManagement);
    consumptionManagement = _consumptionManagement;
    _setupRole(CONSUMPTION_MANAGEMENT, consumptionManagement);
  }

  function setVerifyAddress(address _verifyAddress) external onlyAdmin {
    verifyAddress = _verifyAddress;
  }

  function setConsumptionItem(IConsumptionItem _consumptionItem) external onlyAdmin {
    consumptionItem = _consumptionItem;
  }

  function getNonce(address account) external view returns (uint256) {
    return _nonceByAddress[account];
  }

  function setTimeout(uint256 _timeout) external onlyAdmin {
    timeout = _timeout;
  }

  function depositConsumption(string calldata _orderId, uint256[] calldata _nftIds, uint256[] calldata _amounts, uint256 _timestamp, bytes calldata _signature) external {
    require(!_executedOrderIds[_orderId], "Order is excuted");
    require(!_cancelledOrderIds[_orderId], "The order was cancelled");
    require(_timestamp + timeout >= block.timestamp, "Timeout!!");
    require(_nftIds.length == _amounts.length, "_nftIds and _amounts length mismatch");
    require(_validateSignatureImport(_nftIds, _amounts, _nonceByAddress[_msgSender()] + 1, _orderId, _timestamp, _signature), "INVALID_PARAMS");
    _executedOrderIds[_orderId] = true;
    _nonceByAddress[_msgSender()] += 1;
    for(uint256 index = 0; index < _nftIds.length; index++){
      require(consumptionItem.getBalance(_msgSender(), _nftIds[index]) >= _amounts[index], "Not enough balance");
      consumptionItem.transfer(_msgSender(), address(this), _nftIds[index], _amounts[index]);
    }
    emit ConsumptionDeposited(_msgSender(), _orderId, _nftIds, _amounts, _nonceByAddress[_msgSender()], _timestamp, block.timestamp);
  }

  function exportConsumption(string calldata _orderId, uint256[] calldata _nftIds, uint256[] calldata _amounts, uint256 _timestamp, address _address, bytes calldata _signature) external nonReentrant {
    require(!_executedOrderIds[_orderId], "Order is excuted");
    require(!_cancelledOrderIds[_orderId], "The order was cancelled");
    require(_timestamp + timeout >= block.timestamp, "Timeout!!");
    require(_nftIds.length == _amounts.length, "_nftIds and _amounts length mismatch");
    require(_validateSignature(_nftIds, _amounts, _nonceByAddress[_address] + 1, _orderId, _address, _timestamp, _signature), "INVALID_PARAMS");
    _executedOrderIds[_orderId] = true;
    _nonceByAddress[_address] += 1;
    lastExportByUser[_address] = block.timestamp;
    for(uint256 index = 0; index < _nftIds.length; index++){
      uint256 amountRemaining = consumptionItem.getBalance(address(this), _nftIds[index]);
      if(_amounts[index] > amountRemaining){
        if(consumptionItem.getCreatorOfConsumption(_nftIds[index]) == address(0)){
          consumptionItem.mintItem(_nftIds[index], _amounts[index] - amountRemaining, _address, false);
        }
        if(amountRemaining > 0){
          consumptionItem.transfer(address(this), _address, _nftIds[index], amountRemaining);
        }
      } else {
        consumptionItem.transfer(address(this), _address, _nftIds[index], _amounts[index]);
      }
    }
    emit ConsumptionExported(_address, _orderId, _nftIds, _amounts, _nonceByAddress[_address], _timestamp, block.timestamp);
  }

  function withdrawEmergency( uint256[] calldata nftIds, uint256[] calldata amountUsed) external nonReentrant onlyConsumptionManagement {
    require(nftIds.length == amountUsed.length, "Invalid input");
    for(uint256 index = 0; index < nftIds.length; index++){
      if(consumptionItem.getCreatorOfConsumption(nftIds[index]) != address(0)){
        consumptionItem.burnItem(nftIds[index], amountUsed[index], address(this));
      } else {
        consumptionItem.transfer(address(this), consumptionManagement, nftIds[index], amountUsed[index]);
      }
    }
    emit EmergencyWithdraw(block.timestamp, consumptionManagement);
  }

  function cancelExport(string calldata _orderId, bytes calldata _signature) external nonReentrant {
    require(!_cancelledOrderIds[_orderId], "The order was cancelled");
    require(
      !_executedOrderIds[_orderId],
      "The order was executed"
    );
    bytes32 hashValue = keccak256(abi.encodePacked(_orderId, _msgSender()));
    address recover = hashValue.toEthSignedMessageHash().recover(_signature);
    require(recover == verifyAddress, "Message must be signed by Verifier");
    _cancelledOrderIds[_orderId] = true;
    emit CancelExport(_orderId, _msgSender(), block.timestamp);
  }

  function _validateSignature(uint256[] calldata _nftIds, uint256[] calldata _amounts, uint256 _nonce, string calldata _orderId, address _address, uint256 _timestamp, bytes calldata _signature) private view returns (bool) {
    bytes32 hashValue = keccak256(
      abi.encodePacked(_nftIds, _amounts, _nonce, _address, _orderId, _timestamp) 
    );
    address recover = hashValue.toEthSignedMessageHash().recover(_signature);
    return recover == verifyAddress;
  }
  
  function _validateSignatureImport(uint256[] calldata _nftIds, uint256[] calldata _amounts, uint256 _nonce, string calldata _orderId, uint256 _timestamp, bytes calldata _signature) private view returns (bool) {
    bytes32 hashValue = keccak256(
      abi.encodePacked(_nftIds, _amounts, _nonce, _orderId, _timestamp) 
    );
    address recover = hashValue.toEthSignedMessageHash().recover(_signature);
    return recover == verifyAddress;
  }

  function _validateContractAddress(address _contractAddress) private view {
    require(
      _contractAddress != address(0),
      "Address can not be zero address"
    );
    require(_contractAddress.isContract(), "Address must be a contract");
  }

  function supportsInterface(bytes4 interfaceId)
    public
    view
    virtual
    override(AccessControl, ERC1155Receiver)
    returns (bool)
  {
    return super.supportsInterface(interfaceId);
  }
}"
