pragma solidity >=0.6.0 <0.8.0;
pragma experimental ABIEncoderV2;

contract BountyHunter is Context, ERC721Enumerable{
    
    using EnumerableSet for EnumerableSet.UintSet;
    using Strings for uint256;
    using SafeMath for uint256;
    
    struct HunterInfo{
        uint256 id;
        uint256 level;
        uint256 rarity;
        uint256 xp;
        uint256 totalXp;
        uint256 missions;
        uint256 maxMissionId;
        uint256 rewards;
        uint256 mintPerBlock;
        uint256 totalMinted;
        address owner;
    }
    
    struct UserInfo{
        uint256 tokenId;
        uint256 totalMission;
        uint256 success;
        uint256 maxNftLevel;
        uint256 totalReward;
        uint256 totalNft;
        uint256 maxMission;
        uint256 lastRewardBlock;
    }
    
    struct MissionInfo{
        uint256 missionId;
        uint256 price;
        uint256 multiple;
        uint256 reward;
        uint256 xp;
        uint256 needRarity;
        uint256 totalTry;
        uint256 totalSuccess;
        uint256 totalReward;
    }
    
    struct Chance{
        uint256 great;
        uint256 success;
        uint256 mediocre;
        uint256 fail;
    }
    
    BetaPulsarToken public betaPulsar;
    BountyToken public bounty;
    address public developer;
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    uint256 public nextTokenId = 0;
    uint256 public hunterPrice = 1e20;
    uint256 public dieXp = 9400;
    uint256 internal rewardPercent = 2000;
    uint256 internal defaultLevelXp = 25;
    uint256 internal constant defaultDie = 1000;
    uint256 internal constant reduceFail = 6;
    uint256 internal constant increaseMediocre = 3;
    uint256 internal constant increaseSuccess = 2;
    uint256 internal constant increaseGreat = 1;
    address private lastUser;
    bool public pause = false;
    
    constructor(
        address _developer,
        BetaPulsarToken _betaPulsar,
        BountyToken _bounty
    ) ERC721("Bounty Hunter","HUNTER") public {
        developer = _developer;
        betaPulsar = _betaPulsar;
        bounty = _bounty;
    }
    
    EnumerableSet.UintSet missionIds;
    mapping (uint256=>Chance) public getChanceByHunter;
    mapping (uint256=>MissionInfo) public getMissionById;
    mapping (uint256=>HunterInfo) public getHunterById;
    mapping (address=>UserInfo) public getUserByAddress;
    mapping (address=>bool) public userHasToken;
    mapping (address=>uint256) public exHuntersXp;
    
    event BuyHunter(address indexed user, uint256 tokenId, uint256 price);
    event CreateHunter(address indexed user, uint256 tokenId);
    event SetRewardToken(address indexed user, address indexed token);
    event SetDevAddress(address indexed admin, address indexed developer);
    event AddMissionInfo(address indexed dev, uint256 missionId);
    event SetMissionInfo(address indexed dev, uint256 missionId);
    event HarvestedBounty(address indexed user, uint256 hunterId, uint256 amount);
    event SetHunterPrice(address indexed dev, uint256 newPrice);
    event SetRewardPercent(address indexed dev, uint256 newPercent);
    event SetDefaultLevelXp(address indexed dev, uint256 newXp);
    event MissionCompleted(address indexed user, uint256 mission, uint256 hunter, uint256 result);
    event SetPause(address indexed dev, bool isPause);
    
    modifier onlyDev() {
        require(developer == _msgSender(), "Caller is not the developer");
        _;
    }
    modifier onlyHunterOwner() {
        require(balanceOf(_msgSender()) == 1, "You do not have a HUNTER");
        _;
    }
    modifier canHunterPlay(uint256 hunterId, uint256 missionId){
        require(canPlay(hunterId, missionId), "rarity is not enough");
        _;
    }
    modifier isMission(uint256 missionId){
        require(missionIds.contains(missionId), "no id");
        _;
    }
    modifier isOwner(uint256 hunterId){
        require(ownerOf(hunterId) == msg.sender, "wow");
        _;
    }
    modifier isNotPaused{
        require(!pause);
        _;
    }
    modifier onlyEOA() {
        require(tx.origin == msg.sender, "Only EOA");
        _;
    }
    function getMission(uint256 id) public view returns(MissionInfo  memory){ return getMissionById[id]; }
    function getHunter(uint256 id) public view returns(HunterInfo memory){ return getHunterById[id]; }
    function getUser(address user) public view returns(UserInfo memory){ return getUserByAddress[user]; }
    function setDieXp(uint256 _xp) external onlyDev{ dieXp = _xp; }
    function setPause(bool isPause) external onlyDev{
        pause = isPause;
        emit SetPause(msg.sender, pause);
    }
    function setHunterPrice(uint256 _price) external onlyDev{ 
        hunterPrice = _price; 
        emit SetHunterPrice(msg.sender, hunterPrice);
    }
    function setRewardPercent(uint256 _rewardPercent) external onlyDev{ 
        rewardPercent = _rewardPercent; 
        emit SetRewardPercent(msg.sender, rewardPercent);
    }
    function setDefaultLevelXp(uint256 amount) external onlyDev{ 
        defaultLevelXp = amount; 
        emit SetDefaultLevelXp(msg.sender, defaultLevelXp);
    }
    function canPlay(uint256 hunterId, uint256 missionId) public view virtual returns(bool){
        require(missionIds.contains(missionId), "no id");
        HunterInfo storage hunter = getHunterById[hunterId];
        MissionInfo storage mission = getMissionById[missionId];
        return hunter.level >= mission.needRarity;
    }
    function setDevAddress(address _devAddress) external onlyDev{
        developer = _devAddress;
        emit SetDevAddress(_msgSender(), _devAddress);
    }
    function addMission(
        uint256 _id, 
        uint256 _price,
        uint256 _multiple,
        uint256 _reward,
        uint256 _xp,
        uint256 _needRarity) external onlyDev{
        require(_id != 0, "Id can not be zero");
        require(!missionIds.contains(_id), "id already exists");
        getMissionById[_id] = MissionInfo(_id, _price, _multiple, _reward, _xp, _needRarity, 0, 0, 0);
        missionIds.add(_id);
        emit AddMissionInfo(msg.sender, _id);
    }
    function setMission(
        uint256 _id, 
        uint256 _price, 
        uint256 _multiple,
        uint256 _reward,
        uint256 _xp,
        uint256 _needRarity) external onlyDev{
        require(_id != 0, "Id can not be zero");
        require(missionIds.contains(_id), "no id");
        MissionInfo storage mission = getMissionById[_id];
        mission.price = _price;
        mission.multiple = _multiple;
        mission.reward = _reward;
        mission.xp = _xp;
        mission.needRarity = _needRarity;
        
        emit SetMissionInfo(msg.sender, _id);
    }
    function removeMission(uint256 _id) external onlyDev{
        require(missionIds.contains(_id), "id does not exist");
        missionIds.remove(_id);
    }
    function buyToken() external onlyEOA isNotPaused {
        UserInfo storage user = getUserByAddress[_msgSender()];
        require(balanceOf(msg.sender) == 0, "You already have a HUNTER");
        require(hunterPrice <= betaPulsar.balanceOf(_msgSender()), "Not enough PULs");
        betaPulsar.transferFrom(_msgSender(), BURN_ADDRESS, hunterPrice);
        uint256 tokenId = _createToken(_msgSender());
        userHasToken[msg.sender] = true;
        user.tokenId = tokenId;
        user.totalNft = user.totalNft.add(1);
        if(exHuntersXp[msg.sender] > 0){
            HunterInfo storage hunter = getHunterById[tokenId];
            hunter.xp = hunter.xp.add(exHuntersXp[msg.sender]);
            hunter.totalXp = hunter.totalXp.add(exHuntersXp[msg.sender]);
            levelUp(tokenId);
        }
        if(user.maxNftLevel == 0){ user.maxNftLevel = 1; }
        emit BuyHunter(_msgSender(), tokenId, hunterPrice);
    }
    function _createToken(address _customer) internal returns(uint256){
        uint256 tokenId = ++nextTokenId;
        getHunterById[tokenId] = HunterInfo(tokenId, 1, 1, 0, 0, 0, 0, 0, 0, 0, _customer);
        _mint(_customer, tokenId);
        _approve(BURN_ADDRESS, tokenId);
        emit CreateHunter(_customer, tokenId);
        return tokenId;
    }
    function createSpecialToken(uint256 level, uint256 rarity, uint256 xp, uint256 totalXp, uint256 mintPerBlock, address owner) public onlyDev {
        require(balanceOf(owner) == 0, "already has a token");
        UserInfo storage user = getUserByAddress[owner];
        uint256 tokenId = ++nextTokenId;
        getHunterById[tokenId] = HunterInfo(tokenId, level, rarity, xp, totalXp, 0, 0, 0, mintPerBlock, 0, owner);
        user.tokenId = tokenId;
        user.totalNft = user.totalNft.add(1);
        if(user.maxNftLevel < level){ user.maxNftLevel = level; }
        if(level == 100){ user.lastRewardBlock = block.number; }
        _mint(owner, tokenId);
        _approve(BURN_ADDRESS, tokenId);
        userHasToken[owner] = true;
        emit CreateHunter(owner, tokenId);
    }
    function _transfer(address sender, address recipient, uint256 tokenId) internal virtual override{
        require(sender != recipient, "you can not send to yourself");
        UserInfo storage senderUser = getUserByAddress[sender];
        HunterInfo storage hunter = getHunterById[tokenId];
        if(recipient != BURN_ADDRESS){
            require(balanceOf(recipient) == 0, "already has a token");
            UserInfo storage recipientUser = getUserByAddress[recipient];
            super._transfer(sender, recipient, tokenId);
            userHasToken[sender] = false;
            userHasToken[recipient] = true;
            senderUser.tokenId = 0;
            recipientUser.tokenId = tokenId;
            recipientUser.totalNft = recipientUser.totalNft.add(1);
            hunter.owner = recipient;
            if(recipientUser.maxNftLevel < hunter.level){ recipientUser.maxNftLevel = hunter.level; }
            if(hunter.level == 100){ recipientUser.lastRewardBlock = block.number; }
        }
        else {
            super._transfer(sender, recipient, tokenId);
            senderUser.tokenId = 0;
            hunter.owner = recipient;
            userHasToken[sender] = false;
        }
    }
    function levelUp(uint256 _tokenId) internal {
        HunterInfo storage hunter = getHunterById[_tokenId];
        UserInfo storage user = getUserByAddress[msg.sender];
        uint256 currentLevel = hunter.level;
        uint256 currentRarity = hunter.rarity;
        uint256 currentXp = hunter.xp;
        uint256 needXp = defaultLevelXp.mul(currentRarity);
        while(currentXp >= needXp && currentLevel != 100){
            currentXp = currentXp.sub(needXp);
            currentLevel = currentLevel.add(1);
            if(currentLevel > 25 && currentLevel <= 50 && currentRarity != 2){ currentRarity = 2; }
            else if (currentLevel > 50 && currentLevel <= 75 && currentRarity != 3){ currentRarity = 3; }
            else if (currentLevel > 75 && currentRarity != 4){ currentRarity = 4; }
            needXp = defaultLevelXp.mul(currentRarity);
            if(currentLevel == 100){ 
                user.lastRewardBlock = block.number;
            }
        }
        hunter.level = currentLevel;
        hunter.rarity = currentRarity;
        hunter.xp = currentXp;
        if(user.maxNftLevel < currentLevel){ user.maxNftLevel = currentLevel;}
        if(hunter.level == 100){ 
            hunter.mintPerBlock = hunter.rewards.mul(rewardPercent).div(1000000000);
        }
    }
    function _generateRandomNumber(address sender) private view returns (uint256) {
        uint256 randomHash =
            uint256(keccak256(abi.encodePacked(blockhash(block.number - 1), block.timestamp + uint256(sender))))%100;
        return randomHash;
    }
    function startMission(uint256 _hunterId, uint256 _missionId) external onlyHunterOwner isMission(_missionId) isOwner(_hunterId) onlyEOA isNotPaused {
        MissionInfo storage mission = getMissionById[_missionId];
        HunterInfo storage hunter = getHunterById[_hunterId];
        require(mission.price <= betaPulsar.balanceOf(msg.sender), "you do not have enough token");
        betaPulsar.transferFrom(msg.sender, BURN_ADDRESS, mission.price);
        uint256 _level = hunter.level;
        require(hunter.rarity >= mission.needRarity, "You can not play this mission!");
        if(_level == 100){ harvest(msg.sender); }
        uint256 _die = defaultDie.sub(reduceFail.mul(_level));
        uint256 _mediocre = _die.add(mission.multiple.mul(100)).add(increaseMediocre.mul(_level));
        uint256 _success = _mediocre.add(((mission.multiple)**2).mul(10)).add(increaseSuccess.mul(_level));
        uint256 _great = _success.add((mission.multiple)**3).add(increaseGreat.mul(_level));
        getChanceByHunter[hunter.id] = Chance(_great, _success, _mediocre, _die);
        uint256 currentNum = uint256(keccak256(abi.encodePacked(block.timestamp, hunter.xp, lastUser, hunter.level, block.number, _generateRandomNumber(msg.sender))))%_great;
        UserInfo storage user = getUserByAddress[msg.sender];
        user.totalMission = user.totalMission.add(1);
        mission.totalTry = mission.totalTry.add(1);
        hunter.missions = hunter.missions.add(1);
        if(currentNum < _die){
            result(BURN_ADDRESS, msg.sender, 0, hunter.id);
            exHuntersXp[msg.sender] = hunter.totalXp.mul(dieXp).div(10000);
            if(user.maxMission < mission.missionId){ user.maxMission = user.maxMission; }
            if(hunter.maxMissionId < mission.missionId){ hunter.maxMissionId = hunter.maxMissionId; }
            lastUser = msg.sender;
            emit MissionCompleted(msg.sender, _missionId, _hunterId, 1);
        } 
        else if(currentNum >= _die && currentNum < _mediocre){
            result(msg.sender, msg.sender, mission.reward.mul(1).mul(1e16), hunter.id);
            exHuntersXp[msg.sender] = 0;
            hunter.xp = hunter.xp.add(mission.xp);
            hunter.totalXp = hunter.totalXp.add(mission.xp);
            user.success = user.success.add(1);
            mission.totalSuccess = mission.totalSuccess.add(1);
            mission.totalReward = mission.totalReward.add(mission.reward.mul(1).mul(1e16));
            hunter.rewards = hunter.rewards.add(mission.reward.mul(1).mul(1e16));
            user.totalReward = user.totalReward.add(mission.reward.mul(1).mul(1e16));
            levelUp(hunter.id);
            if(user.maxMission < mission.missionId){ user.maxMission = mission.missionId; }
            if(hunter.maxMissionId < mission.missionId){ hunter.maxMissionId = mission.missionId; }
            lastUser = msg.sender;
            emit MissionCompleted(msg.sender, _missionId, _hunterId, 2);
        } 
        else if(currentNum >= _mediocre && currentNum < _success){
            result(msg.sender, msg.sender, mission.reward.mul(2).mul(1e16), hunter.id);
            exHuntersXp[msg.sender] = 0;
            hunter.xp = hunter.xp.add(mission.xp.mul(2));
            hunter.totalXp = hunter.totalXp.add(mission.xp.mul(2));
            user.success = user.success.add(1);
            mission.totalSuccess = mission.totalSuccess.add(1);
            mission.totalReward = mission.totalReward.add(mission.reward.mul(2).mul(1e16));
            hunter.rewards = hunter.rewards.add(mission.reward.mul(2).mul(1e16));
            user.totalReward = user.totalReward.add(mission.reward.mul(2).mul(1e16));
            levelUp(hunter.id);
            if(user.maxMission < mission.missionId){ user.maxMission = mission.missionId; }
            if(hunter.maxMissionId < mission.missionId){ hunter.maxMissionId = mission.missionId; }
            lastUser = msg.sender;
            emit MissionCompleted(msg.sender, _missionId, _hunterId, 3);
        } 
        else if(currentNum >= _success && currentNum < _great){
            result(msg.sender, msg.sender, mission.reward.mul(4).mul(1e16), hunter.id);
            exHuntersXp[msg.sender] = 0;
            hunter.xp = hunter.xp.add(mission.xp.mul(4));
            hunter.totalXp = hunter.totalXp.add(mission.xp.mul(4));
            user.success = user.success.add(1);
            mission.totalSuccess = mission.totalSuccess.add(1);
            mission.totalReward = mission.totalReward.add(mission.reward.mul(4).mul(1e16));
            hunter.rewards = hunter.rewards.add(mission.reward.mul(4).mul(1e16));
            user.totalReward = user.totalReward.add(mission.reward.mul(4).mul(1e16));
            levelUp(hunter.id);
            if(user.maxMission < mission.missionId){ user.maxMission = mission.missionId; }
            if(hunter.maxMissionId < mission.missionId){ hunter.maxMissionId = mission.missionId; }
            lastUser = msg.sender;
            emit MissionCompleted(msg.sender, _missionId, _hunterId, 4);
        }
    }
    
    function pendingBounty(address _user) public view returns(uint256){
        UserInfo storage user = getUserByAddress[_user];
        if (user.lastRewardBlock == 0) { return  0; }
        else{
            if(user.tokenId == 0){
                return 0;
            }
            else{
                HunterInfo storage hunter = getHunterById[user.tokenId];
                if(hunter.level == 100){
                    uint256 pending = hunter.mintPerBlock.mul(block.number.sub(user.lastRewardBlock));
                    return pending;
                }
                else{
                    return 0;
                }
            }
        }
    }
    
    function harvestBounty() external onlyEOA isNotPaused {
        UserInfo storage user = getUserByAddress[msg.sender];
        uint256 pending = pendingBounty(msg.sender);
        if(pending > 0){
            user.lastRewardBlock = block.number;
            bounty.mint(address(this), pending);
            bounty.transfer(msg.sender, pending);
        }
        emit HarvestedBounty(msg.sender, user.tokenId, pending);
    }
    function harvest(address _user) internal {
        UserInfo storage user = getUserByAddress[_user];
        uint256 pending = pendingBounty(_user);
        if(pending > 0){
            user.lastRewardBlock = block.number;
            bounty.mint(address(this), pending);
            bounty.transfer(_user, pending);
        }
        emit HarvestedBounty(_user, user.tokenId, pending);
    }
    function result(address _resultAddress, address _user, uint256 _reward, uint256 _tokenId) internal {
        if(_resultAddress != _user){
            safeTransferFrom(msg.sender, _resultAddress, _tokenId);
        }
        if(_reward != 0){
            bounty.mint(address(this), _reward);
            bounty.transfer(_resultAddress, _reward);
        }
    }
}
