pragma solidity ^0.8.18;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract StakingContract is Ownable {
    IERC20 public VTEXToken =IERC20(0xd69201C57d48016fC50Aab11F0a89Cea117fCfff);
    address public financeAddress = 0x8B2eDa7B334366a5832A6e8ebE58381EfD2902CE;
    uint256 public constant DECIMALS_MULTIPLIER = 10**6; // 6 decimal places
    uint256 public min_claim;
    uint256 public claim_fee;
    address[] public admins;

    struct Packages {
        uint256 packageId;
        string PackageName;
        uint256 duration;
        uint256 amountMin;
        uint256 amountMax;
        uint256 rewardDaily;
        uint256 minimumClaim;
        uint256 totalStaked;
        uint256 userStaked;
        uint256 terms;
        uint256 apy;
        bool status;
    }

    struct Stake {
        uint256 stakingId;
        uint256 packageId;
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        uint256 lastClaim;
        uint256 rewardDay;
        uint256 alreadyClaimed;
        uint256 minimumClaim;
        bool claimed;
    }

    struct ReferralCommision {
        uint256 referral;
        uint256 staker;
        uint256 level;
        uint256 amount;
        uint256 commision;
        uint256 date;
    }

    Packages[] public packageList;

    mapping(address => Stake[]) public stakers;
    mapping(address => mapping(uint256 => ReferralCommision[]))
        public referralUsers;
    mapping(address => uint256) public currentCommissions;
    mapping(address => bool) public isAdmin;

    constructor() {
        isAdmin[msg.sender] = true; // Deployer is the first admin
        admins.push(msg.sender); // Tambahkan deployer ke dalam list admin
        transferOwnership(msg.sender); // Set the deployer as the owner
    }

    /*  Admin Role  */

    modifier onlyAdmin() {
        require(isAdmin[msg.sender], "Not an admin");
        _;
    }

    function addAdmin(address _admin) external onlyOwner {
        require(!isAdmin[_admin], "Address is already an admin");
        isAdmin[_admin] = true;
        admins.push(_admin);
    }

    function removeAdmin(address _admin) external onlyOwner {
        require(isAdmin[_admin], "Address is not an admin");
        isAdmin[_admin] = false;

        for (uint256 i = 0; i < admins.length; i++) {
            if (admins[i] == _admin) {
                admins[i] = admins[admins.length - 1];
                admins.pop();
                break;
            }
        }
    }

    function addPackage(
        uint256 _packageId,
        string memory _PackageName,
        uint256 _duration,
        uint256 _amounMin,
        uint256 _amountMax,
        uint256 _rewardDaily,
        uint256 _minClaim,
        uint256 _apy,
        uint256 _terms,
        bool _status
    ) external onlyAdmin returns (bool) {
        Packages memory newPackage = Packages({
            packageId: _packageId,
            PackageName: _PackageName,
            duration: _duration,
            amountMin: _amounMin,
            amountMax: _amountMax,
            rewardDaily: _rewardDaily,
            minimumClaim: _minClaim,
            status: _status,
            terms: _terms,
            apy: _apy,
            totalStaked: 0,
            userStaked: 0
        });

        packageList.push(newPackage);
        return true;
    }

    function removePackage(uint256 _index) external onlyAdmin {
        require(_index < packageList.length, "Invalid index");
        delete packageList[_index];
    }

    function getAllPackages() external view returns (Packages[] memory) {
        return packageList;
    }

    function getAdmins() external view returns (address[] memory) {
        return admins;
    }

    function stake(
        uint256 _stakingId,
        uint256 _amount,
        address _referral1,
        address _referral2,
        address _referral3
    ) external returns (bool) {
        require(_stakingId < packageList.length, "Invalid staking ID");
        Packages storage selectedPackage = packageList[_stakingId];
        require(selectedPackage.status, "Package is not available");
        require(
            _amount >= selectedPackage.amountMin &&
                _amount <= selectedPackage.amountMax,
            "Invalid staking amount"
        );
        require(
            VTEXToken.transferFrom(msg.sender, address(this), _amount),
            "TransferFrom failed"
        );
        stakers[msg.sender].push(
            Stake({
                stakingId: _stakingId,
                packageId: selectedPackage.packageId,
                amount: _amount,
                startTime: block.timestamp,
                lastClaim: block.timestamp,
                endTime: block.timestamp + selectedPackage.duration,
                rewardDay: (_amount * selectedPackage.rewardDaily) / 1000000,
                alreadyClaimed: 0,
                minimumClaim: selectedPackage.minimumClaim,
                claimed: false
            })
        );

        selectedPackage.totalStaked += _amount;
        selectedPackage.userStaked += 1;

        emit Staked(
            block.timestamp,
            msg.sender,
            selectedPackage.packageId,
            _amount,
            block.timestamp + selectedPackage.duration
        );

        // Calculate referral commission
        uint256 referral1Commission = (_amount * 7) / 100;
        uint256 referral2Commission = (_amount * 2) / 100;
        uint256 referral3Commission = (_amount * 1) / 100;

        // Add referral commission to currentCommissions mapping
        if (_referral1 != address(0) && _referral1 != msg.sender) {
            currentCommissions[_referral1] += referral1Commission;
            referralUsers[_referral1][_stakingId].push(
                ReferralCommision({
                    referral: referral1Commission,
                    staker: _amount,
                    level: 1,
                    amount: _amount,
                    commision: referral1Commission,
                    date: block.timestamp
                })
            );
        }

        if (
            _referral2 != address(0) &&
            _referral2 != msg.sender &&
            _referral2 != _referral1 &&
            _referral2 != _referral3
        ) {
            currentCommissions[_referral2] += referral2Commission;
            referralUsers[_referral2][_stakingId].push(
                ReferralCommision({
                    referral: referral2Commission,
                    staker: _amount,
                    level: 2,
                    amount: _amount,
                    commision: referral2Commission,
                    date: block.timestamp
                })
            );
        }
        if (
            _referral3 != address(0) &&
            _referral3 != msg.sender &&
            _referral3 != _referral2 &&
            _referral3 != _referral1
        ) {
            currentCommissions[_referral2] += referral3Commission;
            referralUsers[_referral2][_stakingId].push(
                ReferralCommision({
                    referral: referral3Commission,
                    staker: _amount,
                    level: 3,
                    amount: _amount,
                    commision: referral3Commission,
                    date: block.timestamp
                })
            );
        }
        return true; // Return true to indicate success
    }

    function claimReferralCommission() external {
        uint256 myCommisions = currentCommissions[msg.sender];
        require(
            myCommisions >= min_claim * DECIMALS_MULTIPLIER,
            "Mimimum claim is 20 DR168"
        );
        uint256 claimFee = claim_fee * DECIMALS_MULTIPLIER;
        uint256 userReceived = myCommisions - claimFee;
        require(
            VTEXToken.transfer(financeAddress, claimFee),
            "Referral commission Fee transfer failed"
        );
        require(
            VTEXToken.transfer(msg.sender, userReceived),
            "Referral commission Fee transfer failed"
        );
        currentCommissions[msg.sender] = 0;

        emit claimReferral(block.timestamp, msg.sender, myCommisions);
    }

    function setFinanceAddress(address newFinanceAddress) external onlyAdmin {
        require(newFinanceAddress != address(0), "Invalid address");
        financeAddress = newFinanceAddress;
    }

    function getUserStakings(address user)
        public
        view
        returns (Stake[] memory)
    {
        return stakers[user];
    }

    function getTotalStakedAmount(address user) public view returns (uint256) {
        Stake[] memory userStakings = stakers[user];
        uint256 totalStakedAmount = 0;
        for (uint256 i = 0; i < userStakings.length; i++) {
            totalStakedAmount += userStakings[i].amount;
        }
        return totalStakedAmount;
    }

    function getMyStakingsAndTotalAmount()
        external
        view
        returns (Stake[] memory, uint256)
    {
        Stake[] memory myStakings = getUserStakings(msg.sender);
        uint256 totalStakedAmount = getTotalStakedAmount(msg.sender);
        return (myStakings, totalStakedAmount);
    }

    function getCurrentReward(address user, uint256 stakingIndex)
        public
        view
        returns (uint256)
    {
        Stake[] memory userStakings = stakers[user];
        require(stakingIndex < userStakings.length, "Invalid staking index");
        Stake memory staking = userStakings[stakingIndex];
        uint256 currentTime = block.timestamp;
        // Calculate the time difference in seconds
        uint256 timePassed = currentTime - staking.lastClaim;
        // Calculate the reward for the time passed
        uint256 currentReward = (timePassed * staking.rewardDay) / 86400;
        return currentReward;
    }

    function claimReward(uint256 stakingIndex) external {
        Stake[] storage userStakings = stakers[msg.sender];
        require(stakingIndex < userStakings.length, "Invalid staking index");
        Stake storage staking = userStakings[stakingIndex];

        uint256 currentTime = block.timestamp;
        uint256 currentReward = getCurrentReward(msg.sender, stakingIndex);

        // Calculate the time difference in seconds
        uint256 timePassed = currentTime - staking.lastClaim;

        // If the current time is beyond the end time, update the time passed to the difference between last claim and end time
        if (currentTime > staking.endTime) {
            timePassed = staking.endTime - staking.lastClaim;
        }

        uint256 totalReward = currentReward;

        // Check if the reward is greater than or equal to 20 tokens
        require(
            totalReward >= staking.minimumClaim,
            "Reward is not sufficient for claiming"
        );

        // Transfer the remaining reward to user
        require(
            VTEXToken.transfer(msg.sender, totalReward),
            "Reward transfer failed"
        );

        // Update staking details
        staking.lastClaim = currentTime;
        staking.alreadyClaimed += totalReward;

        emit Staked(
            block.timestamp,
            msg.sender,
            stakingIndex,
            staking.packageId,
            totalReward
        );
    }

    function releaseStaking(uint256 stakingIndex) external {
        Stake[] storage userStakings = stakers[msg.sender];
        require(stakingIndex < userStakings.length, "Invalid staking index");
        Stake storage staking = userStakings[stakingIndex];
        require(!staking.claimed, "Staking already claimed");
        require(block.timestamp > staking.endTime, "Staking has not ended yet");

        uint256 currentTime = block.timestamp;
        uint256 currentReward = getCurrentReward(msg.sender, stakingIndex);
        uint256 totalAmount = staking.amount + currentReward;

        // Update staking details
        staking.lastClaim = currentTime;
        staking.claimed = true;

        // Transfer the total amount to user
        require(VTEXToken.transfer(msg.sender, totalAmount), "Transfer failed");

        // Update package details
        Packages storage selectedPackage = packageList[staking.packageId];
        selectedPackage.totalStaked -= staking.amount; // Reduce total staked
        selectedPackage.userStaked--; // Decrease user staked count

        emit releaseStakingEvent(
            block.timestamp,
            msg.sender,
            stakingIndex,
            staking.packageId
        );
    }

    function transferTokens(address to, uint256 amount) external onlyAdmin {
        require(to != address(0), "Invalid address");
        require(amount > 0, "Amount must be greater than 0");
        require(VTEXToken.transfer(to, amount), "Transfer failed");
    }

    function setVTEXTokenAddress(address newTokenAddress) external onlyAdmin {
        require(newTokenAddress != address(0), "Invalid address");
        VTEXToken = IERC20(newTokenAddress);
    }

    function updatePackageStatus(uint256 packageId, bool newStatus)
        external
        onlyAdmin
    {
        require(packageId < packageList.length, "Invalid package ID");
        Packages storage selectedPackage = packageList[packageId];
        selectedPackage.status = newStatus;
    }

    function setMinClaim(uint256 _minClaim) external onlyAdmin {
        min_claim = _minClaim;
    }

    function setClaimFee(uint256 _claimFee) external onlyAdmin {
        claim_fee = _claimFee;
    }

    function updateStakeStatus(address user, uint256 stakingIndex)
        external
        onlyAdmin
    {
        require(user != address(0), "Invalid address");
        require(stakingIndex < stakers[user].length, "Invalid staking index");

        // Toggle the claim status
        stakers[user][stakingIndex].claimed = !stakers[user][stakingIndex]
            .claimed;

        emit StakeStatusUpdated(
            block.timestamp,
            user,
            stakingIndex,
            stakers[user][stakingIndex].claimed
        );
    }

    function updateStake(
        address user,
        uint256 stakingIndex,
        uint256 newAmount,
        uint256 newStartTime,
        uint256 newEndTime,
        uint256 newLastClaim,
        uint256 newRewardDay,
        uint256 newAlreadyClaimed,
        uint256 newMinimumClaim,
        bool newClaimed
    ) external onlyAdmin {
        require(user != address(0), "Invalid address");
        require(stakingIndex < stakers[user].length, "Invalid staking index");

        Stake storage staking = stakers[user][stakingIndex];

        staking.amount = newAmount;
        staking.startTime = newStartTime;
        staking.endTime = newEndTime;
        staking.lastClaim = newLastClaim;
        staking.rewardDay = newRewardDay;
        staking.alreadyClaimed = newAlreadyClaimed;
        staking.minimumClaim = newMinimumClaim;
        staking.claimed = newClaimed;

        emit StakeUpdated(
            block.timestamp,
            user,
            stakingIndex,
            newAmount,
            newStartTime,
            newEndTime,
            newLastClaim,
            newRewardDay,
            newAlreadyClaimed,
            newMinimumClaim,
            newClaimed
        );
    }

    event StakeUpdated(
        uint256 timeStamp,
        address indexed user,
        uint256 stakingIndex,
        uint256 newAmount,
        uint256 newStartTime,
        uint256 newEndTime,
        uint256 newLastClaim,
        uint256 newRewardDay,
        uint256 newAlreadyClaimed,
        uint256 newMinimumClaim,
        bool newClaimed
    );

    event claimReferral(
        uint256 timeStamp,
        address indexed user,
        uint256 myCommisions
    );

    event Staked(
        uint256 timeStamp,
        address indexed user,
        uint256 packageId,
        uint256 amount,
        uint256 endTime
    );

    event Claimed(
        uint256 timeStamp,
        address indexed user,
        uint256 StakingId,
        uint256 packageId,
        uint256 amount
    );

    event releaseStakingEvent(
        uint256 timeStamp,
        address indexed user,
        uint256 StakingId,
        uint256 packageId
    );

    event StakeStatusUpdated(
        uint256 timeStamp,
        address indexed user,
        uint256 stakingIndex,
        bool newStatus
    );
}
