      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IERC20 {
    function allowance(address owner, address spender) external view returns (uint256);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}

contract TransferMCTManually is Ownable {
    IERC20 private token;

    event Data (
        uint256 amount,
        address indexed receiver,
        address indexed userId,
        string status,
        string context
    );

    constructor(address tokenContractAddr_) {
        token = IERC20(tokenContractAddr_);
    }

    // Ensure that only the contract owner can set the token address
    function setTokenAddr(address newContractAddr_) external onlyOwner {
        require(newContractAddr_ != address(0), "setTokenAddr: newContractAddr_ is address ZERO");
        token = IERC20(newContractAddr_);
    }

    // Function to get the token address
    function getTokenAddr() external view returns (address) {
        return address(token);
    }

    function transferMCT(
        address receiver_,
        uint256 amount_,
        string memory status_,
        string memory context_
    ) external {
        // Validate input parameters
        require(receiver_ != address(0), "transferMCT: receiver is address ZERO");
        require(amount_ > 0, "transferMCT: amount must be greater than ZERO");

        // Get the sender address
        address sender = msg.sender;
        require(sender != address(0), "transferMCT: sender is address ZERO");

        // Check allowance before transfer
        uint256 allowance = token.allowance(sender, address(this));
        require(allowance >= amount_, "transferMCT: Insufficient allowance");

        // Perform the transfer
        bool transferSuccess = token.transferFrom(sender, receiver_, amount_);
        require(transferSuccess, "transferMCT: Transfer failed");

        // Emit transfer data
        emit Data(amount_, receiver_, sender, status_, context_);
    }
}
