      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import {ERC1155} from "openzeppelin-contracts/token/ERC1155/ERC1155.sol";
import {Ownable} from "openzeppelin-contracts/access/Ownable.sol";
import {EnumerableSet} from "openzeppelin-contracts/utils/structs/EnumerableSet.sol";
import {ISwagActionHook} from "./interfaces/ISwagActionHook.sol";
import {LibEncode} from "./libraries/LibEncode.sol";

contract WildcardSwag is ERC1155, Ownable {
    // ----------------- Errors -------------------- //

    error ActionUnauthorized();
    error MintToZeroAddress();
    error FrozenMetadata();
    error InvalidToken();
    error MetadataNotSet();
    error InvalidNumUids();
    error NotUidOwner();
    error ZeroAddressNotAllowed();
    error InvalidTokenId();

    // ----------------- Events -------------------- //

    event MetadataFrozen(uint256 indexed tokenId);
    event SwagActionHookAddressSet(address indexed swagActionHookAddress);

    // ----------------- State variables -------------------- //

    /// @notice contract that verifies swag actions (minting, setting token URI, etc)
    ISwagActionHook public swagActionHook;
    /// @notice counter for the uid (unique identifier for each token)
    uint256 public uidCounter;
    /// @notice mapping from token id to owner address to set of uids the owner owns for that token
    mapping(uint256 => mapping(address => EnumerableSet.UintSet)) private _tokenIdToOwnerToUids;
    /// @notice list of tokenIds that have been minted so far
    uint256[] public tokenIds;
    /// @notice mapping from token ID to whether it exists
    mapping(uint256 => bool) public tokenIdToExists;
    /// @notice mapping from token ID to metadata URI
    mapping(uint256 => string) public tokenURIs;
    /// @notice mapping from token ID to whether the metadata is frozen
    mapping(uint256 => bool) public isMetadataFrozen;

    /// @notice pass empty string to ERC1155 constructor because we override uri in this contract, so it is not needed in parent
    constructor(address swagActionHookAddress) ERC1155("") {
        if (swagActionHookAddress == address(0)) {
            revert ZeroAddressNotAllowed();
        }
        swagActionHook = ISwagActionHook(swagActionHookAddress);
    }

    // ----------------- Actions -------------------- //

    /**
     * @notice Mints tokens to given address
     */
    function mint(address to, uint256 id, uint256 amount) public {
        // make sure not minting to the zero address
        if (to == address(0)) {
            revert MintToZeroAddress();
        }

        // make sure id is not zero
        if (id == 0) {
            revert InvalidTokenId();
        }

        // make sure the msg.sender is allowed to mint
        if (!swagActionHook.canMint(msg.sender)) {
            revert ActionUnauthorized();
        }

        // if this is the first time the token is being minted, add it to the list of tokenIds
        if (!tokenIdToExists[id]) {
            tokenIds.push(id);
            tokenIdToExists[id] = true;
        }

        _mint(to, id, amount, "");

        // read uid into memory
        uint256 currUid = uidCounter;
        EnumerableSet.UintSet storage toUids = _tokenIdToOwnerToUids[id][to];
        for (uint256 i = 0; i < amount; ++i) {
            // track each individual uid
            uint256 nextUid = ++currUid;
            EnumerableSet.add(toUids, nextUid);
        }

        uidCounter += amount;
    }

    function _afterTokenTransfer(
        address, // operator
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override {
        // ignore mint
        if (from == address(0)) {
            return;
        }

        // a user can transfer no ids if they want, this is a noop
        // note: ids and amounts arrays are guaranteed to be the same length at this point
        if (ids.length == 0) {
            return;
        }

        // we only transfer specific uids if bytes data is present and the number of token ids being transferred is exactly 1
        // (i.e. transferring specific uids on a bulk transfer is not supported)
        uint256 numTokenIds = ids.length;
        bool useSpecificUids = data.length > 0 && numTokenIds == 1;
        if (useSpecificUids) {
            // convert bytes data to array of uids
            uint256[] memory uidsToTransfer = LibEncode.decodeUint256Array(data);
            // make sure the number of uids matches the amount of tokens being transferred
            uint256 amount = amounts[0];
            if (uidsToTransfer.length != amount) {
                revert InvalidNumUids();
            }

            uint256 tokenId = ids[0];
            EnumerableSet.UintSet storage fromUids = _tokenIdToOwnerToUids[tokenId][from];
            EnumerableSet.UintSet storage toUids = _tokenIdToOwnerToUids[tokenId][to];
            _transferUids(uidsToTransfer, fromUids, toUids);
        } else {
            // randomly select uids for each token being transferred
            for (uint256 i = 0; i < numTokenIds; ++i) {
                uint256 tokenId = ids[i];
                uint256 amount = amounts[i];
                EnumerableSet.UintSet storage fromUids = _tokenIdToOwnerToUids[tokenId][from];
                EnumerableSet.UintSet storage toUids = _tokenIdToOwnerToUids[tokenId][to];
                // randomly select the uids to transfer for this tokenId
                uint256[] memory uidsToTransfer = new uint256[](amount);
                for (uint256 j = 0; j < amount; ++j) {
                    uidsToTransfer[j] = EnumerableSet.at(fromUids, j);
                }

                // transfer the uids
                _transferUids(uidsToTransfer, fromUids, toUids);
            }
        }
    }

    /**
     * Transfers a specific set of uids from one user to another, reverting if the user does not own all the uids being transferred
     */
    function _transferUids(
        uint256[] memory uids,
        EnumerableSet.UintSet storage fromUids,
        EnumerableSet.UintSet storage toUids
    ) private {
        uint256 uidsLen = uids.length;
        for (uint256 i = 0; i < uidsLen; ++i) {
            uint256 uid = uids[i];
            if (!EnumerableSet.contains(fromUids, uid)) {
                revert NotUidOwner();
            }

            EnumerableSet.remove(fromUids, uid);
            EnumerableSet.add(toUids, uid);
        }
    }

    /**
     * @notice Sets token uri for a specific token
     */
    function setTokenURI(uint256 tokenId, string memory tokenURI) public {
        // make sure the token exists
        if (!tokenIdToExists[tokenId]) {
            revert InvalidToken();
        }

        // make sure the metadata isn't frozen
        if (isMetadataFrozen[tokenId]) {
            revert FrozenMetadata();
        }

        // make sure the msg.sender is allowed to set the tokenURI
        if (!swagActionHook.canSetTokenURI(msg.sender)) {
            revert ActionUnauthorized();
        }

        tokenURIs[tokenId] = tokenURI;
        emit URI(tokenURI, tokenId);
    }

    /**
     * @notice Freeze the metadata uri for a specific token. This action cannot be undone
     */
    function freezeMetadata(uint256 tokenId) public {
        // make sure the token exists
        if (!tokenIdToExists[tokenId]) {
            revert InvalidToken();
        }

        // make sure the tokenURI has been set (is not empty)
        if (bytes(tokenURIs[tokenId]).length == 0) {
            revert MetadataNotSet();
        }

        // make sure the msg.sender is allowed to freeze the metadata
        if (!swagActionHook.canFreezeMetadata(msg.sender)) {
            revert ActionUnauthorized();
        }

        isMetadataFrozen[tokenId] = true;
    }

    // ----------------- Owner functions -------------------- //

    /**
     * @notice Sets the hook contract that verifies swag actions (minting, setting URI, etc)
     */
    function setSwagActionHook(address swagActionHookAddress) public onlyOwner {
        swagActionHook = ISwagActionHook(swagActionHookAddress);
        emit SwagActionHookAddressSet(swagActionHookAddress);
    }

    // ----------------- View functions -------------------- //

    /**
     * @notice Returns the list of valid tokenIds
     */
    function getTokenIds() public view returns (uint256[] memory) {
        return tokenIds;
    }

    /**
     * @notice Returns the total number of swag pins
     */
    function getNumTokenIds() public view returns (uint256) {
        return tokenIds.length;
    }

    /**
     * @notice Checks whether the given account owns any piece of swag
     */
    function ownsSwag(address account) public view returns (bool) {
        uint256 numTokens = tokenIds.length;
        for (uint256 i = 0; i < numTokens; ++i) {
            if (balanceOf(account, tokenIds[i]) > 0) {
                return true;
            }
        }

        return false;
    }

    /**
     * @notice Override uri function from ERC1155
     */
    function uri(uint256 tokenId) public view override returns (string memory) {
        return tokenURIs[tokenId];
    }

    /// @notice Encode uids into bytes data. To be used for transferring specific uids
    function encodeUids(uint256[] calldata uids) public pure returns (bytes memory) {
        return LibEncode.encodeUint256Array(uids);
    }

    /// @notice Returns all uids the user owns for the given tokenId
    function getUids(address owner, uint256 tokenId) public view returns (uint256[] memory) {
        return EnumerableSet.values(_tokenIdToOwnerToUids[tokenId][owner]);
    }

    /// @notice Returns whether the user owns the given uid
    function ownsUid(address owner, uint256 tokenId, uint256 uid) public view returns (bool) {
        return EnumerableSet.contains(_tokenIdToOwnerToUids[tokenId][owner], uid);
    }

    /// @notice Returns the value stored at position `index` in the user's uid set
    function uidAt(address owner, uint256 tokenId, uint256 index) public view returns (uint256) {
        return EnumerableSet.at(_tokenIdToOwnerToUids[tokenId][owner], index);
    }

    /// @notice Returns the number of uid values the user owns for the given tokenId
    function numUids(address owner, uint256 tokenId) public view returns (uint256) {
        return EnumerableSet.length(_tokenIdToOwnerToUids[tokenId][owner]);
    }
}
