     * @param data raw bytes data
     * @param sizeBytes The size in bytes of each element in the array. ex. uint16 array = 2, uint32 array = 4, etc.
     */
    function decodeArray(bytes memory data, uint8 sizeBytes) internal pure returns (bytes32[] memory) {
        uint32 arrayLength = uint32(bytes4(slice(data, 0, 4)));
        bytes32[] memory array = new bytes32[](arrayLength);
        for (uint256 i = 0; i < arrayLength; i++) {
            array[i] = bytes32(slice(data, 4 + (uint256(sizeBytes) * i), sizeBytes));
