contract StakingContract is Ownable {

    using Strings for uint256;
    using SafeMath for uint256;
    using Counters for Counters.Counter;
    using Address for address;


    address public erc20ContractAddress = address(0xAEB3e641Ec85517A63604CB13FCE73BA22FcC827);


    bool public paused = false;
    bool public claimsPaused = false;


    mapping(address => StakeHolder) public stakeHolders;
    mapping(uint256 => MonthlyStake) public monthlyStakes;


    //how often a claim can be made
    uint256 public contractClaimIntervalMultiplier = 30;
    //how often a claim can be made
    uint256 public contractClaimIntervalSeconds = 1 days;
    uint256 public totalRewards = 0;
    uint256 public currentStakes = 0;

    uint256 public minStakeAmount = 100000000000000000000;


    struct StakeHolder {
        uint256 amount;
        uint256 since;
        uint256 intervalUnit;
        string interestPercentStr;
        uint256 claimableAmountPerInterval;
        uint256 totalClaimableAmount;
        uint256 claimedAmount;
        uint256 claimIntervalSeconds;
        uint256 claimIntervalMultiplier;
        uint256 releaseTime;

        uint256 lastClaim;
    }

    struct MonthlyStake {
        uint256 intervalUnit;
        string interestPercentStr;
        uint256 interestPercent;
    }


    constructor()  {

    }

    function setMinStakeAmount(uint256 _minStakeAmount) public onlyOwner {
        require(_minStakeAmount > 0, "Min Stake amount must be greater than zero");
        minStakeAmount = _minStakeAmount;
    }

    function setContractIntervalParams(uint256 _contractClaimIntervalMultiplier, uint256 _contractClaimIntervalSeconds) public onlyOwner {
        require(_contractClaimIntervalMultiplier > 0, "Multiplier must be greater than zero");
        require(_contractClaimIntervalSeconds > 0, "Interval seconds amount must be greater than zero");
        contractClaimIntervalMultiplier = _contractClaimIntervalMultiplier;
        contractClaimIntervalSeconds = _contractClaimIntervalSeconds;
    }


    function getMonthlyStake(uint256 _intervalUnit) public view returns (MonthlyStake memory){
        return monthlyStakes[_intervalUnit];
    }

    function hasMonthlyStake(uint256 _intervalUnit) public view returns (bool){
        return monthlyStakes[_intervalUnit].intervalUnit > 0;
    }


    function addMonthlyStake(uint256 _intervalUnit, string memory _interestPercentStr, uint256 _interestPercent) public onlyOwner {
        require(!hasMonthlyStake(_intervalUnit), "Monthly stake already exists");
        require(_interestPercent > 0, "Interest percent must be greater than zero");

        monthlyStakes[_intervalUnit] = MonthlyStake(
            _intervalUnit,
            _interestPercentStr,
            _interestPercent
        );
    }

    function editMonthlyStake(uint256 _intervalUnit, string memory _interestPercentStr, uint256 _interestPercent) public onlyOwner {
        require(hasMonthlyStake(_intervalUnit), "Monthly stake does not exist");
        MonthlyStake memory item = monthlyStakes[_intervalUnit];
        item.interestPercentStr = _interestPercentStr;
        item.interestPercent = _interestPercent;
        monthlyStakes[_intervalUnit] = item;
    }

    function deleteMonthlyStake(uint256 _intervalUnit) public onlyOwner {
        require(hasMonthlyStake(_intervalUnit), "Monthly stake does not exist");
        delete monthlyStakes[_intervalUnit];
    }


    function getStake(address _stakeHolder) public view returns (StakeHolder memory){
        return stakeHolders[_stakeHolder];
    }


    function hasStake(address _stakeHolder) public view returns (bool){
        return stakeHolders[_stakeHolder].amount > 0;
    }


    function stakeTokens(address stakeAddress, uint256 _amount, uint256 _intervalUnit) public {
        require(_amount >= minStakeAmount, "Amount is less than the minimum stake amount");
        require(!paused, "Contract is paused");
        require(!hasStake(stakeAddress), "Address already has stake");
        require(hasMonthlyStake(_intervalUnit), "Monthly stake does not exist");
        IERC20 token = IERC20(erc20ContractAddress);
        require(token.balanceOf(msg.sender) >= _amount, "Insufficient ERC20 balance for staking.");
        //check allowance
        if (_amount > token.allowance(msg.sender, address(this))) {
            revert("Insufficient ERC20 allowance.");
        }
        //do the erc20 token transfer
        token.transferFrom(msg.sender, address(this), _amount);

        //calculate
        _addStakeholder(stakeAddress, _amount, _intervalUnit, block.timestamp);

    }

    function _addStakeholder(address stakeAddress, uint256 _amount, uint256 _intervalUnit, uint256 _since) internal {
        MonthlyStake memory monthlyStake = monthlyStakes[_intervalUnit];

        uint256 contractClaimInterval = contractClaimIntervalSeconds * contractClaimIntervalMultiplier;

        uint256 _releaseTime = block.timestamp + (monthlyStake.intervalUnit * contractClaimInterval);

        uint256 _totalClaimableAmount = _amount.mul(monthlyStake.interestPercent).div(10000);
        uint256 intervalsCount = monthlyStake.intervalUnit.mul(contractClaimIntervalMultiplier);

        uint256 _claimableAmountPerInterval = _totalClaimableAmount.div(intervalsCount);

        StakeHolder memory stakeHolder;
        stakeHolder.amount = _amount;
        stakeHolder.since = _since;
        stakeHolder.intervalUnit = monthlyStake.intervalUnit;
        stakeHolder.interestPercentStr = monthlyStake.interestPercentStr;
        stakeHolder.claimableAmountPerInterval = _claimableAmountPerInterval;
        stakeHolder.totalClaimableAmount = _totalClaimableAmount;
        stakeHolder.claimedAmount = 0;
        stakeHolder.claimIntervalSeconds = contractClaimIntervalSeconds;
        stakeHolder.claimIntervalMultiplier = contractClaimIntervalMultiplier;
        stakeHolder.releaseTime = _releaseTime;
        stakeHolder.lastClaim = _since;

        stakeHolders[stakeAddress] = stakeHolder;
        currentStakes = currentStakes.add(_amount);
    }


    function calcStakeHolderStakeRewardForIntervalUnit(uint256 _amount, uint256 _intervalUnit) public view returns (StakeHolder memory){
        require(_amount >= minStakeAmount, "Amount is less than the minimum stake amount");
        require(hasMonthlyStake(_intervalUnit), "Monthly stake does not exist");

        MonthlyStake memory monthlyStake = monthlyStakes[_intervalUnit];

        uint256 contractClaimInterval = contractClaimIntervalSeconds * contractClaimIntervalMultiplier;

        uint256 _releaseTime = block.timestamp + (monthlyStake.intervalUnit * contractClaimInterval);
        uint256 _since = block.timestamp;

        uint256 _totalClaimableAmount = _amount.mul(monthlyStake.interestPercent).div(10000);
        uint256 intervalsCount = monthlyStake.intervalUnit.mul(contractClaimIntervalMultiplier);
        uint256 _claimIntervalSeconds = contractClaimIntervalSeconds;
        uint256 _claimIntervalMultiplier = contractClaimIntervalMultiplier;

        uint256 _claimableAmountPerInterval = _totalClaimableAmount.div(intervalsCount);
        uint256 _lastClaim = _since;
        uint256 _claimedAmount = 0;


        return StakeHolder(
            _amount,
            _since,
            monthlyStake.intervalUnit,
            monthlyStake.interestPercentStr,
            _claimableAmountPerInterval,
            _totalClaimableAmount,
            _claimedAmount,
            _claimIntervalSeconds,
            _claimIntervalMultiplier,
            _releaseTime,
            _lastClaim
        );
    }

    function calcStakeRewardForIntervalUnit(uint256 _amount, uint256 _intervalUnit) public view returns (uint256){
        require(_amount >= minStakeAmount, "Amount is less than the minimum stake amount");
        require(hasMonthlyStake(_intervalUnit), "Monthly stake does not exist");

        MonthlyStake memory monthlyStake = monthlyStakes[_intervalUnit];
        uint256 _totalClaimableAmount = _amount.mul(monthlyStake.interestPercent).div(10000);

        return _totalClaimableAmount;
    }


    //either get rewards or close stake and get everything
    function claimPayment(address _stakeAddress) public {
        require(!claimsPaused, "Claims are paused");
        require(hasStake(_stakeAddress), "Address does not have stake");

        StakeHolder memory stakeHolder = stakeHolders[_stakeAddress];
        IERC20 token = IERC20(erc20ContractAddress);
        uint256 remainReward = stakeHolder.totalClaimableAmount.sub(stakeHolder.claimedAmount);
        uint256 payReward = 0;
        if (stakeHolder.releaseTime <= block.timestamp) {
            //transfer stake plus any remaining rewards to user and delete stake
            payReward = stakeHolder.amount.add(remainReward);
            require(token.balanceOf(address(this)) >= payReward, "Insufficient ERC20 contract balance for transfer.");

            IERC20(erc20ContractAddress).transfer(_stakeAddress, payReward);
            totalRewards = totalRewards.add(remainReward);
            currentStakes = currentStakes.sub(stakeHolder.amount);
            delete stakeHolders[_stakeAddress];

        } else {
            if (remainReward <= 0) {
                revert("No more rewards available");
            }

            uint256 unclaimedPeriodSeconds = block.timestamp - stakeHolder.lastClaim;
            if (unclaimedPeriodSeconds < stakeHolder.claimIntervalSeconds) {
                revert("Reward is not yet available");
            }

            uint256 payIntervals = 1;
            uint256 payIntervalsInSeconds = payIntervals.mul(stakeHolder.claimIntervalSeconds);

            while (payIntervalsInSeconds < unclaimedPeriodSeconds) {
                payIntervals++;
                payIntervalsInSeconds = payIntervals.mul(stakeHolder.claimIntervalSeconds);
            }

            payReward = payIntervals.mul(stakeHolder.claimableAmountPerInterval);
            if (payReward >= remainReward) {
                payReward = remainReward;
            }

            if (payReward <= 0) {
                revert("No more rewards available");
            }

            stakeHolder.lastClaim = block.timestamp;
            stakeHolder.claimedAmount = stakeHolder.claimedAmount.add(payReward);
            stakeHolders[_stakeAddress] = stakeHolder;

            //pay user with payReward
            require(token.balanceOf(address(this)) >= payReward, "Insufficient ERC20 contract balance for transfer.");

            IERC20(erc20ContractAddress).transfer(_stakeAddress, payReward);
            totalRewards = totalRewards.add(payReward);

        }
    }


    function checkPayment(address _stakeAddress) public view returns (uint256){
        require(!claimsPaused, "Claims are paused");
        require(hasStake(_stakeAddress), "Address does not have stake");

        StakeHolder memory stakeHolder = stakeHolders[_stakeAddress];
        uint256 remainReward = stakeHolder.totalClaimableAmount.sub(stakeHolder.claimedAmount);
        uint256 payReward = 0;
        if (stakeHolder.releaseTime <= block.timestamp) {
            //transfer stake plus any remaining rewards to user and delete stake
            payReward = stakeHolder.amount.add(remainReward);

        } else {
            if (remainReward <= 0) {
                return 0;
            }

            uint256 unclaimedPeriodSeconds = block.timestamp - stakeHolder.lastClaim;
            if (unclaimedPeriodSeconds < stakeHolder.claimIntervalSeconds) {
                return 0;
            }

            uint256 payIntervals = 1;
            uint256 payIntervalsInSeconds = payIntervals.mul(stakeHolder.claimIntervalSeconds);

            while (payIntervalsInSeconds < unclaimedPeriodSeconds) {
                payIntervals++;
                payIntervalsInSeconds = payIntervals.mul(stakeHolder.claimIntervalSeconds);
            }

            payReward = payIntervals.mul(stakeHolder.claimableAmountPerInterval);
            if (payReward >= remainReward) {
                payReward = remainReward;
            }

            if (payReward <= 0) {
                return 0;
            }

        }

        return payReward;
    }


    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function pauseClaims(bool _state) public onlyOwner {
        claimsPaused = _state;
    }


    function setStakeToken(address _erc20ContractAddress) public onlyOwner {
        erc20ContractAddress = _erc20ContractAddress;
    }


    function withdrawToken(address erc20Address, address to_, uint256 amount_) external onlyOwner {
        IERC20(erc20Address).approve(to_, amount_);
        IERC20(erc20Address).transfer(to_, amount_);
    }

    function withdrawNative() public payable onlyOwner {
        require(payable(msg.sender).send(address(this).balance));
    }

}
