    struct GlobalState {
        uint160 startPrice;
        int24 startTick;
        uint16 fee;
    }

// the top level state of the swap, the results of which are recorded in storage at the end
    struct SwapState {
        // the amount remaining to be swapped in/out of the input/output asset
        int256 amountSpecifiedRemaining;
        // the amount already swapped out/in of the output/input asset
        int256 amountCalculated;
        // current sqrt(price)
        uint160 sqrtPriceX96;
        // the tick associated with the current price
        int24 tick;
        // the current liquidity in range
        uint128 liquidity;
    }

    struct StepComputations {
        // the price at the beginning of the step
        uint160 sqrtPriceStartX96;
        // the next tick to swap to from the current tick in the swap direction
        int24 tickNext;
        // whether tickNext is initialized or not
        bool initialized;
        // sqrt(price) for the next tick (1/0)
        uint160 sqrtPriceNextX96;
        // how much is being swapped in in this step
        uint256 amountIn;
        // how much is being swapped out
        uint256 amountOut;
        // how much fee is being paid in
        uint256 feeAmount;
    }

/// @title Packed tick initialized state library
/// @notice Stores a packed mapping of tick index to its initialized state
/// @dev The mapping uses int16 for keys since ticks are represented as int24 and there are 256 (2^8) values per word.
library TickBitmap {
    /// @notice Computes the position in the mapping where the initialized bit for a tick lives
    /// @param tick The tick for which to compute the position
    /// @return wordPos The key in the mapping containing the word in which the bit is stored
    /// @return bitPos The bit position in the word where the flag is stored
    function position(int24 tick) private pure returns (int16 wordPos, uint8 bitPos) {
        wordPos = int16(tick >> 8);
        bitPos = uint8(tick % 256);
    }

    /// @notice Returns the next initialized tick contained in the same word (or adjacent word) as the tick that is either
    /// to the left (less than or equal to) or right (greater than) of the given tick
    /// @param poolAddress Pool containing the mapping in which to compute the next initialized tick
    /// @param tick The starting tick
    /// @param tickSpacing The spacing between usable ticks
    /// @param lte Whether to search for the next initialized tick to the left (less than or equal to the starting tick)
    /// @return next The next initialized or uninitialized tick up to 256 ticks away from the current tick
    /// @return initialized Whether the next tick is initialized, as the function only searches within up to 256 ticks
    function nextInitializedTickWithinOneWord(
        address poolAddress,
        int24 tick,
        int24 tickSpacing,
        bool lte
    ) internal view returns (int24 next, bool initialized) {
        IUniswapV3Pool pool = IUniswapV3Pool(poolAddress);
        int24 compressed = tick / tickSpacing;
        if (tick < 0 && tick % tickSpacing != 0) compressed--; // round towards negative infinity

        if (lte) {
            (int16 wordPos, uint8 bitPos) = position(compressed);
            // all the 1s at or to the right of the current bitPos
            uint256 mask = (1 << bitPos) - 1 + (1 << bitPos);
            uint256 masked = pool.tickBitmap(wordPos) & mask;

            // if there are no initialized ticks to the right of or at the current tick, return rightmost in the word
            initialized = masked != 0;
            // overflow/underflow is possible, but prevented externally by limiting both tickSpacing and tick
            next = initialized
            ? (compressed - int24(bitPos - BitMath.mostSignificantBit(masked))) * tickSpacing
            : (compressed - int24(bitPos)) * tickSpacing;
        } else {
            // start from the word of the next tick, since the current tick state doesn't matter
            (int16 wordPos, uint8 bitPos) = position(compressed + 1);
            // all the 1s at or to the left of the bitPos
            uint256 mask = ~((1 << bitPos) - 1);
            uint256 masked = pool.tickBitmap(wordPos) & mask;

            // if there are no initialized ticks to the left of the current tick, return leftmost in the word
            initialized = masked != 0;
            // overflow/underflow is possible, but prevented externally by limiting both tickSpacing and tick
            next = initialized
            ? (compressed + 1 + int24(BitMath.leastSignificantBit(masked) - bitPos)) * tickSpacing
            : (compressed + 1 + int24(type(uint8).max - bitPos)) * tickSpacing;
        }
    }
}
abstract contract UniV3likeQuoterCore {
    using LowGasSafeMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;

    function quote(
        address poolAddress,
        bool zeroForOne,
        int256 amountSpecified,
        uint160 sqrtPriceLimitX96
    ) public virtual view returns (int256 amount0, int256 amount1) {
        require(amountSpecified != 0, 'amountSpecified cannot be zero');
        bool exactInput = amountSpecified > 0;
        (int24 tickSpacing, uint16 fee, SwapState memory state) = getInitState(
            poolAddress,
            zeroForOne,
            amountSpecified,
            sqrtPriceLimitX96
        );
        // continue swapping as long as we haven't used the entire input/output and haven't reached the price limit
        while (state.amountSpecifiedRemaining != 0 && state.sqrtPriceX96 != sqrtPriceLimitX96) {
            StepComputations memory step;
            step.sqrtPriceStartX96 = state.sqrtPriceX96;

            (step.tickNext, step.initialized, step.sqrtPriceNextX96) = nextInitializedTickAndPrice(
                poolAddress,
                state.tick,
                tickSpacing,
                zeroForOne
            );
            // compute values to swap to the target tick, price limit, or point where input/output amount is exhausted
            (state.sqrtPriceX96, step.amountIn, step.amountOut, step.feeAmount) = SwapMath.computeSwapStep(
                state.sqrtPriceX96,
                getSqrtRatioTargetX96(zeroForOne, step.sqrtPriceNextX96, sqrtPriceLimitX96),
                state.liquidity,
                state.amountSpecifiedRemaining,
                fee
            );
            if (exactInput) {
                state.amountSpecifiedRemaining -= (step.amountIn + step.feeAmount).toInt256();
                state.amountCalculated = state.amountCalculated.sub(step.amountOut.toInt256());
            } else {
                state.amountSpecifiedRemaining += step.amountOut.toInt256();
                state.amountCalculated = state.amountCalculated.add((step.amountIn + step.feeAmount).toInt256());
            }
            // shift tick if we reached the next price
            if (state.sqrtPriceX96 == step.sqrtPriceNextX96) {
                // if the tick is initialized, run the tick transition
                if (step.initialized) {
                    (,int128 liquidityNet,,,,,,) = getTicks(poolAddress, step.tickNext);
                    // if we're moving leftward, we interpret liquidityNet as the opposite sign
                    // safe because liquidityNet cannot be type(int128).min
                    if (zeroForOne)
                        liquidityNet = -liquidityNet;
                    state.liquidity = LiquidityMath.addDelta(state.liquidity, liquidityNet);
                }
                state.tick = zeroForOne ? step.tickNext - 1 : step.tickNext;
            } else if (state.sqrtPriceX96 != step.sqrtPriceStartX96) {
                // recompute unless we're on a lower tick boundary (i.e. already transitioned ticks), and haven't moved
                state.tick = TickMath.getTickAtSqrtRatio(state.sqrtPriceX96);
            }
        }

        (amount0, amount1) = zeroForOne == exactInput
        ? (amountSpecified - state.amountSpecifiedRemaining, state.amountCalculated)
        : (state.amountCalculated, amountSpecified - state.amountSpecifiedRemaining);
    }

    function getInitState(
        address poolAddress,
        bool zeroForOne,
        int256 amountSpecified,
        uint160 sqrtPriceLimitX96
    ) internal view returns (int24 ts, uint16 fee, SwapState memory state) {
        GlobalState memory gs = getPoolGlobalState(poolAddress);
        checkSqrtPriceLimitWithinAllowed(zeroForOne, sqrtPriceLimitX96, gs.startPrice);
        ts = getTickSpacing(poolAddress);
        fee = gs.fee;
        state = SwapState({
        amountSpecifiedRemaining: amountSpecified,
        liquidity: getLiquidity(poolAddress),
        sqrtPriceX96: gs.startPrice,
        amountCalculated: 0,
        tick: gs.startTick
        });
    }

    function checkSqrtPriceLimitWithinAllowed(
        bool zeroForOne,
        uint160 sqrtPriceLimit,
        uint160 startPrice
    ) internal pure {
        bool withinAllowed = zeroForOne
        ? sqrtPriceLimit < startPrice && sqrtPriceLimit > TickMath.MIN_SQRT_RATIO
        : sqrtPriceLimit > startPrice && sqrtPriceLimit < TickMath.MAX_SQRT_RATIO;
        require(withinAllowed, 'sqrtPriceLimit out of bounds');
    }

    function nextInitializedTickAndPrice(
        address pool,
        int24 tick,
        int24 tickSpacing,
        bool zeroForOne
    ) internal view returns (int24 tickNext, bool initialized, uint160 sqrtPriceNextX96) {
        (tickNext, initialized) = nextInitializedTickWithinOneWord(pool, tick, tickSpacing, zeroForOne);
        // ensure that we do not overshoot the min/max tick, as the tick bitmap is not aware of these bounds
        if (tickNext < TickMath.MIN_TICK)
            tickNext = TickMath.MIN_TICK;
        else if (tickNext > TickMath.MAX_TICK)
            tickNext = TickMath.MAX_TICK;
        // get the price for the next tick
        sqrtPriceNextX96 = TickMath.getSqrtRatioAtTick(tickNext);
    }

    function getSqrtRatioTargetX96(
        bool zeroForOne,
        uint160 sqrtPriceNextX96,
        uint160 sqrtPriceLimitX96
    ) internal pure returns (uint160) {
        return (zeroForOne ? sqrtPriceNextX96<sqrtPriceLimitX96 : sqrtPriceNextX96>sqrtPriceLimitX96)
        ? sqrtPriceLimitX96
        : sqrtPriceNextX96;
    }

    function getPoolGlobalState(address pool) internal virtual view returns (GlobalState memory);

    function getLiquidity(address pool) internal virtual view returns (uint128);

    function getTickSpacing(address pool) internal virtual view returns (int24);

    function nextInitializedTickWithinOneWord(
        address poolAddress,
        int24 tick,
        int24 tickSpacing,
        bool zeroForOne
    ) internal virtual view returns (int24 next, bool initialized);

    function getTicks(address pool, int24 tick) internal virtual view returns (
        uint128 liquidityTotal,
        int128 liquidityDelta,
        uint256 outerFeeGrowth0Token,
        uint256 outerFeeGrowth1Token,
        int56 outerTickCumulative,
        uint160 outerSecondsPerLiquidity,
        uint32 outerSecondsSpent,
        bool initialized
    );

}

contract UniV3QuoterCore is UniV3likeQuoterCore {

    function getPoolGlobalState(
        address pool
    ) internal override view returns (GlobalState memory gs) {
        gs.fee = uint16(IUniswapV3Pool(pool).fee());
        (gs.startPrice, gs.startTick,,,,,) = IUniswapV3Pool(pool).slot0();
    }

    function getTickSpacing(
        address pool
    ) internal override view returns (int24) {
        return IUniswapV3Pool(pool).tickSpacing();
    }

    function getLiquidity(address pool) internal override view returns (uint128) {
        return IUniswapV3Pool(pool).liquidity();
    }

    function nextInitializedTickWithinOneWord(
        address poolAddress,
        int24 tick,
        int24 tickSpacing,
        bool zeroForOne
    ) internal override view returns (int24 next, bool initialized) {
        return TickBitmap.nextInitializedTickWithinOneWord(
            poolAddress,
            tick,
            tickSpacing,
            zeroForOne
        );
    }

    function getTicks(address pool, int24 tick) internal override view returns (
        uint128 liquidityTotal,
        int128 liquidityDelta,
        uint256 outerFeeGrowth0Token,
        uint256 outerFeeGrowth1Token,
        int56 outerTickCumulative,
        uint160 outerSecondsPerLiquidity,
        uint32 outerSecondsSpent,
        bool initialized
    ) {
        return IUniswapV3Pool(pool).ticks(tick);
    }

}

interface IUniswapV3StaticQuoter {
    /// @notice Returns the amount out received for a given exact input swap without executing the swap
    /// @param path The path of the swap, i.e. each token pair and the pool fee
    /// @param amountIn The amount of the first token to swap
    /// @return amountOut The amount of the last token that would be received
    function quoteExactInput(bytes memory path, uint256 amountIn)
    external
    view
    returns (uint256 amountOut);

    struct QuoteExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint256 amountIn;
        uint24 fee;
        uint160 sqrtPriceLimitX96;
    }

    /// @notice Returns the amount out received for a given exact input but for a swap of a single pool
    /// @param params The params for the quote, encoded as `QuoteExactInputSingleParams`
    /// tokenIn The token being swapped in
    /// tokenOut The token being swapped out
    /// fee The fee of the token pool to consider for the pair
    /// amountIn The desired input amount
    /// sqrtPriceLimitX96 The price limit of the pool that cannot be exceeded by the swap
    /// @return amountOut The amount of `tokenOut` that would be received
    function quoteExactInputSingle(QuoteExactInputSingleParams memory params)
    external
    view
    returns (uint256 amountOut);
}

interface IWMATIC {
    function deposit() external payable;
}
interface IUNIV3POSITION {
    function increaseLiquidity(INonfungiblePositionManager.IncreaseLiquidityParams calldata params) external payable returns (
        uint128 liquidity,
        uint256 amount0,
        uint256 amount1
    );
}

contract Liquidity is UniV3QuoterCore, Ownable {
    mapping(uint256 => Deposit) public deposits;
    using LowGasSafeMath for uint256;
    using LowGasSafeMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;
    using Path for bytes;

    ISwapRouter public immutable swapRouter;
    address public immutable factory;
    INonfungiblePositionManager public immutable nonfungiblePositionManager;
    IUNIV3POSITION public uniV3 = IUNIV3POSITION(0xC36442b4a4522E871399CD717aBDD847Ab11FE88);
    IQuoter public quote = IQuoter(0xb27308f9F90D607463bb33eA1BeBb41C27CE5AB6);
    address public USDT = 0xc2132D05D31c914a87C6611C10748AEb04B58e8F;
    address public WMATIC = 0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270;
    IWMATIC public wmatic = IWMATIC(WMATIC);
    address public immutable TREND;
    uint24 public immutable poolFee;
    uint public tokenIdOfPool;

    uint public addLPAmount;
    bool public isLP;
    address public MM;

    /// @notice Represents the deposit of an NFT
    struct Deposit {
        address owner;
        uint128 liquidity;
        address token0;
        address token1;
    }

    /// @dev deposits[tokenId] => Deposit

    constructor(ISwapRouter _swapRouter, INonfungiblePositionManager _nonfungiblePositionManager, address _TREND, uint24 _poolFee, address _factory, uint _tokenId, address mm) {
        require(mm != address(0), "Pools::mm: invalid input");
        swapRouter = _swapRouter;
        nonfungiblePositionManager = _nonfungiblePositionManager;
        TREND = _TREND;
        poolFee = _poolFee;
        factory = _factory;
        tokenIdOfPool = _tokenId;
        TransferHelper.safeApprove(_TREND, address(_swapRouter), 10000000000000000000 ether);
        TransferHelper.safeApprove(_TREND, address(_nonfungiblePositionManager), 10000000000000000000 ether);
        TransferHelper.safeApprove(WMATIC, address(_swapRouter), 10000000000000000000 ether);
        TransferHelper.safeApprove(WMATIC, address(_nonfungiblePositionManager), 10000000000000000000 ether);

        isLP = true;
        MM = mm;
    }

    function setIsLP() external onlyOwner {
        isLP = !isLP;
    }
    function setMM(address mm) external onlyOwner {
        require(mm != address(0), "Pools::mm: invalid input");
        MM = mm;
    }
    function getChainID() public pure returns (uint256) {
        uint256 id;
        assembly {
            id := chainid()
        }
        return id;
    }
    function TREND2USDT() external view returns(uint amountOut) {
        uint priceOnMatic = getQuote(TREND, WMATIC, 1 ether, poolFee);
        uint priceMatic = getQuote(WMATIC, USDT, priceOnMatic, poolFee);
        if(getChainID() == 137) priceMatic *= 10**12;
        return priceMatic;
    }
    function MATIC2USDT() external view returns(uint amountOut) {
        uint priceMatic = getQuote(WMATIC, USDT, 1 ether, 3000);
        if(getChainID() == 137) priceMatic *= 10**12;
        return priceMatic;
    }
    /// @notice swapExactInputSingle swaps a fixed amount of DAI for a maximum possible amount of WETH9
    /// using the DAI/WETH9 0.3% pool by calling `exactInputSingle` in the swap router.
    /// @dev The calling address must approve this contract to spend at least `amountIn` worth of its DAI for this function to succeed.
    /// @param amountIn The exact amount of DAI that will be swapped for WETH9.
    /// @return amountOut The amount of WETH9 received.
    function swapExactInputSingle(uint256 amountIn) internal returns (uint256 amountOut) {
        // Naively set amountOutMinimum to 0. In production, use an oracle or other data source to choose a safer value for amountOutMinimum.
        // We also set the sqrtPriceLimitx96 to be 0 to ensure we swap our exact input amount.
        ISwapRouter.ExactInputSingleParams memory params =
        ISwapRouter.ExactInputSingleParams({
        tokenIn: WMATIC,
        tokenOut: TREND,
        fee: poolFee,
        recipient: address(this),
        deadline: block.timestamp,
        amountIn: amountIn,
        amountOutMinimum: 0,
        sqrtPriceLimitX96: 0
        });

        // The call to `exactInputSingle` executes the swap.
        amountOut = swapRouter.exactInputSingle(params);
    }
    function _addLP(uint amountAdd0, uint amountAdd1) internal {
        INonfungiblePositionManager.IncreaseLiquidityParams memory params =
        INonfungiblePositionManager.IncreaseLiquidityParams({
        tokenId: tokenIdOfPool,
        amount0Desired: amountAdd0,
        amount1Desired: amountAdd1,
        amount0Min: 0,
        amount1Min: 0,
        deadline: block.timestamp
        });

        nonfungiblePositionManager.increaseLiquidity(params);
    }
    function addLP() external payable {
        uint toLP = msg.value * 75 / 100;
        addLPAmount += toLP;
        payable(MM).send(msg.value - toLP);
        uint addAmount = addLPAmount;
        if(addAmount < 1000 ether) {
            wmatic.deposit{value:addAmount}();
            uint amountAdd1 = swapExactInputSingle(addAmount/2);
            _addLP(addAmount/2, amountAdd1);
            addLPAmount = 0;
        } else {
            if(addAmount > 10_000 ether) addAmount = 10_000 ether;
            uint n = addAmount / 1000 ether;
            for(uint i = 0; i < n; i++) {
                uint _addAmount = 1000 ether;
                wmatic.deposit{value:_addAmount}();
                uint amountAdd1 = swapExactInputSingle(_addAmount/2);
                _addLP(_addAmount/2, amountAdd1);
                addLPAmount -= _addAmount;
            }
        }

    }

    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) private view returns (IUniswapV3Pool) {
        return IUniswapV3Pool(PoolAddress.computeAddress(factory, PoolAddress.getPoolKey(tokenA, tokenB, fee)));
    }

    function getQuote(address tokenIn, address tokenOut, uint256 amountIn, uint24 fee) public view returns (uint256 amountOut){
        bool zeroForOne = tokenIn < tokenOut;
        IUniswapV3Pool pool = getPool(tokenIn, tokenOut, fee);

        (int256 amount0, int256 amount1) = UniV3likeQuoterCore.quote(
            address(pool),
            zeroForOne,
            amountIn.toInt256(),
            zeroForOne ? TickMath.MIN_SQRT_RATIO + 1 : TickMath.MAX_SQRT_RATIO - 1
        );

        return zeroForOne ? uint256(-amount1) : uint256(-amount0);
    }
    function inCaseTokensGetStuck(IERC20 token, address user) external onlyOwner {
        uint _amount = token.balanceOf(address(this));
        require(token.transfer(user, _amount));
    }
    function getStuck(address user) external onlyOwner {
        uint _amount = address(this).balance;
        payable(user).transfer(_amount);
    }
}
