    "contracts/VoxiesServices.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./utils/BaseRelayRecipient.sol";
import "./utils/ContextMixin.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract VoxiesServices is
    BaseRelayRecipient,
    ContextMixin,
    Ownable,
    ReentrancyGuard,
    Pausable
{
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;
    Counters.Counter private id;

    IERC20 public immutable voxel;

    mapping(address => bool) private _admins;
    mapping(string => uint256) public servicesAndPrice;
    address public treasuryWallet;
    string[] public services;

    constructor(IERC20 _voxel, address _treasuryWallet) {
        voxel = _voxel;
        treasuryWallet = _treasuryWallet;
        _admins[msg.sender] = true;
    }

    struct Service {
        address buyer;
        uint256 price;
        uint256 service;
        uint256 timestamp;
    }

    event ServiceRequested(
        uint256 indexed id,
        address indexed buyer,
        string service,
        uint256 price,
        uint256 quantity,
        uint256 indexed timestamp
    );

    function serviceRequest(string memory serviceName, uint256 quantity)
        external
        whenNotPaused
        nonReentrant
    {
        uint256 price = servicesAndPrice[serviceName];
        require(price > 0, "Service does not exist");
        id.increment();
        require(quantity > 0, "please enter a valid quantity");

        voxel.safeTransferFrom(_msgSender(), treasuryWallet, price * quantity);

        emit ServiceRequested(
            id.current(),
            _msgSender(),
            serviceName,
            price * quantity,
            quantity,
            block.timestamp
        );
    }

    // Internal
    function _msgSender()
        internal
        view
        override(Context, BaseRelayRecipient)
        returns (address)
    {
        return BaseRelayRecipient._msgSender();
    }

    // Admin only
    modifier onlyAdmin() {
        require(
            _admins[_msgSender()] || _msgSender() == owner(),
            "Caller does not have Admin Access"
        );
        _;
    }

    function updateAdmin(address _admin, bool isAdmin) external onlyAdmin {
        _admins[_admin] = isAdmin;
    }

    function pause() public onlyAdmin {
        _pause();
    }

    function unpause() public onlyAdmin {
        _unpause();
    }

    function setTrustedForwarder(address _trustedForwarder) external onlyAdmin {
        trustedForwarder = _trustedForwarder;
    }

    function updateServices(string memory serviceName, uint256 price)
        public
        onlyAdmin
    {
        servicesAndPrice[serviceName] = price;

        bool alreadyExists = false;

        for (uint256 i = 0; i < services.length; i++) {
            if (
                keccak256(bytes(serviceName)) == keccak256(bytes(services[i]))
            ) {
                alreadyExists = true;
            }
        }
        if (alreadyExists == false) {
            services.push(serviceName);
        }
    }

    function updateServicesBulk(
        string[] memory serviceNames,
        uint256[] memory prices
    ) external onlyAdmin {
        require(
            serviceNames.length == prices.length,
            "services, and prices length do not match"
        );
        for (uint256 i = 0; i < serviceNames.length; i++) {
            updateServices(serviceNames[i], prices[i]);
        }
    }

    function updateTreasuryWallet(address newTreasuryWallet)
        external
        onlyAdmin
    {
        treasuryWallet = newTreasuryWallet;
    }

    function removeService(string memory serviceName) public onlyAdmin {
        for (uint256 i = 0; i < services.length - 1; i++) {
            if (
                keccak256(bytes(services[i])) == keccak256(bytes(serviceName))
            ) {
                delete services[i];
                break;
            }
        }
        delete servicesAndPrice[serviceName];
    }

    function removeServiceBulk(string[] memory serviceNames)
        external
        onlyAdmin
    {
        for (uint256 x = 0; x < serviceNames.length; x++) {
            for (uint256 i = 0; i < services.length - 1; i++) {
                if (
                    keccak256(bytes(services[i])) ==
                    keccak256(bytes(serviceNames[x]))
                ) {
                    delete services[i];
                    break;
                }
            }
            delete servicesAndPrice[serviceNames[x]];
        }
    }
}
"
