// along with this program.  If not, see <http://www.gnu.org/licenses/>.

pragma solidity 0.8.19;

import "./interfaces/IOracle.sol";
import "./Assimilators.sol";

contract Storage {
	struct Curve {
		// Curve parameters
		int128 alpha;
		int128 beta;
		int128 delta;
		int128 epsilon;
		int128 lambda;
		int128[] weights;
		// Assets and their assimilators
		Assimilator[] assets;
		mapping(address => Assimilator) assimilators;
		// Oracles to determine the price
		// Note that 0'th index should always be USDC 1e18
		// Oracle's pricing should be denominated in Currency/USDC
		mapping(address => IOracle) oracles;
		// ERC20 Interface
		uint256 totalSupply;
		mapping(address => uint256) balances;
		mapping(address => mapping(address => uint256)) allowances;
	}

	struct Assimilator {
		address addr;
		uint8 ix;
	}

	// Curve parameters
	Curve public curve;

	// Ownable
	address public owner;

	string public name;
	string public symbol;
	uint8 public constant decimals = 18;

	address[] public derivatives;
	address[] public numeraires;
	address[] public reserves;

	// Curve operational state
	bool public frozen = false;
	bool public emergency = false;
	bool internal notEntered = true;
}
"
    },
    "contracts/Structs.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./interfaces/ICurveFactory.sol";
import "./interfaces/IOracle.sol";

struct OriginSwapData {
	address _origin;
	address _target;
	uint256 _originAmount;
	address _recipient;
	address _curveFactory;
}

struct TargetSwapData {
	address _origin;
	address _target;
	uint256 _targetAmount;
	address _recipient;
	address _curveFactory;
}

struct SwapInfo {
	int128 totalAmount;
	int128 totalFee;
	int128 amountToUser;
	int128 amountToTreasury;
	int128 protocolFeePercentage;
	address treasury;
	ICurveFactory curveFactory;
}

struct CurveInfo {
	string _name;
	string _symbol;
	address _baseCurrency;
	address _quoteCurrency;
	uint256 _baseWeight;
	uint256 _quoteWeight;
	IOracle _baseOracle;
	IOracle _quoteOracle;
	uint256 _alpha;
	uint256 _beta;
	uint256 _feeAtHalt;
	uint256 _epsilon;
	uint256 _lambda;
}

struct DepositData {
	uint256 deposits;
	uint256 minQuote;
	uint256 minBase;
	uint256 maxQuote;
	uint256 maxBase;
}

struct IntakeNumLpRatioInfo {
	uint256 baseWeight;
	uint256 minBase;
	uint256 maxBase;
	uint256 quoteWeight;
	uint256 minQuote;
	uint256 maxQuote;
	int128 amount;
}
