      "content": "// SPDX-License-Identifier: MIT LICENSE

pragma solidity ^0.8.0;

import {FxBaseChildTunnel} from "./FxBaseChildTunnel.sol";
import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC721/ERC721.sol";
import "./Scrap.sol";
import "./Whitelist.sol";

contract Scrapyard is Whitelist, FxBaseChildTunnel {
  struct Staking {
    address owner;
    address asset;
    uint256 tokenId;
    uint256 stakedTimestamp;
    uint256 lastClaimTimestamp;
  }

  struct StakingWithBalance {
    bytes32 key;
    address asset;
    uint256 tokenId;
    uint256 stakedTimestamp;
    uint256 lastClaimTimestamp;
    uint256 balance;
  }

  Scrap scrap;

  mapping(address=>uint256) public assetsPrizes;

  struct AllowedAssetWithNetwork {
    address asset;
    bool isEth;
  }
  AllowedAssetWithNetwork[] _allowedAssets;

  mapping(address=>bytes32[]) _userStakings;
  mapping(bytes32=>Staking) public stakings;
  mapping(address=>uint256) public unclaimedBalance;

  event Stake(address account, address assets, uint256 tokenId, uint256 timestamp);
  event Unstake(address account, address asset, uint256 tokenId, uint256 timestamp);
  event Claim(address account, uint256 amount, uint256 timestamp);

  constructor(address _scrap, address _fxChild) FxBaseChildTunnel(_fxChild) {
    scrap = Scrap(_scrap);
  }

  function allowedAssets() public view returns (AllowedAssetWithNetwork[] memory) {
    return _allowedAssets;
  }

  function addAllowedAsset(address _asset, bool _isEth) public onlyOwner {
    _allowedAssets.push(AllowedAssetWithNetwork(_asset, _isEth));
  }

  function userStakings(address account) public view returns (StakingWithBalance[] memory) {
    bytes32[] storage keys = _userStakings[account];
    StakingWithBalance[] memory list = new StakingWithBalance[](keys.length);
    
    for (uint16 i = 0; i < list.length; i++) {
      bytes32 key = keys[i];
      Staking storage s = stakings[key];
      uint256 balance = getAccumulatedScrapsForToken(key);
      list[i] = StakingWithBalance(key, s.asset, s.tokenId, s.stakedTimestamp, s.lastClaimTimestamp, balance);
    }

    return list;
  }

  function getStakingKey(address account, address asset, uint256 tokenId) public pure returns (bytes32) {
    return keccak256(abi.encode(account, asset, tokenId));
  }

  function isOwnerOf(address account, address asset, uint256 tokenId) public view returns (bool) {
    bytes32 key = getStakingKey(account, asset, tokenId);
    return stakings[key].owner == account;
  }

  function min(uint256 a, uint256 b) internal pure returns (uint256) {
    return a < b ? a : b;
  }

  function getAccumulatedScrapsForToken(bytes32 key) public view returns (uint256) {
    Staking storage staking = stakings[key];
    uint256 timestamp = staking.lastClaimTimestamp;
    uint256 prize = assetsPrizes[staking.asset];
    require(prize != 0, "Invalid asset");
    uint256 accumulated = min(block.timestamp - timestamp, 90 days) * prize / 7 days;
    return accumulated;
  }

  function getAccumulatedScrapsForToken(address account, address asset, uint256 tokenId) public view returns (uint256) {
    return getAccumulatedScrapsForToken(getStakingKey(account, asset, tokenId));
  }

  function claimScraps(bytes32[] calldata keys) public {
    uint256 totalBalance = unclaimedBalance[msg.sender];
    unclaimedBalance[msg.sender] = 0;

    for (uint16 i = 0; i < keys.length; i++) {
      bytes32 key = keys[i];
      Staking storage staking = stakings[key];
      require(staking.owner == msg.sender, "You don't own that token");
      totalBalance += getAccumulatedScrapsForToken(key);
      staking.lastClaimTimestamp = block.timestamp;
    }

    scrap.mint(msg.sender, totalBalance);
    emit Claim(msg.sender, totalBalance, block.timestamp);
  }

  function checkIfAssetIsAllowed(address asset) public view returns (bool) {
    for (uint16 i = 0; i < _allowedAssets.length; i++) {
      if (_allowedAssets[i].asset == asset) {
        return true;
      }
    }
    return false;
  }

  function registerStaking(address account, address asset, uint256 tokenId) internal {
    require(checkIfAssetIsAllowed(asset), "Asset not allowed");
    bytes32 key = getStakingKey(account, asset, tokenId);
    require(assetsPrizes[asset] != 0, "Invalid asset");
    require(stakings[key].owner == address(0), "Already registered");

    Staking memory staking = Staking(account, asset, tokenId, block.timestamp, block.timestamp);
    stakings[key] = staking;
    _userStakings[account].push(key);

    emit Stake(account, asset, tokenId, block.timestamp);
  }

  function removeFromStaking(address account, address asset, uint256 tokenId) internal {
    bytes32 key = getStakingKey(account, asset, tokenId);
    unclaimedBalance[account] += getAccumulatedScrapsForToken(key);
    delete stakings[key];

    bytes32[] storage keys = _userStakings[account];
    for (uint16 i = 0; i < keys.length; i++) {
      if (keys[i] == key) {
        keys[i] = keys[keys.length - 1];
        keys.pop();
        return;
      }
    }
    emit Unstake(account, asset, tokenId, block.timestamp);
  }

  function _processMessageFromRoot(uint256, address sender, bytes memory data) internal override {
    require(checkWhitelist(sender), "Unautorized");
    (address asset, address account, uint256 tokenId, bool staked) = abi.decode(data, (address, address, uint256, bool));
    if (staked) {
      registerStaking(account, asset, tokenId);
    } else {
      removeFromStaking(account, asset, tokenId);
    }
  }

  function receiveFromSibling(bytes calldata t) public isWhitelisted {
    _processMessageFromRoot(0, msg.sender, t);
  }

  function addAsset(address asset, uint256 prize) public onlyOwner {
    assetsPrizes[asset] = prize;
  }
}"
