      "content": "// SPDX-License-Identifier: MIT LICENSE

pragma solidity ^0.8.0;
import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "./Whitelist.sol";

contract Scrap is ERC20, Whitelist {
  constructor() ERC20("SCRAP", "SCRAP") {}

  function mint(address account, uint256 amount) public isWhitelisted {
    _mint(account, amount);
  }

  function burn(address account, uint256 amount) public {
    if (msg.sender != account) {
      uint256 allow = allowance(account, msg.sender);
      require(allow >= amount, "Unauthorized");
      _approve(account, msg.sender, allow - amount);
    }
    _burn(account, amount);
  }
}"
