    using SafeMath for uint256;

    address public fundManager;
    uint256 public fee;
    mapping(address => bool) public gameMasters;

    uint256 public stakedTotalSupply;
    mapping(address => uint256) public stakedBalances;
    mapping(address => uint256) public lockupTime;

    uint256 public constant feeMin = 0;
    uint256 public constant feeMax = 50;

    event RegisterGameMaster(address gameMaster, uint256 at);
    event ChangeFundManager(address fund, uint256 at);
    event ChangeFee(uint256 newFee, uint256 oldFee, uint256 at);
    event Stake(address indexed account, uint256 amount, uint256 lockup, uint256 at);
    event Withdraw(address indexed account, uint256 amount, uint256 at);
    event ChangeLockupTime(address indexed account, uint256 lockup, uint256 at);

    // constructor with ERC20
    constructor() ERC20("EF Defense Coin", "EFD") {
        fundManager = msg.sender;
        fee = 0;
        mintOnce(1800000000 * 10 ** 18, 2000000000 * 10 ** 18);
    }

    // implements ERC20Burnable
    function burn(uint256 amount) public override onlyOperator {
        super.burn(amount);
    }

    function burnFrom(address account, uint256 amount) public override onlyOperator {
        super.burnFrom(account, amount);
    }

    // implements ERC20Pausable
    function pause() public onlyOperator {
        _pause();
    }

    function unpause() public onlyOperator {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    // --------- EFD own functions --------------

    // EFD can mint only once
    function mintOnce(uint256 _amount, uint256 _total) public onlyOperator {
        require(totalSupply() == 0, "can mint only once");
        require(_amount <= _total && _total > 0, "out of range");
        
        _mint(msg.sender, _amount);
        _mint(address(this), _total.sub(_amount));
    }

    function governanceRecoverUnsupported(IERC20 _token, uint256 _amount, address _to) external onlyOperator {
        _token.transfer(_to, _amount);
    }

    function registerGameMaster(address _gameMaster) public {
        require(msg.sender == operator() || gameMasters[msg.sender] == true, "only gameMaster or operator can");
        require(_gameMaster != address(0), "_gameMaster address is zero");
        require(gameMasters[_gameMaster] == false, "gameMaster already registerd");
        gameMasters[_gameMaster] = true;
        emit RegisterGameMaster(_gameMaster, block.number);
    }

    // Fund method
    function changeFundManager(address _fundManager) public {
        require(msg.sender == fundManager || msg.sender == operator(), "only fundManager or operator can");
        require(_fundManager != address(0), "_fundManager address is zero");
        fundManager = _fundManager;
        emit ChangeFundManager(fundManager, block.number);
    }    

    function changeFee(uint256 _fee) public {
        require(msg.sender == fundManager, "only fundManager can");
        require(_fee >= feeMin && _fee <= feeMax, "check fee range");
        uint256 oldFee = fee;
        fee = _fee;
        emit ChangeFee(fee, oldFee, block.number);
    }

    // Game Master method
    modifier onlyGM() {
        require(gameMasters[msg.sender] == true, "only GM can");
        _;
    }

    function provideToken(address recipient, uint256 amount) public onlyGM {
        _transfer(address(this), recipient, amount);
    }

    function reclaimToken(address sender, uint256 amount) public onlyGM {
        uint256 feeAmount = amount.mul(fee).div(100);
        uint256 retrieveAmount = amount.sub(feeAmount);

        if (retrieveAmount > 0) _transfer(sender, address(this), retrieveAmount);
        if (feeAmount > 0) _transfer(sender, fundManager, feeAmount);
    }

    function stakedBalanceOf(address account) public view returns (uint256) {
        return stakedBalances[account];
    }

    function stake(address account, uint256 amount, uint256 lockup) public onlyGM {
        require(amount > 0, "cannot stake 0");
        require(lockup > block.timestamp, "lockup time cannot not be before now");

        stakedTotalSupply = stakedTotalSupply.add(amount);
        stakedBalances[account] = stakedBalances[account].add(amount);
        
        _transfer(account, address(this), amount);
        lockupTime[account] = lockup;

        emit Stake(account, amount, lockup, block.number);
    }

    function withdraw(address account, uint256 amount) public onlyGM {
        require(amount > 0, "cannot withdraw 0");
        require(lockupTime[account] < block.timestamp, "still lockup");

        uint256 _stakedBalance = stakedBalances[account];
        require(_stakedBalance  >= amount, "withdraw request greater than staked amount");

        stakedTotalSupply = stakedTotalSupply.sub(amount);
        stakedBalances[account] = _stakedBalance.sub(amount);
        _transfer(address(this), account, amount);

        emit Withdraw(account, amount, block.number);
    }

    function changeLockupTime(address account, uint256 lockup) public onlyGM {
        require(lockup > block.timestamp, "lockup time cannot not be before now");
        lockupTime[account] = lockup;

        emit ChangeLockupTime(account, lockup, block.timestamp);
    }

}
