pragma solidity >=0.8.4;

import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../token/ERC721L.sol";
import "../utils/Errors.sol";

interface IECDSALedger {
    function recover(bytes32 hash, bytes memory signature) external pure returns (address);
}

/// @notice Implementation of abstract contract ERC721L for the Ledger Quest platform
contract LedgerQuest is
    Ownable,
    ERC721L,
    EIP712
{
    using Strings for uint256;
    // Mapping from address to the allow minting property
    mapping(address => bool) public allowlist;

    // Mapping of collection IDs to number of tokens in each collection
    mapping(uint256 => uint256) public tokensInCollection;
    // Mapping of collection ID to boolean that determines if the collection is paused
    mapping(uint256 => bool) public pausedCollections;

    string private _baseTokenURI;
    string public baseExtension;
    string private _contractURI;

    IECDSALedger private ECDSAContract;
    // Address variable to hold Ledger wallet's address. This will be used in the signature validation
    address private ledgerWallet;

    // Restricted mode to allow minting only for addresses in the allow list
    bool private restictedModeEnabled = true;

    constructor(
        string memory _name,
        string memory _symbol,
        address _ledgerWallet,
        string memory EIP712Name,
        string memory EIP712Version,
        address _ECDSAContractAddress
    ) ERC721L(_name, _symbol) EIP712(EIP712Name, EIP712Version){
        // Set the rest of the variables
        ECDSAContract = IECDSALedger(_ECDSAContractAddress);
        ledgerWallet = _ledgerWallet;
        baseExtension = ".json";
    }

    /**
     * @dev mint function to be called by Ledger's wallet
     * @param signature signature created by the receiver
     * @param receiver address of the person that will receive the NFT
     * @param collectionId uint256 ID of the token to be added to the tokens list of the given address
     */
    function mint(
        bytes calldata signature,
        address receiver,
        uint256 categoryId,
        uint256 collectionId
    ) external {
        if (msg.sender != ledgerWallet) revert Errors.NotAllowed();
        if (restictedModeEnabled && !allowlist[receiver]) revert Errors.ReceiverNotAllowed();
        if (tokensInCollection[collectionId] + 1 == 1 * 10**6)
            revert Errors.MaximumAmountOfTokensReached();
        if (collectionId > 9999)
            revert Errors.InvalidCollectionID();
        if(pausedCollections[collectionId]) revert Errors.MintingNotAllowed();

        // check if receiver already has a token for the given collection ID
        uint256 receiverTokenCount = balanceOf(receiver);

        for (uint256 i; i < receiverTokenCount; ) {
            uint256 ownedTokenId = tokenOfOwnerByIndex(receiver, i);
            uint256 ownedTokenCategoryId = ownedTokenId / 10 ** 10;
            uint256 ownedTokenCollectionId = ownedTokenId / 10 ** 6 - (ownedTokenCategoryId * 10000);
            if(ownedTokenCollectionId == collectionId) revert Errors.MintingNotAllowedForGivenCollection();

            unchecked {
                ++i;
            }
        }

        // generate the digest
        bytes32 digest = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    keccak256("mint(address receiver,uint256 categoryId,uint256 collectionId)"),
                    receiver,
                    categoryId,
                    collectionId
                )
            )
        );
        // recover the signer of the signature
        address signer = ECDSAContract.recover(digest, signature);
        // ensure the signer is the receiver
        if (signer != receiver) revert Errors.InvalidSignature();
        if (signer == address(0)) revert Errors.ECDSAInvalidSignature();

        // overflow is impossible in that case
        unchecked {
            tokensInCollection[collectionId]++;
        }

        uint256 tokenId = categoryId *
            10**10 +
            collectionId *
            10**6 +
            tokensInCollection[collectionId];

        _mint(receiver, tokenId);
    }

    /**
     * @dev function to retrieve the collection of tokens owned by a certain user
     * @param _owner address of the person to display the tokens from
     */
    function walletOfOwner(address _owner)
        external
        view
        returns (uint256[] memory tokenIds)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        tokenIds = new uint256[](ownerTokenCount);

        for (uint256 i; i < ownerTokenCount; ) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);

            unchecked {
                ++i;
            }
        }

        return tokenIds;
    }

    /**
     * @dev function to populate the list of allowed addresses
     * @param addresses array of addresses
     * @param allow array of boolean values
     */
    function seedAllowlist(address[] calldata addresses, bool[] calldata allow)
        external
        onlyOwner
    {
        if (addresses.length != allow.length)
            revert Errors.AddressDoesNotMatchAllowLength();

        uint256 length = addresses.length;

        for (uint256 i; i < length; ) {
            allowlist[addresses[i]] = allow[i];

            unchecked {
                ++i;
            }
        }
    }

    /**
     * @dev function that allows the NFT owner to burn his token
     * @param tokenId the ID of the token
     */
    function burn(uint256 tokenId) external {
        if (ownerOf(tokenId) != msg.sender) revert Errors.NotAllowed();

        _burn(tokenId);
    }

    /**
     * @dev function to retireve the token URI based on token ID
     * Note: currently we are assuming all tokens from the same collection will
     * have the same image. If this is not true, then the logic needs to change
