    "contracts/GrabCoinClubItems.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/EIP712Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

contract GrabCoinClubItems is
    Initializable,
    ERC1155Upgradeable,
    OwnableUpgradeable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    ERC1155BurnableUpgradeable,
    ERC1155SupplyUpgradeable,
    UUPSUpgradeable,
    EIP712Upgradeable
{
    using AddressUpgradeable for address payable;
    using CountersUpgradeable for CountersUpgradeable.Counter;
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using StringsUpgradeable for uint256;

    address public admin;
    string public baseURI;

    address public verifier;
    mapping(address => CountersUpgradeable.Counter) private _nonces;
    // solhint-disable-next-line var-name-mixedcase
    bytes32 private constant _MINT_TYPEHASH =
        keccak256(
            "mint(address sender,uint256 id,uint256 amount,bytes data,address _token,uint256 _amount,uint256 nonce)"
        );
    // solhint-disable-next-line var-name-mixedcase
    bytes32 private constant _MINT_BATCH_TYPEHASH =
        keccak256(
            "mintBatch(address sender,uint256[] ids,uint256[] amounts,bytes data,address _token,uint256 _amount,uint256 nonce)"
        );

    mapping(address => uint256[]) public prices;

    event AdminUpdated(address admin);
    event VerifierUpdated(address verifier);

    modifier onlyAdmin() {
        require(admin == _msgSender(), "Caller is not the admin");
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize() public initializer {
        __ERC1155_init("");
        __Ownable_init();
        __Pausable_init();
        __ReentrancyGuard_init();
        __ERC1155Burnable_init();
        __ERC1155Supply_init();
        __UUPSUpgradeable_init();
        __EIP712_init("GrabCoinClubItems", "1");

        admin = _msgSender();
    }

    // solhint-disable-next-line no-empty-blocks
    receive() external payable {}

    function uri(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked(baseURI, tokenId.toString(), ".json"));
    }

    function setBaseURI(string memory newBaseURI) public onlyOwner {
        baseURI = newBaseURI;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function mintAirdrop(address account, uint256 id, uint256 amount, bytes memory data) public onlyAdmin {
        _mint(account, id, amount, data);
    }

    function mintAirdropBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public onlyAdmin {
        _mintBatch(to, ids, amounts, data);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) whenNotPaused {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    function nonces(address owner) public view returns (uint256) {
        return _nonces[owner].current();
    }

    /**
     * @dev "Consume a nonce": return the current value and increment.
     */
    function _useNonce(address owner) internal virtual returns (uint256 current) {
        CountersUpgradeable.Counter storage nonce = _nonces[owner];
        current = nonce.current();
        nonce.increment();
    }

    function mint(
        uint256 id,
        uint256 amount,
        bytes memory data,
        address _token,
        uint256 _amount,
        bytes memory signature
    ) external payable nonReentrant {
        bytes32 structHash = keccak256(
            abi.encode(
                _MINT_TYPEHASH,
                _msgSender(),
                id,
                amount,
                keccak256(abi.encodePacked(data)),
                _token,
                _amount,
                _useNonce(_msgSender())
            )
        );
        bytes32 hash = _hashTypedDataV4(structHash);
        require(ECDSAUpgradeable.recover(hash, signature) == verifier, "Invalid verifier");

        if (_token == address(0)) {
            require(_amount <= msg.value, "Insufficient fund");
        } else {
            IERC20Upgradeable(_token).safeTransferFrom(_msgSender(), address(this), _amount);
        }

        _mint(_msgSender(), id, amount, data);
    }

    function mintBatch(
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data,
        address _token,
        uint256 _amount,
        bytes memory signature
    ) external payable nonReentrant {
        bytes32 structHash = keccak256(
            abi.encode(
                _MINT_BATCH_TYPEHASH,
                _msgSender(),
                keccak256(abi.encodePacked(ids)),
                keccak256(abi.encodePacked(amounts)),
                keccak256(abi.encodePacked(data)),
                _token,
                _amount,
                _useNonce(_msgSender())
            )
        );
        bytes32 hash = _hashTypedDataV4(structHash);
        require(ECDSAUpgradeable.recover(hash, signature) == verifier, "Invalid verifier");

        if (_token == address(0)) {
            require(_amount <= msg.value, "Insufficient fund");
        } else {
            IERC20Upgradeable(_token).safeTransferFrom(_msgSender(), address(this), _amount);
        }

        _mintBatch(_msgSender(), ids, amounts, data);
    }

    function mint(
        uint256 id,
        uint256 amount,
        bytes memory data,
        address _token,
        uint256 _amount
    ) external payable nonReentrant {
        require(prices[_token][id] > 0, "Invalid token");
        require(_amount == prices[_token][id] * amount, "Invalid amount");

        if (_token == address(0)) {
            require(_amount <= msg.value, "Insufficient fund");
        } else {
            IERC20Upgradeable(_token).safeTransferFrom(_msgSender(), address(this), _amount);
        }

        _mint(_msgSender(), id, amount, data);
    }

    function mintBatch(
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data,
        address _token,
        uint256 _amount
    ) external payable nonReentrant {
        uint256 totalAmount;
        uint256 length = ids.length;
        for (uint256 i; i < length; ) {
            require(prices[_token][ids[i]] > 0, "Invalid token");
            totalAmount += prices[_token][ids[i]] * amounts[i];

            unchecked {
                ++i;
            }
        }
        require(totalAmount == _amount, "Invalid amount");

        if (_token == address(0)) {
            require(_amount <= msg.value, "Insufficient fund");
        } else {
            IERC20Upgradeable(_token).safeTransferFrom(_msgSender(), address(this), _amount);
        }

        _mintBatch(_msgSender(), ids, amounts, data);
    }

    function setAdmin(address newAdmin) external onlyOwner {
        admin = newAdmin;
        emit AdminUpdated(newAdmin);
    }

    function setVerifier(address newVerifier) external onlyOwner {
        verifier = newVerifier;
        emit VerifierUpdated(newVerifier);
    }

    function setPrices(address token, uint256[] memory _prices) external onlyOwner {
        prices[token] = _prices;
    }

    function withdraw(uint256 amount) external onlyOwner {
        payable(_msgSender()).sendValue(amount);
    }

    function withdrawERC20(address token, uint256 amount) external onlyOwner {
        IERC20Upgradeable(token).safeTransfer(_msgSender(), amount);
    }
}
"
    }
  },
