    "contracts/ERC1155CleanEnergyAssets.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

import "./Common.sol";

/**
 * @title CleanEnergyAssets.
 * @notice This contract handles the logic to keep track of energy generation
 * and generate Renewable Energy Certificates (RECs).
 */

contract CleanEnergyAssets is ERC1155, Pausable, Common {
    uint256 public tokenId;
    /// @dev is the energy limit to create a REC (Renewable Energy Certificate)
    uint256 public recDecimals = 18;
    uint256 public energyDecimals = 18;
    uint256 public energyLimit = 1 * 10 ** energyDecimals;

    mapping(uint256 => string) public tokenNameByTokenID;
    mapping(address => uint256) public tokenIdByProjectAddress;
    mapping(uint256 => bool) public isREC;
    mapping(address => uint256) public mintedEnergyByProject;
    mapping(address => uint256) public RECIdByAddress;
    mapping(address => uint256) public burnedAssets;

    event URIUpdated(string newURI);

    error ZeroOrNegativeAmount();
    error NotREC(uint256 tokenId);
    error TokenNotCreatedYet(address projectAddress);

    constructor(
        address _permissionGranterAddr
    ) ERC1155("") Common(_permissionGranterAddr) {
        _setApprovalForAll(address(this), msg.sender, true);
        _createGeneralEnergyAsset("Energy");
    }

    function _createGeneralEnergyAsset(string memory _tokenName) internal {
        tokenNameByTokenID[tokenId] = _tokenName;
        tokenId += 1;
    }

    /**
     * @notice This function creates the project energy asset and their respective REC.
     * @dev This function will be called when a new project is created.
     * @param _tokenName Name of the general clean energy asset.
     * @param _projectAddr New Project Address
     */
    function createProjectEnergyAsset(
        string memory _tokenName,
        address _projectAddr
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "createProjectEnergyAsset"
        )
    {
        tokenNameByTokenID[tokenId] = _tokenName;
        tokenIdByProjectAddress[_projectAddr] = tokenId;
        tokenId += 1;
        //Project REC's
        tokenNameByTokenID[tokenId] = string.concat("REC ", _tokenName);
        isREC[tokenId] = true;
        RECIdByAddress[_projectAddr] = tokenId;
        tokenId += 1;
    }

    /**
     * @notice This function creates energy assets of a project and
     * if the accumulated energy exceeds the limit, new RECs are created.
     * @dev This function will be called from a new energy Report.
     * @param _projectAddr New Project Address
     * @param _amount Name of the general clean energy asset.
     */
    function mint(
        address _projectAddr,
        uint256 _amount
    )
        public
        whenNotPaused
        hasRoleInPermissionGranter(msg.sender, address(this), "mint")
    {
        if (_amount <= 0) revert ZeroOrNegativeAmount();

        uint256 id = tokenIdByProjectAddress[_projectAddr];

        if (id == 0) revert TokenNotCreatedYet(_projectAddr);

        _mint(address(this), id, _amount, ""); //Mint project energy token
        _mint(address(this), 0, _amount, ""); //Mint general energy token

        mintedEnergyByProject[_projectAddr] += _amount;

        if (mintedEnergyByProject[_projectAddr] >= energyLimit) {
            uint256 recId = RECIdByAddress[_projectAddr];

            uint256 amountToMint = mintedEnergyByProject[_projectAddr] /
                energyLimit;

            _mint(address(this), recId, amountToMint * 10 ** recDecimals, "");
            mintedEnergyByProject[_projectAddr] -= energyLimit * amountToMint;
        }
    }

    function burn(
        address _projectAddr,
        uint256 _amount
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(msg.sender, address(this), "burn")
    {
        if (_amount <= 0) revert ZeroOrNegativeAmount();

        uint256 id = tokenIdByProjectAddress[_projectAddr];

        _burn(address(this), id, _amount); //Burn project energy token
        _burn(address(this), 0, _amount); //Burn general energy token
    }

    function burnREC(uint256 _tokenId, uint256 amount) public whenNotPaused {
        if (!isREC[_tokenId]) revert NotREC(_tokenId);

        _burn(msg.sender, _tokenId, amount);
    }

    function withdrawRECs(
        uint256 _tokenId,
        address _receiver,
        uint256 _amount
    ) external whenNotPaused onlyOwner {
        if (!isREC[_tokenId]) revert NotREC(_tokenId);

        safeTransferFrom(address(this), _receiver, _tokenId, _amount, "");
    }

    function setURI(string memory _newUri) public whenNotPaused onlyOwner {
        _setURI(_newUri);
        emit URIUpdated(_newUri);
    }

    function setEnergyLimit(
        uint256 _energyLimit
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(msg.sender, address(this), "setEnergyLimit")
    {
        if (_energyLimit <= 0) revert ZeroOrNegativeAmount();

        energyLimit = _energyLimit;
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes memory
    ) external virtual returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] memory,
        uint256[] memory,
        bytes memory
    ) external virtual returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function _afterTokenTransfer(
        address /* operator */,
        address from,
        address to,
        uint256[] memory ids, //transferred token ids array
        uint256[] memory amounts,
        bytes memory /* data */
    ) internal virtual override {
        uint256 id = ids[0]; //the burn function only sends an unique token id to this function

        if (to == address(0)) {
            if (id != 0) {
                uint256 amount = amounts[0];
                burnedAssets[from] += amount;
            }
        }
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal override whenNotPaused {}
}
"
    },
    "contracts/ERC20Project.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import {ProjectState, Project} from "./Types.sol";

import "./UnergyData.sol";
import {UnergyEvent} from "./UnergyEvent.sol";
import {ProjectsManager} from "./ProjectsManager.sol";
import "./Common.sol";

/**
 * @title ERC20Project.
 * @notice This contract handles the logic for raising the financing needed for a new clean energy project.
 */

contract ERC20Project is ERC20, Common, ReentrancyGuard {
    UnergyData private unergyData;

    event UnergyEventAddressUpdated(address unergyEventAddr);
    event ProjectsManagerAddressUpdated(address projectsManagerAddr);

    error ProjectIsNotInFundingOrInstalledState();
    error TransferToHimself(address destinationAddress);

    constructor(
        string memory name_,
        string memory symbol_,
        address unergyDataAddress_,
        address permissionGranterAddres_
    ) ERC20(name_, symbol_) Ownable() Common(permissionGranterAddres_) {
        unergyData = UnergyData(unergyDataAddress_);
    }

    modifier inFundingOrInstalled() {
        Project memory project = ProjectsManager(
            unergyData.projectsManagerAddress()
        ).getProject(address(this));

        if (
            project.state != ProjectState.FUNDING &&
            project.state != ProjectState.INSTALLED
        ) revert ProjectIsNotInFundingOrInstalledState();
        _;
    }

    function mint(
        address account,
        uint256 amount
    ) external hasRoleInPermissionGranter(msg.sender, address(this), "mint") {
        _mint(account, amount);
    }

    function burn(
        address account,
        uint256 amount
    ) external hasRoleInPermissionGranter(msg.sender, address(this), "burn") {
        _burn(account, amount);
    }

    function transfer(
        address to,
        uint256 amount
    ) public virtual override inFundingOrInstalled returns (bool) {
        address sender = _msgSender();
        if (sender == to) {
            revert TransferToHimself(to);
        }
        _transfer(sender, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        if (from == to) {
            revert TransferToHimself(to);
        }
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        UnergyEvent(unergyData.unergyEventAddress()).beforeTransferReceipt(
            address(this),
            from,
            to,
            amount
        );
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        UnergyEvent(unergyData.unergyEventAddress()).afterTransferReceipt(
            address(this),
            from,
            to,
            amount
        );
    }

    /**
     * @notice This function is only allowed for addresses who has approveSwap permission.
     * @dev This approve function is called when a protocol contract needs to
     * give pWatts allowance to an another address (_spender).
     * @param holder is the public address who has pWatts.
     * @param spender is the public address who spend pWatts.
     * @param amount is the amount allowed to spend.
     */
    function approveSwap(
        address holder,
        address spender,
        uint256 amount
    )
        public
        nonReentrant
        hasRoleInPermissionGranter(msg.sender, address(this), "approveSwap")
        returns (bool)
    {
        _approve(holder, spender, amount);
        return true;
    }

    function approve(
        address spender,
        uint256 amount
    )
        public
        virtual
        override
        hasRoleInPermissionGranter(msg.sender, address(this), "approve")
        returns (bool)
    {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function increaseAllowance(
        address holder,
        uint256 addedValue
    ) public override onlyOwner returns (bool) {
        address spender = msg.sender;
        unchecked {
            _approve(holder, spender, allowance(holder, spender) + addedValue);
        }
        return true;
    }

    function decreaseAllowance(
        address holder,
        uint256 subtractedValue
    ) public override onlyOwner returns (bool) {
        address spender = _msgSender();
        uint256 currentAllowance = allowance(holder, spender);
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(holder, spender, currentAllowance - subtractedValue);
        }
        return true;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function transferOwnership(
        address newOwner
    ) public override onlyOwner notZeroAddress(newOwner) {
        _transferOwnership(newOwner);
    }
}
"
    },
    "contracts/ERC20UWatt.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Votes.sol";

import {UnergyEventAbs} from "./Abstracts.sol";
import "./UnergyData.sol";
import "./Common.sol";

/**
 * @title ERC20UWatt.
 * @notice This contract handles the logic for our stable currency collateralized in clean energy.
 */

contract ERC20UWatt is ERC20, ERC20Votes, Common {
    UnergyData private unergyData;
    address public potentialOwner;

    event UnergyEventAddressUpdated(address unergyEventAddr);
    event OwnerNominated(address pendingOwner);

    error TransferToHimself(address origin, address destinationAddress);

    constructor(
        address unergyDataAddress_,
        address permissionGranterAddres_
    )
        ERC20("Unergy Watt", "uWatt")
        ERC20Permit("Unergy Watt")
        Ownable()
        Common(permissionGranterAddres_)
    {
        unergyData = UnergyData(unergyDataAddress_);
    }

    function mint(
        address account,
        uint256 amount
    ) external hasRoleInPermissionGranter(_msgSender(), address(this), "mint") {
        _mint(account, amount);
    }

    function transfer(
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address sender = _msgSender();
        if (sender == to) {
            revert TransferToHimself(sender, to);
        }
        _transfer(sender, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        if (from == to) {
            revert TransferToHimself(from, to);
        }
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function burn(uint256 amount) external {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) external {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    function decimals() public pure override(ERC20) returns (uint8) {
        return 18;
    }



    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _amount
    ) internal virtual override {
        super._beforeTokenTransfer(_from, _to, _amount);

        UnergyEventAbs(unergyData.unergyEventAddress()).beforeTransferReceipt(
            address(this),
            _from,
            _to,
            _amount
        );
    }

    function transferOwnership(
        address _pendingOwner
    ) public override onlyOwner notZeroAddress(_pendingOwner) {
        potentialOwner = _pendingOwner;
        emit OwnerNominated(_pendingOwner);
    }

    function acceptOwnership() external {
        require(
            msg.sender == potentialOwner,
            "You must be nominated as potential owner before you can accept ownership"
        );
        _transferOwnership(msg.sender);
        potentialOwner = address(0);
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._mint(to, amount);
    }

    function _burn(
        address account,
        uint256 amount
    ) internal override(ERC20, ERC20Votes) {
        super._burn(account, amount);
    }
}
"
    },
    "contracts/interfaces/CommonAbstract.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {PermissionGranter} from "../PermissionGranter.sol";

/**
 * @title CommonAbstract
 * @author Unergy
 * @notice As we are mantaining two versions of the same contract, one upgradeable and one not,
 *         and difers only in the addition of a storage gap and the `initialize` function, we
 *         have created this abstract contract to ensure that both contracts have the same interface.
 */

abstract contract CommonAbstract {
    event SetPermissionGranter(address permissionGranterAddr);

    error ZeroAddressNotAllowed();

    modifier hasRoleInPermissionGranter(
        address _caller,
        address _contract,
        string memory _functionName
    ) {
        _hasRoleInPermissionGranter(_caller, _contract, _functionName);
        _;
    }

    modifier hasMeterPermissionOverProjectContract(
        address _caller,
        address _logicContract,
        address _projectContract
    ) {
        _hasMeterPermissionOverProjectContract(
            _caller,
            _logicContract,
            _projectContract
        );
        _;
    }

    function setPermissionGranterAddr(
        address _permissionGranterAddr
    ) public virtual;

    function getPermissionGranterAddress()
        public
        view
        virtual
        returns (address);

    function _checkPermission(
        bool _hasPermission,
        string memory _functionName,
        address _caller
    ) internal pure virtual;

    function _notZeroAddress(address _address) internal pure virtual;

    function _hasRoleInPermissionGranter(
        address _caller,
        address _contract,
        string memory _functionName
    ) internal virtual;

    function _hasMeterPermissionOverProjectContract(
        address _caller,
        address _logicContract,
        address _projectContract
    ) internal view virtual;
}
"
