    "contracts/PermissionGranter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

import {PermissionType, Permission} from "./Types.sol";

/**
 * @title PermissionGranter.
 * @notice This contract handles the permissions of the contracts that are used in the Unergy Protocol.
 */

contract PermissionGranter is AccessControl {
    address[] public owners;

    uint256 public required;

    mapping(address => bool) public isOwner;
    mapping(address => mapping(bytes4 => bool)) public approved;
    mapping(address => mapping(address => mapping(bytes32 => Permission)))
        private permissions;
    mapping(bytes32 => bool) private meterPermissions;

    event PermissionGranted(
        address indexed _address,
        address indexed _contract,
        string _fname
    );
    event PermissionRevoked(
        address indexed _address,
        address indexed _contract,
        string _fname
    );
    event FunctionApprovedBySigner(
        address indexed signer,
        string indexed fName
    );
    event RevokedSignature(address indexed owner, bytes4 indexed fSelector);
    event SignatureReplaced(
        address indexed signatureReplaced,
        address indexed newSignature
    );

    error NotAuthorizedToCallFunction(string functionName, address caller);

    constructor(address[] memory _owners, uint256 _required) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        require(_owners.length > 0, "Owners required");
        require(
            _required > 0 && _required <= _owners.length,
            "Invalid required number of owners"
        );

        for (uint256 i; i < _owners.length; i++) {
            address owner = _owners[i];
            require(owner != address(0), "Owner cannot be the address zero");
            require(!isOwner[owner], "Owner is not unique");

            isOwner[owner] = true;
            _grantRole(DEFAULT_ADMIN_ROLE, owner);
            owners.push(owner);
        }

        required = _required;
    }

    function _getApprovalCount(
        bytes4 _fSelector
    ) private view returns (uint256 count) {
        for (uint256 i; i < owners.length; i++) {
            if (approved[owners[i]][_fSelector]) {
                count += 1;
            }
        }
    }

    /// @notice This function is used to allow a `caller` to execute a function
    /// @param _address The address of the caller
    /// @param _contract The contract over the caller is allowed to execute the function
    /// @param _fname The name of the function the caller is allowed to execute
    function setPermission(
        address _address,
        address _contract,
        string memory _fname,
        PermissionType _permissionType,
        uint256 _permissionValue
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][this.setPermission.selector] = true;

        if (_getApprovalCount(this.setPermission.selector) >= required) {
            bytes32 role = keccak256(abi.encodePacked(_contract, _fname));

            _grantRole(role, _address);
            permissions[_address][_contract][role] = Permission(
                _permissionType,
                _permissionValue,
                0
            );

            _restoreSignatures(this.setPermission.selector);

            emit PermissionGranted(_address, _contract, _fname);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "setPermission");
        }
    }

    /**
     * @notice This function is used to allow a meter account to execute the energyReport function of the logic contract
     */
    function setMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        /**
         * A meter account is allowed to call only the energyReport function.
         * For this reason, this arguments are hardcoded.
         */
        setPermission(
            _meterAddress,
            _logicContract,
            "energyReport",
            PermissionType.PERMANENT,
            0
        );

        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        meterPermissions[permissionHash] = true;
    }

    function revokeMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        meterPermissions[permissionHash] = false;
    }

    /**
     * @dev This function does not relies on the `getAndUpdatePermission` because the permission is always permanent
     */
    function getMeterPermission(
        address _meterAddress,
        address _logicContract,
        address _projectContract
    ) external view returns (bool) {
        bytes32 permissionHash = keccak256(
            abi.encodePacked(_meterAddress, _logicContract, _projectContract)
        );

        return meterPermissions[permissionHash];
    }

    function setProjectsManagerPermissions(
        address _address,
        address _contract,
        string memory _fname
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(keccak256(abi.encodePacked(_contract, _fname)), _address);

        emit PermissionGranted(_address, _contract, _fname);
    }

    /// @notice This function is used to revoke the permission of a `caller` to execute a function
    /// @param _address { see: this.setPermission }
    /// @param _contract { see: this.setPermission }
    /// @param _fname { see: this.setPermission }
    function revokePermission(
        address _address,
        address _contract,
        string memory _fname
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][this.revokePermission.selector] = true;
        if (_getApprovalCount(this.revokePermission.selector) >= required) {
            _revokeRole(
                keccak256(abi.encodePacked(_contract, _fname)),
                _address
            );
            _restoreSignatures(this.revokePermission.selector);

            emit PermissionRevoked(_address, _contract, _fname);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "revokePermission");
        }
    }

    function revokeSignature(
        bytes4 _fSelector
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        approved[msg.sender][_fSelector] = false;
        emit RevokedSignature(msg.sender, _fSelector);
    }

    function replaceSigner(
        address _signerToRevoke,
        address _newSigner
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(
            _signerToRevoke != address(0),
            "Owner cannot be the address zero"
        );
        require(_newSigner != address(0), "Owner cannot be the address zero");
        require(!isOwner[_newSigner], "Owner is not unique");

        approved[msg.sender][this.replaceSigner.selector] = true;

        if (_getApprovalCount(this.replaceSigner.selector) >= required) {
            for (uint256 i; i < owners.length; i++) {
                address owner = owners[i];

                if (_signerToRevoke == owner) {
                    owners[i] = _newSigner;
                }
            }

            _revokeRole(DEFAULT_ADMIN_ROLE, _signerToRevoke);
            isOwner[_signerToRevoke] = false;

            _grantRole(DEFAULT_ADMIN_ROLE, _newSigner);
            isOwner[_newSigner] = true;

            _restoreSignatures(this.replaceSigner.selector);

            emit SignatureReplaced(_signerToRevoke, _newSigner);
        } else {
            emit FunctionApprovedBySigner(msg.sender, "replaceSigner");
        }
    }

    function _restoreSignatures(bytes4 _fSelector) internal {
        for (uint256 i; i < owners.length; i++) {
            address owner = owners[i];
            approved[owner][_fSelector] = false;
        }
    }

    /// @notice The functions returns a boolean indicating if a `caller` is allowed to execute a function
    /// @param _address { see: this.setPermission }
    /// @param _contract { see: this.setPermission }
    /// @param _fname { see: this.setPermission }
    function getPermission(
        address _address,
        address _contract,
        string memory _fname
    ) public view returns (bool) {
        return
            hasRole(keccak256(abi.encodePacked(_contract, _fname)), _address);
    }

    function getAndUpdatePermission(
        address _address,
        address _contract,
        string memory _fname
    ) external returns (bool) {
        bytes32 role = keccak256(abi.encodePacked(_contract, _fname));
        bool _hasRole = hasRole(role, _address);

        if (!_hasRole) return false;

        Permission storage permission = permissions[_address][_contract][role];

        if (permission._type == PermissionType.PERMANENT) return true;

        if (
            permission._type == PermissionType.TIMELOCK &&
            block.timestamp < permission._value
        ) return true;

        if (
            permission._type == PermissionType.ONETIME &&
            permission._timesUsed < 1
        ) {
            permission._timesUsed++;
            return true;
        }

        if (
            permission._type == PermissionType.EXECUTIONS &&
            permission._timesUsed < permission._value
        ) {
            permission._timesUsed++;
            return true;
        }

        return false;
    }
}
"
    },
    "contracts/ProjectsManager.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

import {Project, ProjectInput, ProjectState, Milestone, Signatures, Signature} from "./Types.sol";

import "./CommonUpgradeable.sol";
import "./UnergyData.sol";
import "./ERC1155CleanEnergyAssets.sol";
import "./ERC20Project.sol";
import "./UnergyEvent.sol";

/**
 * @title ProjectsManager (v2) - Last upgrade at 2023-09-24
 * @notice This contract manages the project-related operations and stores the data related to it.
 */

contract ProjectsManager is
    Initializable,
    PausableUpgradeable,
    CommonUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using CountersUpgradeable for CountersUpgradeable.Counter;

    UnergyData private unergyData;
    address public potentialOwner;

    address[] private projectsAddresses;

    CountersUpgradeable.Counter private _projectIdsCounter;

    mapping(address => Project) private projects;
    mapping(address => bool) private projectExists;
    mapping(address => address[]) private projectHolders;
    mapping(address => Milestone[]) private milestones;
    mapping(address => bool[2]) private signatures;

    event SignatureSet(address indexed projectAddress, Signature signature);
    /**
     * @custom:upgrade-note This function was added in the upgrade v2 at 2023-09-24
     */
    event MilestoneUpdated(address indexed projectAddress, string name);
    event MilestonesUpdated(address indexed projectAddress);
    event MilestoneAdded(address indexed projectAddress, string name);
    event ProjectCreated(address indexed projectAddress);
    event ProjectConfigured(address indexed projectAddress);
    event ProjectUpdated(address indexed projectAddress);
    event ProjectHolderAdded(
        address indexed projectAddress,
        address indexed holder
    );
    event OwnerNominated(address pendingOwner);

    error ProjectDoesNotExist(address projectAddress);
    error MilestoneAlreadyReached(string name);
    error MaintenancePercentageExceeded(uint256 givenMaintenancePercentage);
    error OperatorFeeExceeded(uint256 givenOperatorFee);
    error ApproveSwapFailed();
    /**
     * @custom:upgrade-note This function was added in the upgrade v2 at 2023-09-24
     */
    error InvalidInputLength(uint256 expectedLength, uint256 givenLength);

    modifier ifProjectExists(address _projectAddress) {
        if (!projectExists[_projectAddress])
            revert ProjectDoesNotExist(_projectAddress);
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address unergyDataAddr,
        address _permissionGranterAddr
    ) external initializer {
        __Pausable_init();
        __Common_init(_permissionGranterAddr);
        __UUPSUpgradeable_init();
        unergyData = UnergyData(unergyDataAddr);
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyOwner {}

    /**
     * @notice Allows to admin user  to create a  new project
     * @dev This function deploys an ERC20 Project and configures all necessary permissions.
     * @param _projectName the ERC20 name
     * @param _projectSymbol the ERC20 symbol
     */
    function createProject(
        ProjectInput memory _projectInput,
        string memory _projectName,
        string memory _projectSymbol
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(msg.sender, address(this), "createProject")
    {
        uint256 projectId = _projectIdsCounter.current();
        _projectIdsCounter.increment();

        ERC20Project erc20Project = new ERC20Project(
            _projectName,
            _projectSymbol,
            address(unergyData),
            address(permissionGranter)
        );

        address erc20ProjectAddress = address(erc20Project);

        Project memory project = _createProject(
            _projectInput,
            erc20ProjectAddress,
            projectId
        );
        projects[project.addr] = project;
        projectsAddresses.push(project.addr);
        projectExists[project.addr] = true;

        address cleanEnergyAssetsAddr = unergyData.cleanEnergyAssetsAddress();

        CleanEnergyAssets(cleanEnergyAssetsAddr).createProjectEnergyAsset(
            _projectName,
            erc20ProjectAddress
        );

        emit ProjectCreated(project.addr);
    }

    function _createProject(
        ProjectInput memory _projectInput,
        address _erc20ProjectAddress,
        uint256 _projectId
    ) private view whenNotPaused returns (Project memory) {
        if (_projectInput.maintenancePercentage >= 100e18) {
            revert MaintenancePercentageExceeded(
                _projectInput.maintenancePercentage
            );
        }
        if (_projectInput.operatorFee >= _projectInput.totalPWatts) {
            revert OperatorFeeExceeded(_projectInput.operatorFee);
        }

        Project memory project = Project({
            id: _projectId,
            maintenancePercentage: _projectInput.maintenancePercentage,
            initialProjectValue: _projectInput.initialProjectValue,
            currentProjectValue: _projectInput.currentProjectValue,
            swapFactor: _projectInput.swapFactor,
            state: ProjectState.FUNDING,
            addr: _erc20ProjectAddress,
            adminAddr: _projectInput.adminAddr,
            installerAddr: _projectInput.installerAddr,
            usdDepreciated: 0,
            pWattsSupply: _projectInput.totalPWatts,
            signatures: Signatures({
                isSignedByInstaller: false,
                isSignedByOriginator: false
            }),
            operator: _projectInput.operator,
            operatorFee: _projectInput.operatorFee,
            stableAddr: _projectInput.stableAddr
        });

        return project;
    }

    function configureProject(
        ProjectInput memory _projectInput,
        address erc20ProjectAddress
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(msg.sender, address(this), "createProject")
    {
        ERC20Project erc20Project = ERC20Project(erc20ProjectAddress);

        erc20Project.mint(_projectInput.adminAddr, _projectInput.totalPWatts);
        bool success = erc20Project.approveSwap(
            _projectInput.adminAddr,
            address(this),
            _projectInput.operatorFee
        );

        if (!success) revert ApproveSwapFailed();

        IERC20Upgradeable(address(erc20Project)).safeTransferFrom(
            _projectInput.adminAddr,
            _projectInput.operator,
            _projectInput.operatorFee
        );

        success = erc20Project.approveSwap(
            _projectInput.operator,
            unergyData.unergyLogicReserveAddress(),
            _projectInput.totalPWatts
        );

        if (!success) revert ApproveSwapFailed();

        erc20Project.transferOwnership(msg.sender);

        emit ProjectConfigured(erc20ProjectAddress);
    }

    function updateProject(
        address _projectAddress,
        Project memory _project
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        Project storage project = projects[_projectAddress];

        project.maintenancePercentage = _project.maintenancePercentage;
        project.initialProjectValue = _project.initialProjectValue;
        project.currentProjectValue = _project.currentProjectValue;
        project.swapFactor = _project.swapFactor;
        project.pWattsSupply = _project.pWattsSupply;
        project.usdDepreciated = _project.usdDepreciated;
        project.operator = _project.operator;
        project.operatorFee = _project.operatorFee;
        project.stableAddr = _project.stableAddr;
        project.installerAddr = _project.installerAddr;
        project.adminAddr = _project.adminAddr;
        project.state = _project.state;
        project.signatures = _project.signatures;

        emit ProjectUpdated(project.addr);
    }

    function setProjectState(
        address _projectAddress,
        ProjectState _projectState
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        Project storage project = projects[_projectAddress];
        project.state = _projectState;

        emit ProjectUpdated(project.addr);
    }

    function addProjectHolder(
        address _projectAddress,
        address _holderAddress
    )
        external
        whenNotPaused
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        getProject(_projectAddress);
        if (_holderExists(_projectAddress, _holderAddress)) return;

        projectHolders[_projectAddress].push(_holderAddress);

        emit ProjectHolderAdded(_projectAddress, _holderAddress);
    }

    function _holderExists(
        address _projectAddress,
        address _holderAddress
    ) private view returns (bool) {
        address[] memory holders = projectHolders[_projectAddress];

        for (uint256 i = 0; i < holders.length; i++) {
            if (holders[i] == _holderAddress) {
                return true;
            }
        }

        return false;
    }

    function addProjectMilestone(
        address _projectAddress,
        string memory _name,
        uint256 weight
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        Project memory project = getProject(_projectAddress);
        Milestone[] storage projectMilestones = milestones[project.addr];

        projectMilestones.push(
            Milestone({
                name: _name,
                weight: weight,
                isReached: false,
                wasSignedByInstaller: false,
                wasSignedByOriginator: false
            })
        );

        _checkMilestoneWeights(projectMilestones);

        emit MilestoneAdded(project.addr, _name);
    }

    /**
     * @custom:upgrade-note This function was added in the upgrade v2 at 2023-09-24
     */
    function updateProjectMilestones(
        address _projectAddress,
        Milestone[] memory _milestones
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        _checkMilestoneWeights(_milestones);
        Milestone[] storage currentMilestones = milestones[_projectAddress];

        if (currentMilestones.length != _milestones.length) {
            revert InvalidInputLength(
                currentMilestones.length,
                _milestones.length
            );
        }

        for (uint256 i; i < currentMilestones.length; i++) {
            currentMilestones[i] = _milestones[i];
        }

        emit MilestonesUpdated(_projectAddress);
    }

    function updateMilestoneAtIndex(
        address _projectAddress,
        uint256 _index,
        Milestone memory _milestone
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(
            msg.sender,
            address(this),
            "updateProjectRelatedProperties"
        )
    {
        Project memory project = getProject(_projectAddress);
        Milestone[] storage projectMilestones = milestones[project.addr];

        projectMilestones[_index] = _milestone;

        _checkMilestoneWeights(projectMilestones);

        emit MilestoneUpdated(project.addr, _milestone.name);
    }

    function _checkMilestoneWeights(
        Milestone[] memory _milestones
    ) internal pure {
        uint256 acummulatedWeight;
        for (uint256 i; i < _milestones.length - 1; i++) {
            acummulatedWeight += _milestones[i].weight;
        }
        require(
            acummulatedWeight <= 100,
            "milestone weights are greater than 100, check the weights again."
        );
    }

    /**
     * @notice Sets the project signature to the given value
     * @param _projectAddress the address of the project whose signature will be modified
     * @param _signature installer or originator signature {see: Signature on Types.sol}
     */
    function setSignature(
        address _projectAddress,
        Signature _signature
    )
        external
        whenNotPaused
        ifProjectExists(_projectAddress)
        hasRoleInPermissionGranter(msg.sender, address(this), "setSignature")
    {
        Project storage project = projects[_projectAddress];

        if (_signature == Signature.INSTALLER) {
            project.signatures.isSignedByInstaller = true;
        } else {
            project.signatures.isSignedByOriginator = true;
        }

        emit SignatureSet(_projectAddress, _signature);
    }

    /**
     * @notice This function finds a project and revert if doesn't exists
     * @param _projectAddress The address of the project to look for
     * @return Project
     */
    function getProject(
        address _projectAddress
    ) public view returns (Project memory) {
        if (!projectExists[_projectAddress])
            revert ProjectDoesNotExist(_projectAddress);

        return projects[_projectAddress];
    }

    function getProjectHolders(
        address _projectAddress
    ) external view returns (address[] memory) {
        getProject(_projectAddress);

        return projectHolders[_projectAddress];
    }

    function getTotalPWatts(
        address _projectAddress
    ) external view ifProjectExists(_projectAddress) returns (uint256) {
        Project memory project = getProject(_projectAddress);

        return project.pWattsSupply;
    }

    function getProjectState(
        address _projectAddress
    ) external view ifProjectExists(_projectAddress) returns (ProjectState) {
        Project memory project = getProject(_projectAddress);

        return project.state;
    }

    function getProjectMilestones(
        address _projectAddress
    )
        external
        view
        ifProjectExists(_projectAddress)
        returns (Milestone[] memory)
    {
        Project memory project = getProject(_projectAddress);

        return milestones[project.addr];
    }

    function getSignatures(
        address _projectAddress
    ) external view ifProjectExists(_projectAddress) returns (bool[2] memory) {
        Project memory project = getProject(_projectAddress);

        return [
            project.signatures.isSignedByInstaller,
            project.signatures.isSignedByOriginator
        ];
    }

    function transferOwnership(
        address _pendingOwner
    ) public override onlyOwner whenNotPaused notZeroAddress(_pendingOwner) {
        potentialOwner = _pendingOwner;
        emit OwnerNominated(_pendingOwner);
    }

    function acceptOwnership() external {
        require(
            msg.sender == potentialOwner,
            "You must be nominated as potential owner before you can accept ownership"
        );
        _transferOwnership(msg.sender);
        potentialOwner = address(0);
    }

    function pause() external onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external onlyOwner whenPaused {
        _unpause();
    }
}
"
