    "contracts/mainWallet/MainWalletBeacon.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract MainWalletBeacon is Ownable {
    UpgradeableBeacon immutable beacon;

    address public blueprint;

    constructor(address _initBlueprint) {
        beacon = new UpgradeableBeacon(_initBlueprint);
        blueprint = _initBlueprint;
        transferOwnership(tx.origin);
    }

    function update(address _newBlueprint) public onlyOwner {
        beacon.upgradeTo(_newBlueprint);
        blueprint = _newBlueprint;
    }

    function implementation() public view returns (address){
        return beacon.implementation();
    }
}
"
    },
    "contracts/mainWallet/MainWalletUpg.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import ".././RevenueWallet.sol";
import ".././Vesting.sol";

interface IDFOFactory {

    function deployToken(string memory, string memory, bool) external returns(address);
    function createEquityFundraising(Vesting.TokenData calldata, Vesting.VestingPlan [] memory, uint, Vesting.FundraisingData[] calldata, uint, address [] memory, uint) external;
    function createLiabilityFundraising(address, address, bytes memory, address) external returns (address);
    function createRevenueWallet(address, string calldata, string calldata) external returns(address);
}

interface IERC20Token {
    function mint(uint) external;
}

contract MainWalletUpg is Ownable, Initializable{

    string public companyName;
    bool public isPrivateCompany;

    event WithdrawalProcessed(address mainWalletAddress, address tokenAddress, address recipient, uint amount);

    address public developerAddress;

    function initialize(string memory _companyName, bool _isPrivate, address _developerAddress) external initializer{
        _transferOwnership(tx.origin);
        companyName = _companyName;
        developerAddress = _developerAddress;
        isPrivateCompany = _isPrivate;
    }

    function createRevenueWallet(string memory _label, string memory _description) public onlyOwner {
        _createRevenueWallet(_label, _description);
    }

    function _createRevenueWallet(string memory _label, string memory _description) private {
        IDFOFactory(developerAddress).createRevenueWallet(address(this), _label, _description);
    }

    function createEquityFundraising(Vesting.VestingPlan [] memory _tokenVestingData,
        uint _tokenVestingsCount,
        Vesting.TokenData calldata _tokenData,
        Vesting.FundraisingData [] memory _fundraisingData,
        uint _numOfFundraisings,
        address [] memory _attachedERC20Address,
        uint _attachedERC20AddressLength) public onlyOwner{
        IDFOFactory(developerAddress).createEquityFundraising(_tokenData, _tokenVestingData, _tokenVestingsCount, _fundraisingData, _numOfFundraisings, _attachedERC20Address, _attachedERC20AddressLength);
    }

    function createLiabilityFundraising(string memory _tokenName, string memory _tokenTicker, bytes memory _fundraisingData, address _attachedERC20Address) public onlyOwner returns(address){
        address liabilityTokenAddress = _deployToken(_tokenName, _tokenTicker, false);
        return IDFOFactory(developerAddress).createLiabilityFundraising(liabilityTokenAddress, address(this), _fundraisingData, _attachedERC20Address);
    }

    function _deployToken(string memory _name, string memory _ticker, bool _isEquity) internal returns(address) {
        return IDFOFactory(developerAddress).deployToken(_name, _ticker, _isEquity);
    }

    function withdrawToken(address _tokenContract, address _recipient, uint256 _amount) external onlyOwner{
        IERC20(_tokenContract).transfer(_recipient, _amount);
        emit WithdrawalProcessed(address(this), _tokenContract, _recipient, _amount);
    }
}
"
    },
    "contracts/mainWallet/MainWalletUpgFactory.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "./MainWalletUpg.sol";
import "./MainWalletBeacon.sol";
import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";

contract MainWalletUpgFactory {

    MainWalletBeacon immutable beacon;

    constructor(address _initBlueprint) {
        beacon = new MainWalletBeacon(_initBlueprint);
    }

    function createMainWallet(string memory _companyName, bool _isPrivate, address _developerAddress) public returns(address){
        BeaconProxy ship = new BeaconProxy(
            address(beacon),
            abi.encodeWithSelector(MainWalletUpg(address(0)).initialize.selector, _companyName, _isPrivate, _developerAddress)
        );
        return address(ship);
    }

    function getBeacon() public view returns (address) {
        return address(beacon);
    }

    function getImplementation() public view returns (address) {
        return beacon.implementation();
    }

}
"
    },
    "contracts/RevenueWallet.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract RevenueWallet is Ownable{

    address immutable private mainWalletAddress;

    constructor(address _mainWalletAddress){
        mainWalletAddress = _mainWalletAddress;
        _transferOwnership(tx.origin);
    }

    event WithdrawalProcessed(address mainWalletAddress, address revenueWalletAddress, address tokenAddress, address recipient, uint amount);

    function withdrawToken(address _tokenContract, address _recipient, uint256 _amount) external onlyOwner{
        ERC20(_tokenContract).transfer(_recipient, _amount);
        emit WithdrawalProcessed(mainWalletAddress, address(this), _tokenContract, _recipient, _amount);
    }
}
"
    },
    "contracts/Vesting.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
 * @dev Vesting library.

 * This library is used for token distribution based on vesting params.
 * Also here are some structs used across Forcefi smart contracts.
 */
library Vesting {
    using SafeMath for uint256;

    /**
    * @notice Fundraising data struct.

    * @param1 - fundraising label to identify the contract
    * @param2  - when vesting will start
    * @param3  - how much time tokens will be locked
    * @param4  - how much vesting will last
    * @param5  - how often vesting will be released. (If set to 0, it will be linear)
    * @param6  - how much tokens will be releasable right after cliff period ends
    * @param7  - total tokens amount that can be raised
    * @param8  - minimal amount of tokens that's needed to successfully finish campaign
    * @param9  - investment token : locked token ratio multiplied by 100. Higher rate means more investment tokens are needed to get locked token.
    * @param10  - sale start date timestamp
    * @param11  - sale end date timestamp
    * @param12  - if bool is set true, only whitelisted addresses can participate
    * @param13  - minimal amount of tokens that can be bought in these campaign for single invest event
    */
    struct FundraisingData {
        string _label;
        uint _vestingStart;
        uint _cliffPeriod;
        uint _vestingPeriod;
        uint _releasePeriod;
        uint _tgePercent;
        uint _totalCampaignLimit;
        uint _campaignSoftCap;
        uint _rate;
        uint _startDate;
        uint _endDate;
        bool _isPrivate;
        uint _campaignMinTicketLimit;
    }

    /**
    * @notice Vesting plan data struct.

    * @param1 - vesting label to identify the contract
    * @param2  - when sale will start
    * @param3  - how much time tokens will be locked
    * @param4  - how much vesting will last
    * @param5  - how often vesting will be released. (If set to 0, it will be linear)
    * @param6  - how much tokens will be releasable right after cliff period ends
    * @param7  - total tokens amount that can be allocated for this vesting plan
    * @param8  - how much tokens are already allocated for this vesting plan
    * @param9  - whether the plan is initialized, prevent of duplicate vesting plans
    */
    struct VestingPlan {
        string label;
        uint saleStart;
        uint cliffPeriod;
        uint vestingPeriod;
        uint releasePeriod;
        uint tgePercent;
        uint totalTokenAmount;
        uint tokenAllocated;
        bool initialized;
    }

    /**
    * @notice Individual Vesting data struct.

    * @param1 - tokens allocated for this owner vesting
    * @param2  - tokens released
    * @param3  - whether the plan is initialized, prevent of duplicate vesting plans
    * @param4  - initialization timestamp to calculate the right amount of tokens that can be released based on vesting plan
    */
    struct IndividualVesting {
        uint tokenAmount;
        uint tokensReleased;
        bool initialized;
        uint initializedTimestamp;
    }

    /**
    * @notice Token data struct.

    * @param1 - ERC20 token name
    * @param2  - ERC20 token ticker
    * @param3  - ERC20 factory address that created the token
    * @param4  - initial mint amount
    * @param5  - param is used for equity fundraising
    */
    struct TokenData{
        string _tokenName;
        string _tokenTicker;
        address _erc20TokenFactoryAddress;
        uint _mintAmount;
        bool isNewToken;
    }

    function computeReleasableAmount(uint start, uint duration, uint period, uint lockUpPeriod, uint tgeAmount, uint invested, uint released) internal view returns(uint256){
        uint256 currentTime = block.timestamp;
        if (currentTime >= start.add(duration)) {
            return invested.sub(released);
        } else {
            uint256 tgeCalculatedAmount = invested * tgeAmount / 100;
            uint256 timeFromStart = currentTime.sub(start).sub(lockUpPeriod);
            uint256 vestedPeriods = timeFromStart.div(period);
            uint256 vestedSeconds = vestedPeriods.mul(period);
            uint256 vestedAmount = (invested - tgeCalculatedAmount).mul(vestedSeconds).div(duration);

            uint256 tgeVestedAmount = vestedAmount.add(tgeCalculatedAmount);
            tgeVestedAmount = tgeVestedAmount.sub(released);

            return tgeVestedAmount;
        }
    }

    function toWei(uint _amount) internal pure returns(uint){
        return _amount * 10**18;
    }
}
"
    }
