pragma solidity ^0.8.12;






contract KlekshunNFTStaking is Ownable, ReentrancyGuard {
    event Received();

    using Strings for uint256;

    enum Step {
        Off,
        On
    }
    Step public Steps;
    struct StakingInfo {
        uint256 stakingTimestamp;
        uint256 lastClaimedTimestamp;
    }

    struct ContractRule {
        uint256 reward;
    }

    IERC20 public rewardToken;
    IERC721[] public stakeableNFTs;
    uint256 public timeUnit = 86400; // 1 day in seconds

    mapping(uint256 => mapping(uint256 => StakingInfo)) internal stakingInfos;
    mapping(uint256 => ContractRule) public contractRules;

    constructor(IERC20 _rewardToken, IERC721[] memory _stakeableNFTs) {
        rewardToken = _rewardToken;
        stakeableNFTs = _stakeableNFTs;
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    // STAKING FUNCTION
    function stake(uint256 _contractIndex, uint256[] calldata _tokenIds)
        external
        callerIsUser
    {
        require(Steps == Step.On, "Staking is disabled.");
        IERC721 nftCollection = stakeableNFTs[_contractIndex];

        for (uint256 i = 0; i < _tokenIds.length; i++) {
            require(
                nftCollection.ownerOf(_tokenIds[i]) == msg.sender,
                "You don't own this NFT"
            );

            StakingInfo storage info = stakingInfos[_contractIndex][
                _tokenIds[i]
            ];
            require(info.stakingTimestamp == 0, "NFT already staked");

            info.stakingTimestamp = block.timestamp;
            info.lastClaimedTimestamp = block.timestamp;
        }
    }

    function unstake(uint256 _contractIndex, uint256[] calldata _tokenIds) nonReentrant
    external
    {
        require(Steps == Step.On, "Unstaking is disabled.");

        for (uint256 i = 0; i < _tokenIds.length; i++) {
            IERC721 nftCollection = stakeableNFTs[_contractIndex];
            require(nftCollection.ownerOf(_tokenIds[i]) == msg.sender, "You don't own this NFT");

            uint256 reward = calculateReward(_contractIndex, _tokenIds[i]);
            if (reward > 0) {
                rewardToken.transfer(msg.sender, reward);
            }

            delete stakingInfos[_contractIndex][_tokenIds[i]];
        }
    }

    function claimRewards(uint256 _contractIndex, uint256[] calldata _tokenIds) nonReentrant
        external
    {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            IERC721 nftCollection = stakeableNFTs[_contractIndex];
            require(nftCollection.ownerOf(_tokenIds[i]) == msg.sender, "You don't own this NFT");

            uint256 reward = calculateReward(_contractIndex, _tokenIds[i]);
            if (reward > 0) {
                rewardToken.transfer(msg.sender, reward);
                stakingInfos[_contractIndex][_tokenIds[i]].lastClaimedTimestamp = block.timestamp;
            }
        }
    }



    // FUNCTIONS FOR REWARDS CALCULATION
    function calculateReward(uint256 _contractIndex, uint256 _tokenId)
        internal
        view
        returns (uint256)
    {
        StakingInfo storage info = stakingInfos[_contractIndex][_tokenId];
        if(info.stakingTimestamp == 0){
            return 0;
        }
        uint256 timeElapsed = block.timestamp - info.lastClaimedTimestamp;
        uint256 rewardPerDay = contractRules[_contractIndex].reward;
        return (timeElapsed / timeUnit) * rewardPerDay;
    }


    // EXTERNAL FUNCTION FOR CHECKING USER REWARDS
    function earnedRewards(uint256 _contractIndex, uint256 _tokenId)
        external
        view
        returns (uint256)
    {
        return calculateReward(_contractIndex, _tokenId);
    }

    function batchEarnedRewards(
        uint256 _contractIndex,
        uint256[] calldata _tokenIds
    ) external view returns (uint256[] memory rewards) {
        rewards = new uint256[](_tokenIds.length);
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            rewards[i] = calculateReward(_contractIndex, _tokenIds[i]);
        }
        return rewards;
    }

    function setReward(uint256 _contract, uint256 _reward) external onlyOwner {
        contractRules[_contract].reward = _reward * 10**18;
    }

    function updateStakeableNFTs(uint256 _index, IERC721 _newNFT)
        external
        onlyOwner
    {
        if (_index < stakeableNFTs.length) {
            stakeableNFTs[_index] = _newNFT;
        } else {
            stakeableNFTs.push(_newNFT);
        }
    }

    function setStep(uint256 _step) external onlyOwner {
        Steps = Step(_step);
    }

    function checkTokenBalance() external view returns (uint256) {
        return rewardToken.balanceOf(address(this));
    }

    function withdrawRewardToken() external onlyOwner {
        rewardToken.transfer(msg.sender, rewardToken.balanceOf(address(this)));
    }

    function areTokensStaked(uint256 _contractIndex, uint256[] calldata _tokenIds)
        external
        view
        returns (bool[] memory stakedStatus)
    {
        stakedStatus = new bool[](_tokenIds.length);
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            stakedStatus[i] = stakingInfos[_contractIndex][_tokenIds[i]].stakingTimestamp != 0;
        }
        return stakedStatus;
    }

}
