contract SPINWHEEL is Context, Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    address public rewardToken=0xc2132D05D31c914a87C6611C10748AEb04B58e8F;
    address public nftAddress;
    uint256 public totalRewards;
    uint256 public totalClaimed;
    mapping(uint256=>uint256) public chanceForCategory;
    mapping(uint256=>uint256) public rewards;
    mapping(uint256=>uint256) public lastSpinned;
    uint256 public PERCENT_DIVIDER = 100000;
    uint256 nonce =1;

    struct User{    
        uint256 noOfTimesPlayed;
        uint256 totalRewards;
    }
    mapping(address=>User) public userList;

    event ClaimReward(address nftAsset,uint256 reward,address indexed account,uint256 indexed nftId,uint256 time);
    event SpinWheel(address user,uint256 no,uint256 catId,uint256 nftId,uint256 timestamp,uint256 rewards);
    constructor (address _nftAddress) {
        nftAddress=_nftAddress;
        chanceForCategory[1] = 24 hours;
        chanceForCategory[2] = 12 hours;
        chanceForCategory[3] = 8 hours;

        rewards[2]=5*1e17;
        rewards[7]=2*1e18;
        rewards[12]=5*1e17;
        rewards[15]=5*1e17;
        rewards[18]=5*1e17;
    }

    function random() internal returns (uint) {
        uint randomnumber = uint(keccak256(abi.encodePacked(block.timestamp, msg.sender, nonce))) % 20;
        randomnumber = randomnumber + 1;
        nonce++;
        return randomnumber;
    }


    function startSpinWheel(uint256 nftId) public returns(uint256 winningPosition)
    {
        uint256 no = random();
        require(IERC721(nftAddress).ownerOf(nftId)==_msgSender(),"You are not owner of this nft");
        uint256 cat_id = getCategoryId(nftId);
        require(block.timestamp>lastSpinned[nftId].add(chanceForCategory[cat_id]),"Need to wait for some time");
        lastSpinned[nftId] = block.timestamp;
        if(rewards[no]>0)
        {
            IERC20(rewardToken).transfer(_msgSender(),rewards[no]);
            userList[_msgSender()].totalRewards =  userList[_msgSender()].totalRewards.add(rewards[no]);
        }
        userList[_msgSender()].noOfTimesPlayed =  userList[_msgSender()].noOfTimesPlayed.add(1);

        emit SpinWheel(_msgSender(),no,cat_id,nftId,block.timestamp,rewards[no]);
        return no;
    }


    function getCategoryId(uint256 nftId) public pure returns(uint256)
    {
        if(nftId>=1 && nftId<=15)
        {
            return 3;
        }
        else if(nftId>=16 && nftId<=105){
            return 2;
        }
        else if(nftId>=106 && nftId<=555)
        {
            return 1;
        }
        return 0;
    }

    function setTimeForSpin(uint256 cat_id,uint256 timespan) external onlyOwner
    {
        chanceForCategory[cat_id] = timespan;
    }

    function setRewards(uint256 segmentNo,uint256 reward) external onlyOwner
    {
        rewards[segmentNo] = reward;
    }

    function getTimeLeft(uint256 nftId) external view returns(uint256 timespan)
    {
        uint256 cat_id = getCategoryId(nftId);
        return lastSpinned[nftId].add(chanceForCategory[cat_id]);
    }

}"
