// File contracts/v2/StakeV2.sol

//  

pragma solidity ^0.8.4;








contract StakeV2 is ReentrancyGuard, Pausable, Ownable, IERC721Receiver {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    /* ========== STATE VARIABLES ========== */

    IERC20 public immutable rewardsToken;
    address[] public collectionAddresses;
    mapping(address => uint256) public collectionRatio;
    mapping(address => bool) public isAllowedCollection;
    mapping(address => uint256) public totalStaked;

    mapping(address => mapping(address => uint256))
        public collectionToUserToBalances;
    mapping(address => uint256) public rewardPerTokenStored; // sum of R/L from 0 to now
    mapping(address => uint256) public lastUpdateTime;

    uint256 public periodFinish = 0;
    uint256 public rewardsDuration = 7 days;
    uint256 public rewardRate = 0;

    mapping(address => mapping(address => uint256))
        public collectionToUserToRewardPerTokenPaid; // R/L from o to last update
    mapping(address => mapping(address => uint256))
        public collectionToUserToRewards;

    mapping(address => mapping(uint256 => address)) public nftToUser;

    // fee
    IStakeFee public stakeFee;
    address public chargePayee;

    // booster
    address public booster;
    mapping(address => uint256) public userToBoosterBalance;
    uint256 public boosterRatio = 2000; // 20%, must divide by 1e4
    mapping(address => uint256) public B; // user booster balance * user nft balance

    /* ========== CONSTRUCTOR ========== */

    constructor(
        address _rewardsToken,
        address[] memory _collections,
        uint256[] memory _ratios,
        address _booster,
        uint256 _boosterRatio,
        IStakeFee _stakeFee
    ) Ownable() {
        rewardsToken = IERC20(_rewardsToken);
        collectionAddresses = _collections;
        chargePayee = msg.sender;
        for (uint256 i = 0; i < _collections.length; i++) {
            collectionRatio[_collections[i]] = _ratios[i];
            isAllowedCollection[_collections[i]] = true;
        }
        booster = _booster;
        boosterRatio = _boosterRatio;
        stakeFee = _stakeFee;
    }

    /* ========== VIEWS ========== */

    function balanceOf(address collection, address account)
        external
        view
        returns (uint256)
    {
        return collectionToUserToBalances[collection][account];
    }

    function lastTimeRewardApplicable() public view returns (uint256) {
        return block.timestamp < periodFinish ? block.timestamp : periodFinish;
    }

    function rewardPerToken(address collection) public view returns (uint256) {
        if (totalStaked[collection] == 0) {
            return rewardPerTokenStored[collection];
        }

        uint256 top = rewardRate.mul(collectionRatio[collection]).mul(1e4);
        uint256 bot = (
            (totalStaked[collection].mul(1e4)).add(
                B[collection].mul(boosterRatio)
            )
        ).mul(100);

        return
            rewardPerTokenStored[collection].add(
                lastTimeRewardApplicable()
                    .sub(lastUpdateTime[collection])
                    .mul(1e18)
                    .mul(top)
                    .div(bot)
            );
    }

    function earned(address collection, address account)
        public
        view
        returns (uint256)
    {
        return
            collectionToUserToBalances[collection][account]
                .mul(1e4 + userToBoosterBalance[account].mul(boosterRatio))
                .mul(
                    rewardPerToken(collection).sub(
                        collectionToUserToRewardPerTokenPaid[collection][
                            account
                        ]
                    )
                )
                .div(1e18)
                .div(1e4)
                .add(collectionToUserToRewards[collection][account]);
    }

    function getRewardForDuration() external view returns (uint256) {
        return rewardRate.mul(rewardsDuration);
    }

    /* ========== FEE FUNCTIONS ========== */
    function setChargePayee(address _payee) public onlyOwner {
        chargePayee = _payee;
    }

    function setStakeFee(IStakeFee _stakeFee) public onlyOwner {
        stakeFee = _stakeFee;
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stake(address collection, uint256[] memory ids)
        external
        payable
        nonReentrant
        whenNotPaused
        updateReward(collection, msg.sender)
    {
        require(
            msg.value ==
                stakeFee.getFees(
                    address(this),
                    msg.sender,
                    IStakeFee.FeeType.Stake
                ) *
                    ids.length,
            "stake fee is not enough"
        );
        require(ids.length > 0, "Cannot stake 0 nft");
        require(isAllowedCollection[collection], "Collection is not allowed");

        totalStaked[collection] = totalStaked[collection].add(ids.length);
        collectionToUserToBalances[collection][
            msg.sender
        ] = collectionToUserToBalances[collection][msg.sender].add(ids.length);

        B[collection] = B[collection].add(
            ids.length.mul(userToBoosterBalance[msg.sender])
        );

        for (uint256 i = 0; i < ids.length; i++) {
            IERC721(collection).safeTransferFrom(
                msg.sender,
                address(this),
                ids[i]
            );
            nftToUser[collection][ids[i]] = msg.sender;
        }
        payable(chargePayee).transfer(msg.value);
        emit Staked(msg.sender, collection, ids.length);
    }

    function withdraw(address collection, uint256[] memory ids)
        public
        payable
        nonReentrant
        updateReward(collection, msg.sender)
    {
        require(
            msg.value ==
                stakeFee.getFees(
                    address(this),
                    msg.sender,
                    IStakeFee.FeeType.Withdraw
                ) *
                    ids.length,
            "withdraw fee is not enough"
        );
        require(ids.length > 0, "Cannot withdraw 0 nft");

        totalStaked[collection] = totalStaked[collection].sub(ids.length);
        collectionToUserToBalances[collection][
            msg.sender
        ] = collectionToUserToBalances[collection][msg.sender].sub(ids.length);

        B[collection] = B[collection].sub(
            ids.length.mul(userToBoosterBalance[msg.sender])
        );

        for (uint256 i = 0; i < ids.length; i++) {
            require(nftToUser[collection][ids[i]] == msg.sender, "not owner");
            IERC721(collection).safeTransferFrom(
                address(this),
                msg.sender,
                ids[i]
            );
        }
        payable(chargePayee).transfer(msg.value);
        emit Withdrawn(msg.sender, collection, ids.length);
    }

    function getReward() public payable nonReentrant {
        require(
            msg.value ==
                stakeFee.getFees(
                    address(this),
                    msg.sender,
                    IStakeFee.FeeType.Claim
                ),
            "claim fee is not enough"
        );
        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            _updateReward(collectionAddresses[i], msg.sender);
            uint256 reward = collectionToUserToRewards[collectionAddresses[i]][
                msg.sender
            ];

            if (reward > 0) {
                rewardsToken.safeTransfer(msg.sender, reward);
                emit RewardPaid(msg.sender, reward);
                collectionToUserToRewards[collectionAddresses[i]][
                    msg.sender
                ] = 0;
            }
        }
        payable(chargePayee).transfer(msg.value);
    }

    function addBooster(uint256 id) public payable nonReentrant {
        require(booster != address(0), "booster is not set");
        require(userToBoosterBalance[msg.sender] < 3, "booster limit reached");
        require(
            msg.value ==
                stakeFee.getFees(
                    address(this),
                    msg.sender,
                    IStakeFee.FeeType.AddBooster
                ),
            "add booster fee is not enough"
        );

        IERC721(booster).safeTransferFrom(msg.sender, address(this), id);
        nftToUser[booster][id] = msg.sender;

        userToBoosterBalance[msg.sender] = userToBoosterBalance[msg.sender].add(
            1
        );
        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            B[collectionAddresses[i]] = B[collectionAddresses[i]].add(
                collectionToUserToBalances[collectionAddresses[i]][msg.sender]
            );
            _updateReward(collectionAddresses[i], msg.sender);
        }
        payable(chargePayee).transfer(msg.value);
    }

    function removeBooster(uint256 id) public payable nonReentrant {
        require(booster != address(0), "booster is not set");
        require(userToBoosterBalance[msg.sender] > 0, "no booster to remove");
        require(nftToUser[booster][id] == msg.sender, "not owner");
        require(
            msg.value ==
                stakeFee.getFees(
                    address(this),
                    msg.sender,
                    IStakeFee.FeeType.RemoveBooster
                ),
            "remove booster fee is not enough"
        );

        IERC721(booster).safeTransferFrom(address(this), msg.sender, id);
        userToBoosterBalance[msg.sender] = userToBoosterBalance[msg.sender].sub(
            1
        );

        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            B[collectionAddresses[i]] = B[collectionAddresses[i]].sub(
                collectionToUserToBalances[collectionAddresses[i]][msg.sender]
            );

            _updateReward(collectionAddresses[i], msg.sender);
        }
        payable(chargePayee).transfer(msg.value);
    }

    /* ========== RESTRICTED FUNCTIONS ========== */

    function notifyRewardAmount(uint256 reward) external onlyOwner {
        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            _updateReward(collectionAddresses[i], address(0));
        }
        if (block.timestamp >= periodFinish) {
            rewardRate = reward.div(rewardsDuration);
        } else {
            uint256 remaining = periodFinish.sub(block.timestamp);
            uint256 leftover = remaining.mul(rewardRate);
            rewardRate = reward.add(leftover).div(rewardsDuration);
        }

        uint256 balance = rewardsToken.balanceOf(address(this));
        require(
            rewardRate <= balance.div(rewardsDuration),
            "Provided reward too high"
        );

        for (uint256 i = 0; i < collectionAddresses.length; i++) {
            lastUpdateTime[collectionAddresses[i]] = block.timestamp;
        }
        periodFinish = block.timestamp.add(rewardsDuration);
        emit RewardAdded(reward);
    }

    function setRewardsDuration(uint256 _rewardsDuration) external onlyOwner {
        require(
            block.timestamp > periodFinish,
            "Previous rewards period must be complete before changing the duration for the new period"
        );
        rewardsDuration = _rewardsDuration;
        emit RewardsDurationUpdated(rewardsDuration);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function enableBooster(address _booster) external onlyOwner {
        require(booster == address(0), "booster already set");
        booster = _booster;
    }

    function setBoosterRatio(uint256 _ratio) external onlyOwner {
        require(_ratio < 3300, "ratio must be less than 33%");
        boosterRatio = _ratio;
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address collection, address account) {
        _updateReward(collection, account);
        _;
    }

    function _updateReward(address collection, address account) internal {
        rewardPerTokenStored[collection] = rewardPerToken(collection);
        lastUpdateTime[collection] = lastTimeRewardApplicable();
        if (account != address(0)) {
            collectionToUserToRewards[collection][account] = earned(
                collection,
                account
            );
            collectionToUserToRewardPerTokenPaid[collection][
                account
            ] = rewardPerTokenStored[collection];
        }
    }

    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes memory data
    ) external pure returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    /* ========== EVENTS ========== */

    event RewardAdded(uint256 reward);
    event Staked(
        address indexed user,
        address indexed collection,
        uint256 amount
    );
    event Withdrawn(
        address indexed user,
        address indexed collection,
        uint256 amount
    );
    event RewardPaid(address indexed user, uint256 reward);
    event RewardsDurationUpdated(uint256 newDuration);
}
