// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "./interfaces/IERC721Collection.sol";

contract ERC721Collection is IERC721Metadata, ERC721, ERC721Enumerable, AccessControl, VRFConsumerBase, IERC721Collection {
    uint256[] internal _mintableIds;
    uint256 internal _pendingRequests;
    mapping(bytes32 => address) _requestIds;
    string private _storageURI;
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    bytes32 internal _keyHash;
    uint256 internal _vrfFee;
    uint256 internal _maxSupply;

    constructor(string memory name, string memory symbol, address vrfCoordinator, address LINKToken, bytes32 keyHash, uint256 vrfFee) ERC721(name, symbol) VRFConsumerBase(
            vrfCoordinator, // Polygon VRF Coordinator
            LINKToken  // Polygon LINK Token
        ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _keyHash = keyHash; // VRF KeyHash
        _vrfFee = vrfFee; // VRF Fees
    }

    function addTokens(uint256[] memory tokenIds) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < tokenIds.length; i++) {
            _mintableIds.push(tokenIds[i]);
        }
        _maxSupply += tokenIds.length;
    }

    function maxSupply() external view returns (uint256) {
        return _maxSupply;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(AccessControl, ERC721, IERC165, ERC721Enumerable) returns (bool) {
        return
            interfaceId == type(IERC721).interfaceId ||
            interfaceId == type(IERC721Metadata).interfaceId ||
            interfaceId == type(AccessControl).interfaceId ||
            interfaceId == type(ERC721Enumerable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function setStorageURI(string memory storageURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _storageURI = storageURI;
    }

    function _baseURI() internal view override returns (string memory) {
        return _storageURI;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override(ERC721, ERC721Enumerable) {
        ERC721._beforeTokenTransfer(from, to, tokenId);
        ERC721Enumerable._beforeTokenTransfer(from, to, tokenId);
    }

    function mint(address to) external onlyRole(MINTER_ROLE) {
        require(LINK.balanceOf(address(this)) >= _vrfFee, "Not enough LINK - fill contract with faucet");
        require(_pendingRequests + totalSupply() < _maxSupply, "All tokens have been minted");
        bytes32 requestId = requestRandomness(_keyHash, _vrfFee);
        _requestIds[requestId] = to;
        _pendingRequests += 1;
    }

    function burn(uint256 tokenId) external onlyRole(BURNER_ROLE) {
        _burn(tokenId);
    }

    function fulfillRandomness(bytes32 requestId, uint256 randomness) internal override {
        uint256 newTokenIndex = randomness % _mintableIds.length;
        uint256 newTokenId = _mintableIds[newTokenIndex];
        _mintableIds[newTokenIndex] = _mintableIds[_mintableIds.length - 1];
        _mintableIds.pop();
        _mint(_requestIds[requestId], newTokenId);
        _pendingRequests -= 1;
    }
}
"
