    "contracts/Runiverse/Runiverse.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;
//import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";
import "./RuniverseAdministrative.sol";
import "./IDomainSeparator.sol";
import "hardhat/console.sol";

contract Runiverse is Pausable, ReentrancyGuard, EIP712, RuniverseAdministrative, IDomainSeparator {
	using SafeERC20 for IERC20;
	using SafeMath for uint256;
    using Counters for Counters.Counter;
    mapping(address => Counters.Counter) private _nonces;

	uint256 public arenaPercentageFee = 6000; // arena rate 60% of 10% 
	uint256 public avatarPercentageFee = 2000; // avatar rate 40% of 10% splitted in 2 player
    uint256 private _biggestGameId; //biggest gameId used

	struct SignedStartGame {
        address sender;
		address playerAddress;
		address tokenChosen;
		uint256 startPrice;
        uint8 useJolly; //1 = yes, 0 = no
        uint256 jollyPrice;
        uint256 amountBetted;
        uint deadline;
        bytes signature;
	}

	struct Player {
		address playerAddress;
		address tokenChosen;
		uint256 startPrice;
		uint256 closePrice;
	}

	struct Run {
		uint256 gameId;
		uint256 startGameTimestamp;
		uint256 endGameTimestamp;
		Player player1;
		Player player2;
		address winner;
        address loser;
	}

	struct RewardInfo {
		address bettedToken;
		uint256 bettedAmount;
		address rewardToken;
		uint256 rewardAmount;
		bool claimed;
	}

	enum Winner {
		PLAYER1,
		PLAYER2,
		NONE
	}

	mapping(uint256 => Run) public runs;
	mapping(uint256 => mapping(address => RewardInfo)) public ledger;

    bytes32 private constant _PLAYER_TYPEHASH =
        keccak256("StartRun(address sender,address tokenChosen,uint256 amountBetted,uint256 gameId,uint256 chainId,uint256 deadline)");
    
    // bytes32 private constant _JOLLYUSED_TYPEHASH =
    //     keccak256("StartRun(address sender,address player,address tokenChosen,uint256 amountBetted,uint256 gameId,uint256 deadline)");
        
	event StartRun(uint256 indexed gameId, address runTokenPlayer1, address runTokenPlayer2, uint256 startGameTimestamp);
	event EndRun(uint256 indexed gameId, address winner, uint256 endGameTimestamp);
	event Claim(address indexed sender, uint256 indexed gameId, address token, uint256 amount);
    event SignatureLenght(uint256 lenght);
    event SplitSignature(bytes32 r, bytes32 s, uint8 v);
    
	constructor(
		address _adminAddress,
		address _operatorAddress,
        address _startRunOperatorAddress,
        address _endRunOperatorAddress,
		uint256 _treasuryFee
	) EIP712("Runiverse","1") {
		require(_treasuryFee <= MAX_TREASURY_FEE, "Treasury fee too high");
		require(_adminAddress != address(0), "Cannot be zero address");
		require(_operatorAddress != address(0), "Cannot be zero address");
        require(_startRunOperatorAddress != address(0), "Cannot be zero address");
        require(_endRunOperatorAddress != address(0), "Cannot be zero address");

		adminAddress = _adminAddress;
		operatorAddress = _operatorAddress;
        startRunOperatorAddress = _startRunOperatorAddress;
        endRunOperatorAddress = _endRunOperatorAddress;
		treasuryFee = _treasuryFee;
        distributionArenaFeesCounter = 1;
        _biggestGameId = 0;
	}

	function startRun(uint256 gameId, SignedStartGame calldata player1, SignedStartGame calldata player2) external onlyStartRunOperator whenNotPaused nonReentrant {
		//check for unique gameid
		Run storage run = runs[gameId];
		require(run.gameId == 0,"Game id already in use");

        //set ticketCost
        require(player1.amountBetted == player2.amountBetted, "Different betted amount");
        uint256 ticketCost = (player1.amountBetted * 1e6);

        //checkBan
        require(bannedPlayers[player1.playerAddress] == false, "Player1 has been banned");
        require(bannedPlayers[player2.playerAddress] == false, "Player2 has been banned");

        require(bettableTokens[player1.tokenChosen] == true, "Player1 tokenChoose is not a bettableToken");
        require(bettableTokens[player2.tokenChosen] == true, "Player2 tokenChoose is not a bettableToken");

		checkSignature(player1.signature, player1, gameId);
        checkSignature(player2.signature, player2, gameId);

		//create run
		run.gameId = gameId;
		run.player1.playerAddress = player1.playerAddress;
		run.player1.tokenChosen = player1.tokenChosen;
		run.player1.startPrice = player1.startPrice;
		run.player2.playerAddress = player2.playerAddress;
		run.player2.tokenChosen = player2.tokenChosen;
		run.player2.startPrice = player2.startPrice;
		run.startGameTimestamp = block.timestamp;
		
		//calculate reward
		RewardInfo storage rewardInfoPlayer1 = ledger[gameId][run.player1.playerAddress];
		RewardInfo storage rewardInfoPlayer2 = ledger[gameId][run.player2.playerAddress];
        
        ERC20 player1PaymentToken = ERC20(run.player1.tokenChosen);
        ERC20 player2PaymentToken = ERC20(run.player2.tokenChosen);

        //check jolly
        if(player1.useJolly==1)
        {
            player1PaymentToken = ERC20(jollyTokenContract);
        }
        
        if(player2.useJolly==1)
        {
            player2PaymentToken = ERC20(jollyTokenContract);
        }

		uint256 player1PaymentTokenDecimals;
		uint256 player2PaymentTokenDecimals;

		try ERC20(player1PaymentToken).decimals() returns (uint8 decimals) {
			player1PaymentTokenDecimals = decimals;
		} catch {
			player1PaymentTokenDecimals = 18;
		}

		try ERC20(player2PaymentToken).decimals() returns (uint8 decimals) {
			player2PaymentTokenDecimals = decimals;
		} catch {
			player2PaymentTokenDecimals = 18;
		}
        
		uint256 bettedAmountPlayer1;
		uint256 bettedAmountPlayer2;

        //check jolly
        if(player1.useJolly==1)
        {
            bettedAmountPlayer1 = ((10**player1PaymentTokenDecimals) * ticketCost) / player1.jollyPrice;
            rewardInfoPlayer1.bettedToken = jollyTokenContract;
            rewardInfoPlayer1.bettedAmount = bettedAmountPlayer1;
        }
        else
        {
            bettedAmountPlayer1 = ((10**player1PaymentTokenDecimals) * ticketCost) / player1.startPrice;
            rewardInfoPlayer1.bettedToken = run.player1.tokenChosen;
            rewardInfoPlayer1.bettedAmount = bettedAmountPlayer1;
        }

        if(player2.useJolly==1)
        {
            bettedAmountPlayer2 = ((10**player2PaymentTokenDecimals) * ticketCost) / player2.jollyPrice;
            rewardInfoPlayer2.bettedToken = jollyTokenContract;
            rewardInfoPlayer2.bettedAmount = bettedAmountPlayer2;
        }
        else
        {
            bettedAmountPlayer2 = ((10**player2PaymentTokenDecimals) * ticketCost) / player2.startPrice;
            rewardInfoPlayer2.bettedToken = run.player2.tokenChosen;
            rewardInfoPlayer2.bettedAmount = bettedAmountPlayer2;
        }

		//move token

		if(player1.useJolly==1)
        {
            IERC20(jollyTokenContract).safeTransferFrom(run.player1.playerAddress, address(this), bettedAmountPlayer1);
        }
        else
        {
            IERC20(run.player1.tokenChosen).safeTransferFrom(run.player1.playerAddress, address(this), bettedAmountPlayer1);
        }

        if(player2.useJolly==1)
        {
            IERC20(jollyTokenContract).safeTransferFrom(run.player2.playerAddress, address(this), bettedAmountPlayer2);
        }
        else
        {
            IERC20(run.player2.tokenChosen).safeTransferFrom(run.player2.playerAddress, address(this), bettedAmountPlayer2);
        }

        if(gameId > _biggestGameId){
            _biggestGameId = gameId;
        }

		//console.log("token transferred");

		//emit event
		emit StartRun(gameId, run.player1.tokenChosen, run.player2.tokenChosen, block.timestamp);
	}

	function endRun(uint256 gameId, uint256[] calldata endGamePrices) external onlyEndRunOperator whenNotPaused nonReentrant {
		Run storage run = runs[gameId];
		require(run.endGameTimestamp == 0, "Run already closed");
		require(
			block.timestamp > run.startGameTimestamp + gameDuration && block.timestamp < run.startGameTimestamp + gameDuration + endGameTimeout,
			"Cannot close run at this time"
		);

		run.player1.closePrice = endGamePrices[0];
		run.player2.closePrice = endGamePrices[1];

		Winner winner = _getWinner(
			int256(run.player1.startPrice),
			int256(run.player1.closePrice),
			int256(run.player2.startPrice),
			int256(run.player2.closePrice)
		);

		RewardInfo memory loserInfo;
		RewardInfo memory winnerInfo;
		uint256 fee;

		require(winner != Winner.NONE, "Game Tied, please refund");

		if (winner == Winner.PLAYER1) {
			run.winner = run.player1.playerAddress;
            run.loser = run.player2.playerAddress;
			winnerInfo = ledger[gameId][run.player1.playerAddress];
			loserInfo  = ledger[gameId][run.player2.playerAddress];
			fee = (loserInfo.bettedAmount * treasuryFee) / 10000;
			_calculateRewards(gameId, run.winner, loserInfo,fee); 
		}
		if (winner == Winner.PLAYER2) {
			run.winner = run.player2.playerAddress;
            run.loser = run.player1.playerAddress;
			winnerInfo = ledger[gameId][run.player2.playerAddress];
			loserInfo = ledger[gameId][run.player1.playerAddress];
			fee = (loserInfo.bettedAmount * treasuryFee) / 10000;
			_calculateRewards(gameId, run.winner, loserInfo,fee);
		}

		//console.log("reward token %s ",loserInfo.bettedToken);
		uint256 arenaFee = fee * arenaPercentageFee / 10000 ;
		_setArenaFee(gameId, loserInfo.bettedToken, arenaFee);
		
		uint256 avatarFee= fee * avatarPercentageFee / 10000;

        //set fees for the avatar used
		_setAvatarFee(gameId, run.player1.tokenChosen , loserInfo.bettedToken,avatarFee);
		_setAvatarFee(gameId, run.player2.tokenChosen, loserInfo.bettedToken ,avatarFee);

		run.endGameTimestamp = block.timestamp;

		emit EndRun(gameId, run.winner, block.timestamp);
	}

    function getRunWinner(uint256 gameId) external view returns (address) {
        Run memory run = runs[gameId];
        return run.winner;
    }

    function getRunLoser(uint256 gameId) external view returns (address) {
        Run memory run = runs[gameId];
        return run.loser;
    }

	function _getWinner(
		int256 player1StartPrice,
		int256 player1EndPrice,
		int256 player2StartPrice,
		int256 player2EndPrice
	) internal pure returns (Winner) {
		int256 player1PriceVariation = ((player1EndPrice - player1StartPrice) * 1e10) / player1StartPrice;
		int256 player2PriceVariation = ((player2EndPrice - player2StartPrice) * 1e10) / player2StartPrice;

		if (player1PriceVariation > player2PriceVariation) {
			return Winner.PLAYER1;
		}

		if (player2PriceVariation > player1PriceVariation) {
			return Winner.PLAYER2;
		}

		return Winner.NONE;
	}

	function _calculateRewards(
		uint256 gameId,
		address winner,
		RewardInfo memory loser,
		uint256 fee
	) internal {
		RewardInfo storage rewardInfo = ledger[gameId][winner];
		rewardInfo.rewardToken = loser.bettedToken;
		rewardInfo.rewardAmount = loser.bettedAmount - fee;
	}

	
	function claim(uint256 gameId) external nonReentrant notContract {
		require(runs[gameId].startGameTimestamp != 0, "Round has not started");
		require(block.timestamp > runs[gameId].endGameTimestamp, "Round has not ended");

		RewardInfo storage rewardInfo = ledger[gameId][msg.sender];
		address rewardToken = rewardInfo.rewardToken;
		uint256 rewardAmount = rewardInfo.rewardAmount;
		address bettedToken = rewardInfo.bettedToken;
		uint256 bettedAmount = rewardInfo.bettedAmount;

        if (claimable(gameId, msg.sender)) {
			rewardInfo.claimed = true;

			emit Claim(msg.sender, gameId, rewardToken, rewardAmount);
			emit Claim(msg.sender, gameId, bettedToken, bettedAmount);

			IERC20(rewardToken).safeTransfer(address(msg.sender), rewardAmount);
			IERC20(bettedToken).safeTransfer(address(msg.sender), bettedAmount);
		} else if (refundable(gameId, msg.sender)) {
			rewardInfo.claimed = true;

			emit Claim(msg.sender, gameId, rewardToken, rewardAmount);

			IERC20(bettedToken).safeTransfer(address(msg.sender), bettedAmount);
		}

		// if (claimable(gameId, msg.sender)) {
		// 	rewardInfo.claimed = true;

		// 	emit Claim(msg.sender, gameId, rewardToken, rewardAmount);
		// 	emit Claim(msg.sender, gameId, bettedToken, bettedAmount);

        //     if(rewardToken==jollyTokenContract)
        //     {
        //         IERC20(rewardToken).safeTransfer(operatorAddress, rewardAmount);
        //     }
        //     else
        //     {
        //         IERC20(rewardToken).safeTransfer(address(msg.sender), rewardAmount);
        //     }

        //     if(bettedToken==jollyTokenContract)
        //     {
        //         IERC20(bettedToken).safeTransfer(operatorAddress, bettedAmount);
        //     }
        //     else
        //     {
        //         IERC20(bettedToken).safeTransfer(address(msg.sender), bettedAmount);
        //     }

		// } else if (refundable(gameId, msg.sender)) {
		// 	rewardInfo.claimed = true;

		// 	emit Claim(msg.sender, gameId, rewardToken, rewardAmount);

		// 	if(bettedToken==jollyTokenContract)
        //     {
        //         IERC20(bettedToken).safeTransfer(operatorAddress, bettedAmount);
        //     }
        //     else
        //     {
        //         IERC20(bettedToken).safeTransfer(address(msg.sender), bettedAmount);
        //     }
		// }
	}

	function claimable(uint256 gameId, address user) public view returns (bool) {
		RewardInfo memory rewardInfo = ledger[gameId][user];
		Run memory run = runs[gameId];

		return rewardInfo.rewardAmount > 0 && rewardInfo.bettedAmount > 0 && !rewardInfo.claimed && run.winner == user;
	}

	function refundable(uint256 gameId, address user) public view returns (bool) {
		RewardInfo memory rewardInfo = ledger[gameId][user];
		Run memory run = runs[gameId];

		return
			run.endGameTimestamp == 0 &&
			run.startGameTimestamp > 0 &&
			!rewardInfo.claimed &&
			block.timestamp > run.startGameTimestamp + gameDuration + endGameTimeout &&
			rewardInfo.bettedAmount > 0;
	}

    /**
     * @dev See {IERC20Transfer-DOMAIN_SEPARATOR}.
     */
    // solhint-disable-next-line func-name-mixedcase
    function DOMAIN_SEPARATOR() external view override returns (bytes32) {
        return _domainSeparatorV4();
    }
     /**
     * @dev See {IERC20Transfer-nonces}.
     */
    function nonces(address owner) public view virtual override returns (uint256) {
        return _nonces[owner].current();
    }

    

    function checkSignature(bytes memory signature, SignedStartGame calldata player, uint256 gameId) internal {
        
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(signature);
        bytes32 structHash = keccak256(abi.encode(_PLAYER_TYPEHASH,player.sender,player.tokenChosen,player.amountBetted,gameId,block.chainid,player.deadline));
        address signer = ECDSA.recover(_hashTypedDataV4(structHash), v, r, s);
        require(signer == player.sender, "invalid signature");
    }

    function splitSignature(bytes memory signature) internal virtual returns (bytes32 r, bytes32 s, uint8 v) {
        require(signature.length == 65, "invalid signature length");

        assembly {
            // first 32 bytes, after the length prefix
            r := mload(add(signature, 32))
            // second 32 bytes
            s := mload(add(signature, 64))
            // final byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(signature, 96)))
        }
    }

    function biggestGameId() external view returns (uint256)
    {
        return _biggestGameId;
    }
}
"
