    "contracts/AutoMatic.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity >0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./interfaces/IUniswapRouter02.sol";
import "./interfaces/IUniswapPair.sol";

interface IVault {
    function notifyRewardAmount(address token, uint256 amount) external;
}

contract AutoMatic is Context, ERC20, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address;

    mapping(address => bool) private minters;
    mapping (address => bool) private mintersWithoutFee;

    uint256 public constant MAX_ALLOCATION = 10000;
    uint256 public alloc1 = 1000;
    uint256 public alloc2 = 500;
    uint256 public alloc3 = 0;
    address public wallet1; // Dev Wallet
    address public wallet2; // Market Wallet
    address public wallet3; // Treasury Wallet

    uint256 public taxFee = 1000; // 10% tax for the tradding
    uint256 public minSwapAmount = 10 ether;
    bool private inSwap;
    bool public startedSale;
    bool public autoBuyback = true;
    mapping(address => bool) public isExcludedFromFee;
    address public feeRecipient;
    bool public feeRecipientIsVault;

    address public unirouter =
        address(0xa5E0829CaCEd8fFDD4De3c43696c57F7D7A678ff);
    IERC20 public constant WMATIC =
        IERC20(0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270);

    event UpdateAllocation(uint256 alloc1, uint256 alloc2, uint256 alloc3);
    event UpdateWallets(address indexed wallet1, address indexed wallet2, address indexed wallet3);
    event UpdateMinter(address indexed minter, bool flag);
    event UpdateTaxFee(uint256 tax);
    event UpdateFeeRecipient(address recipient);

    modifier whiteListed() {
        require(minters[msg.sender], "!minter");
        _;
    }

    constructor(address _wallet1, address _wallet2, address _wallet3) ERC20("AutoMatic", "AUMI") {
        minters[msg.sender] = true;
        mintersWithoutFee[msg.sender] = true;
        wallet1 = _wallet1;
        wallet2 = _wallet2;
        wallet3 = _wallet3;
        feeRecipient = msg.sender;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[msg.sender] = true;
        isExcludedFromFee[wallet1] = true;
        isExcludedFromFee[wallet2] = true;
        isExcludedFromFee[wallet3] = true;
    }

    function setAllocation(uint256 _alloc1, uint256 _alloc2, uint256 _alloc3) external onlyOwner {
        require(_alloc1.add(_alloc2).add(alloc3) < MAX_ALLOCATION, "invalid value");
        alloc1 = _alloc1;
        alloc2 = _alloc2;
        alloc3 = _alloc3;

        emit UpdateAllocation(_alloc1, _alloc2, _alloc3);
    }

    function totalAllocation() external view returns (uint256) {
        return alloc1 + alloc2 +  alloc3;
    }

    function setWallets(address _wallet1, address _wallet2, address _wallet3) external onlyOwner {
        wallet1 = _wallet1;
        wallet2 = _wallet2;
        wallet3 = _wallet3;

        emit UpdateWallets(_wallet1, _wallet2, _wallet3);
    }

    function setTaxFee(uint256 _taxFee) external onlyOwner {
        taxFee = _taxFee;

        emit UpdateTaxFee(_taxFee);
    }

    function startSale() external onlyOwner {
        startedSale = true;
    }

    function enableAutoBuyback(bool _flag) external onlyOwner {
        autoBuyback = _flag;
    }

    function setFeeRecipient(address _recipient, bool _isVault) external onlyOwner {
        feeRecipient = _recipient;
        feeRecipientIsVault = _isVault;

        emit UpdateFeeRecipient(_recipient);
    }

    function setMinimumSwapAmount(uint256 _amount) external onlyOwner {
        minSwapAmount = _amount;
    }

    function setUniswapRouter(address _router) external onlyOwner {
        unirouter = _router;
    }

    function excludeFromFee(address _account) external onlyOwner {
        isExcludedFromFee[_account] = true;
    }

    function includeFromFee(address _account) external onlyOwner {
        isExcludedFromFee[_account] = false;
    }

    function setMinter(address _minter, bool _flag) external onlyOwner {
        minters[_minter] = _flag;

        emit UpdateMinter(_minter, _flag);
    }

    function setMinterWithoutFee(address _minter, bool _flag) external onlyOwner {
        mintersWithoutFee[_minter] = _flag;
        if (!minters[_minter] && _flag) minters[_minter] = true;

        emit UpdateMinter(_minter, _flag);
    }

    function mint(address _to, uint256 _amount)
        external
        whiteListed
        returns (uint256 mintAmount)
    {
        if (mintersWithoutFee[msg.sender] == true) {
            _mint(_to, _amount);
            return _amount;
        }

        mintAmount = _amount
            .mul(uint256(MAX_ALLOCATION).sub(alloc1).sub(alloc2).sub(alloc3))
            .div(MAX_ALLOCATION);
        _mint(_to, mintAmount);
        if (alloc1 > 0) {
            _mint(wallet1, _amount.mul(alloc1).div(MAX_ALLOCATION));
        }
        if (alloc2 > 0) {
            _mint(wallet2, _amount.mul(alloc2).div(MAX_ALLOCATION));
        }
        if (alloc3 > 0) {
            _mint(wallet3, _amount.mul(alloc3).div(MAX_ALLOCATION));
        }
    }

    function mintWithoutFee(address _to, uint256 _amount) external onlyOwner {
        _mint(_to, _amount);
    }

    /**
     Transfer
     */

    function _transfer(
        address _sender,
        address _recipient,
        uint256 _amount
    ) internal override {
        address pair = IUniswapV2Factory(IUniswapRouter02(unirouter).factory()).getPair(address(this), IUniswapRouter02(unirouter).WETH());

        // Ignore tax process in case that remove liquidity
        if (!startedSale || (_sender == pair && _recipient == unirouter) || _sender == unirouter) {
            super._transfer(_sender, _recipient, _amount);
            return;
        }

        uint collectedTaxes = balanceOf(address(this));
        if (autoBuyback && collectedTaxes >= minSwapAmount && !_isTrading(_sender, _recipient) && !inSwap) {
            buybackAndSupply();
        }

        uint256 tFee = _amount.mul(taxFee).div(10**4);
        if (tFee > 0 && _isTrading(_sender, _recipient) && !(isExcludedFromFee[_sender] || isExcludedFromFee[_recipient])) {
            super._transfer(_sender, address(this), tFee);
            _amount -= tFee;
        }
        super._transfer(_sender, _recipient, _amount);
    }

    /**
     Buyback WMATIC and supply to vault
     */

     function buybackAndSupply() public {
        if (inSwap) return;

        uint256 collectedTaxes = balanceOf(address(this));
        address[] memory route = new address[](2);
        route[0] = address(this);
        route[1] = address(WMATIC);

        uint256 expectAmount = IUniswapRouter02(unirouter).getAmountsOut(
            collectedTaxes,
            route
        )[1];

        if (expectAmount == 0) return;

        inSwap = true;

        IERC20(address(this)).safeApprove(unirouter, collectedTaxes);
        uint256 maticAmount = IUniswapRouter02(unirouter).swapExactTokensForTokens(
            collectedTaxes,
            0, // Accept any amount of WMATIC
            route,
            feeRecipient,
            block.timestamp.add(180)
        )[1];

        if (maticAmount > 0 && feeRecipientIsVault) {
            IVault(feeRecipient).notifyRewardAmount(address(WMATIC), maticAmount);
        }

        inSwap = false;
     }

    function _isTrading(address _sender, address _recipient)
        internal view
        returns (bool)
    {
        address pair = IUniswapV2Factory(IUniswapRouter02(unirouter).factory()).getPair(address(this), IUniswapRouter02(unirouter).WETH());

        if (pair == address(0)) return false; // There is no liquidity yet

        if (_sender == pair && _recipient != unirouter) return true; // Sell Case

        if (_recipient == pair) return true; // Sell Case

        return false;
    }
}"
