    "contracts/LuchaMarketplaceERC1155.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface IERC1155Supply {
	function totalSupply(uint256 id) external view returns (uint256);
}

contract LuchaMarketplaceERC1155 is AccessControl, Pausable, ReentrancyGuard {
	using SafeMath for uint256;
	using Counters for Counters.Counter;

	Counters.Counter public listingIds;
	Counters.Counter public offerIds;

	IERC20 public luchaToken = IERC20(0x6749441Fdc8650b5b5a854ed255C82EF361f1596);
	address public luchaVault = 0xdF789493B9C1aa8B78D467a094FDEb5e44c18e9B;
	address public luchaRewards = 0x72104d619BaEDf632936d9dcE38C089CA3bf12Dc;
	address public luchaTreasury = 0x0Cb11b92Fa5C30eAfe4aE84B7BB4dF3034C38b9d;

	uint256 public listingFee;
	uint256 public minListingPrice;
	uint256 public paymentDivisor;
	uint256 public vaultPart;
	uint256 public rewardsPart;
	uint256 public treasuryPart;

	struct Listing {
		address seller;
		uint256 tokenId;
		uint256 price;
		uint256 quantity;
		uint256 expiry;
	}

	struct Offer {
		address buyer;
		uint256 tokenId;
		uint256 price;
		uint256 quantity;
		uint256 expiry;
	}

	mapping(address => bool) public originsApproved;
	mapping(address => mapping(uint256 => Listing)) public listings;
	mapping(address => mapping(uint256 => Offer)) public offers;

	event ListingCreated(
		uint256 indexed listingId,
		address indexed originContract,
		address indexed seller,
		uint256 tokenId,
		uint256 price,
		uint256 quantity,
		uint256 expiry
	);

	event ListingSold(
		uint256 indexed listingId,
		address indexed originContract,
		address indexed seller,
		address buyer,
		uint256 tokenId,
		uint256 price,
		uint256 quantity
	);

	event ListingCancelled(
		uint256 indexed listingId,
		address indexed originContract
	);

	event OfferCreated(
		uint256 indexed offerId,
		address indexed originContract,
		address indexed buyer,
		uint256 tokenId,
		uint256 price,
		uint256 quantity,
		uint256 expiry
	);

	event OfferAccepted(
		uint256 indexed offerId,
		address indexed originContract,
		address indexed buyer,
		address seller,
		uint256 tokenId,
		uint256 price,
		uint256 quantity
	);

	event OfferCancelled(
		uint256 indexed offerId,
		address indexed originContract
	);

	bytes32 public constant FEES_MANAGER_ROLE = keccak256("FEES_MANAGER_ROLE");

	constructor() {
		_setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
		_setupRole(FEES_MANAGER_ROLE, msg.sender);
	}

	function createListing(address originContract, uint256 tokenId, uint256 price, uint256 quantity, uint256 expiresInDays) external whenNotPaused nonReentrant {
		require(originsApproved[originContract], "Origin contract not approved");
		require(IERC1155(originContract).balanceOf(msg.sender, tokenId) >= quantity, "Insufficient ERC1155 balance");
		require(IERC1155(originContract).isApprovedForAll(msg.sender, address(this)), "Must have approved marketplace");
		require(price >= minListingPrice, "Sale price must be greater than min listing price");
		require(quantity > 0, "Quantity must be greater then 0");
		require(expiresInDays >= 1 && expiresInDays <= 180, "Expiry must be 1-180 days");

		uint256 expiry = block.timestamp.add(expiresInDays.mul(86400));

		listingIds.increment();

		listings[originContract][listingIds.current()] = Listing({
			seller: msg.sender,
			tokenId: tokenId,
			price: price,
			quantity: quantity,
			expiry: expiry
		});

		if (listingFee > 0) luchaToken.transferFrom(msg.sender, luchaTreasury, listingFee);

		emit ListingCreated(listingIds.current(), originContract, msg.sender, tokenId, price, quantity, expiry);
	}

	function cancelListing(uint256 listingId, address originContract) external {
		Listing storage listing = listings[originContract][listingId];

		require(listing.seller == msg.sender, "Must be listing creator to cancel listing");
		require(listing.quantity > 0, "Listing already closed");

		listing.quantity = 0;

		emit ListingCancelled(listingId, originContract);
	}

	function buyListing(uint256 listingId, address originContract, uint256 price, uint256 quantity) external whenNotPaused nonReentrant {
		Listing storage listing = listings[originContract][listingId];
		uint256 cost = price.mul(quantity);

		require(listing.quantity > 0, "Listing closed");
		require(listing.price.mul(quantity) == cost, "Incorrect purchase price");
		require(quantity > 0, "Quantity must be greater than 0");
		require(listing.quantity >= quantity, "Quantity exceeds available balance");
		require(listing.expiry > block.timestamp, "Listing expired");

		listing.quantity = listing.quantity.sub(quantity);

		IERC1155(originContract).safeTransferFrom(listing.seller, msg.sender, listing.tokenId, quantity, "");
		splitPayment(msg.sender, listing.seller, cost);

		emit ListingSold(listingId, originContract, listing.seller, msg.sender, listing.tokenId, listing.price, quantity);
	}

	function createOffer(address originContract, uint256 tokenId, uint256 price, uint256 quantity, uint256 expiresInDays) external whenNotPaused nonReentrant {
		require(originsApproved[originContract], "Origin contract not approved");
		require(IERC1155Supply(originContract).totalSupply(tokenId) > 0, "Token ID does not exist");
		require(luchaToken.balanceOf(msg.sender) >= price.mul(quantity), "Insufficient LUCHA balance");
		require(price >= minListingPrice, "Offer must be greater than min listing price");
		require(quantity > 0, "Quantity must be greater than 0");
		require(expiresInDays >= 1 && expiresInDays <= 180, "Expiry must be 1-180 days");

		uint256 expiry = block.timestamp.add(expiresInDays.mul(86400));

		offerIds.increment();

		offers[originContract][offerIds.current()] = Offer({
			buyer: msg.sender,
			tokenId: tokenId,
			price: price,
			quantity: quantity,
			expiry: expiry
		});

		if (listingFee > 0) luchaToken.transferFrom(msg.sender, luchaTreasury, listingFee);

		emit OfferCreated(offerIds.current(), originContract, msg.sender, tokenId, price, quantity, expiry);
	}

	function cancelOffer(uint256 offerId, address originContract) external {
		Offer storage offer = offers[originContract][offerId];

		require(offer.buyer == msg.sender, "Must be offer creator to cancel offer");
		require(offer.quantity > 0, "Offer already closed");

		offer.quantity = 0;

		emit OfferCancelled(offerId, originContract);
	}

	function acceptOffer(uint256 offerId, address originContract, uint256 price, uint256 quantity) external whenNotPaused nonReentrant {
		Offer storage offer = offers[originContract][offerId];
		uint256 cost = price.mul(quantity);

		require(offer.quantity > 0, "Offer closed");
		require(offer.price.mul(quantity) == cost, "Incorrect offer price");
		require(quantity > 0, "Quantity must be greater than 0");
		require(offer.quantity >= quantity, "Quantity exceeds offer quantity");
		require(offer.expiry > block.timestamp, "Offer expired");

		offer.quantity = offer.quantity.sub(quantity);

		IERC1155(originContract).safeTransferFrom(msg.sender, offer.buyer, offer.tokenId, quantity, "");
		splitPayment(offer.buyer, msg.sender, cost);

		emit OfferAccepted(offerId, originContract, offer.buyer, msg.sender, offer.tokenId, offer.price, quantity);
	}

	function splitPayment(address from, address to, uint256 amount) internal {
		uint256 vaultShare = amount.div(paymentDivisor).mul(vaultPart);
		uint256 rewardsShare = amount.div(paymentDivisor).mul(rewardsPart);
		uint256 treasuryShare = amount.div(paymentDivisor).mul(treasuryPart);

		if (vaultShare > 0) luchaToken.transferFrom(from, luchaVault, vaultShare);
		if (rewardsShare > 0) luchaToken.transferFrom(from, luchaRewards, rewardsShare);
		if (treasuryShare > 0) luchaToken.transferFrom(from, luchaTreasury, treasuryShare);

		uint256 sellerShare = amount.sub(vaultShare).sub(rewardsShare).sub(treasuryShare);
		luchaToken.transferFrom(from, to, sellerShare);
	}

	function setSaleFees(uint256 _paymentDivisor, uint256 _vaultPart, uint256 _rewardsPart, uint256 _treasuryPart) external onlyRole(FEES_MANAGER_ROLE) {
		require(_paymentDivisor > 0 && _paymentDivisor % 100 == 0, "paymentDivisor must be divisible by 100");

		paymentDivisor = _paymentDivisor;
		vaultPart = _vaultPart;
		rewardsPart = _rewardsPart;
		treasuryPart = _treasuryPart;
	}

	function setListingFee(uint256 value) external onlyRole(FEES_MANAGER_ROLE) {
		listingFee = value;
	}

	function setMinListingPrice(uint256 value) external onlyRole(FEES_MANAGER_ROLE) {
		minListingPrice = value;
	}

	function approveOriginContract(address originContract, bool value) external onlyRole(DEFAULT_ADMIN_ROLE) {
		originsApproved[originContract] = value;
	}

	function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_pause();
	}

	function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
		_unpause();
	}
}
