    "contracts/battle/emote/Emote.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract Emote is ERC1155Supply, ERC1155Burnable, AccessControlEnumerable {
    string private _baseURI;
    // Optional mapping for token URIs
    mapping(uint256 => string) private _tokenURIs;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /**
     * @dev Grants `DEFAULT_ADMIN_ROLE`, `MINTER_ROLE`, and `PAUSER_ROLE` to the account that
     * deploys the contract.
     */
    constructor(string memory _uri) ERC1155(_uri) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MINTER_ROLE, _msgSender());
        _setupRole(PAUSER_ROLE, _msgSender());
        _baseURI = _uri;
    }

    function mint(
        address to,
        uint256 id,
        uint256 amount,
        bytes memory data
    ) external onlyRole(MINTER_ROLE) {
        _mint(to, id, amount, data);
    }

    function setURI(uint256 tokenId, string memory tokenURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setURI(tokenId, tokenURI);
    }

    function setBaseURI(string memory baseURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setBaseURI(baseURI);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155Supply, ERC1155) {
        ERC1155Supply._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    /**
     * @dev See {IERC1155MetadataURI-uri}.
     *
     * This implementation returns `_baseURI` or the token-specific uri if the latter is set.
     */
    function uri(uint256 tokenId) public view virtual override returns (string memory) {
        string memory tokenURI = _tokenURIs[tokenId];

        // if token URI is set, use it, otherwise default to `_baseURI`
        return bytes(tokenURI).length > 0 ? tokenURI : _baseURI;
    }

    /**
     * @dev Sets `tokenURI` as the tokenURI of `tokenId`.
     */
    function _setURI(uint256 tokenId, string memory tokenURI) internal virtual {
        _tokenURIs[tokenId] = tokenURI;
        emit URI(uri(tokenId), tokenId);
    }

    /**
     * @dev Sets `baseURI` as the `_baseURI` for all tokens
     */
    function _setBaseURI(string memory baseURI) internal virtual {
        _baseURI = baseURI;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, AccessControlEnumerable)
        returns (bool)
    {
        return ERC1155.supportsInterface(interfaceId) || AccessControlEnumerable.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/battle/emote/EmoteMinter.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.4;

import "./Emote.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/cryptography/draft-EIP712.sol";

contract EmoteMinter is AccessControl, EIP712 {
    Emote public immutable emote;

    /// @dev mapping nonce => consumed
    mapping(uint256 => bool) nonces;

    /// @notice MINTER_ROLE is required to request welcome packs to be purchased.
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    string private constant SIGNING_DOMAIN = "EmoteMinter";
    string private constant SIGNATURE_VERSION = "1";

    struct MintRequest {
        address to;
        uint256 tokenId;
        uint256 quantity;
        uint256 nonce;
        bytes signature;
    }

    constructor(address owner, Emote _emote) EIP712(SIGNING_DOMAIN, SIGNATURE_VERSION) {
        emote = _emote;
        _setupRole(DEFAULT_ADMIN_ROLE, owner);
    }

    function mint(MintRequest calldata request) external {
        address signer = _verify(request);
        require(hasRole(MINTER_ROLE, signer), "Signature invalid or unauthorized");
        // ensure nonce not already consumed
        require(!nonces[request.nonce], "Nonce consumed");
        nonces[request.nonce] = true;
        // mint Emotes
        emote.mint(request.to, request.tokenId, request.quantity, "");
    }

    bytes32 private constant MINT_REQUEST_TYPE_HASH =
        keccak256("MintRequest(address to,uint256 tokenId,uint256 quantity,uint256 nonce)");

    function _hash(MintRequest calldata request) internal view returns (bytes32) {
        return
            _hashTypedDataV4(
                keccak256(
                    abi.encode(MINT_REQUEST_TYPE_HASH, request.to, request.tokenId, request.quantity, request.nonce)
                )
            );
    }

    function _verify(MintRequest calldata request) internal view returns (address) {
        bytes32 digest = _hash(request);
        return ECDSA.recover(digest, request.signature);
    }
}
"
    }
  }
