    "contracts/ZoltTokenDistributor.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol"; // Import SafeMath
import "./Verifier.sol";  // Import the Verifier contract

contract ZoltTokenDistributor is ReentrancyGuard, Ownable {
    using SafeMath for uint256; // Use SafeMath for uint256

    IERC20 public token;
    Verifier public verifier;  // Instance of the Verifier contract
    uint256 public startTime;
    uint256 public initialPercentage;
    uint256 public duration;
    uint256 public vestingPeriod;

    mapping(address => uint256) public lastClaimedAmt;
    event Claimed(address indexed user, uint256 amount);
    event VerifierUpdated(address indexed oldVerifier, address indexed newVerifier);


    constructor(
        address _token, 
        address _verifier,  // Address of the deployed Verifier contract
        uint256 _startTime, 
        uint256 _initialPercentage, 
        uint256 _duration, 
        uint256 _vestingPeriod
    ) {
        require(_initialPercentage <= 100, "Initial percentage must be less than or equal to 100");
        token = IERC20(_token);
        verifier = Verifier(_verifier);  // Initialize the verifier instance
        startTime = _startTime;
        initialPercentage = _initialPercentage;
        duration = _duration;
        vestingPeriod = _vestingPeriod;
    }

    function withdrawTokens(uint256 amount) external onlyOwner {
        token.transfer(msg.sender, amount);
    }

    function withdrawETH() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    receive() external payable {}

    function setStartTime(uint256 _newStartTime) external onlyOwner {
        startTime = _newStartTime;
    }

    function setTokenAddress(address _newTokenAddress) external onlyOwner {
        token = IERC20(_newTokenAddress);
    }

    function setVerifier(address _newVerifier) external onlyOwner {
        address oldVerifier = address(verifier);
        verifier = Verifier(_newVerifier);
        emit VerifierUpdated(oldVerifier, _newVerifier);
    }

    function claim(uint256 totalAmount, bytes32[] calldata merkleProof) external nonReentrant {
        require(block.timestamp > startTime, "Vesting hasn't started yet");

        // Use the Verifier contract for Merkle root verification
        verifier.verify(merkleProof, msg.sender, totalAmount);

        (uint256 claimable, uint256 ignore) = calculateClaimableAmount(msg.sender, totalAmount);

        require(claimable > 0, "No tokens available for claim");
        require(claimable <= totalAmount, "Claim exceeds total allocated amount");

        lastClaimedAmt[msg.sender] = lastClaimedAmt[msg.sender].add(claimable);
        token.transfer(msg.sender, claimable);
        emit Claimed(msg.sender, claimable);
    }

    function calculateClaimableAmount(address user, uint256 totalAmount) public view returns (uint256 amt1, uint256 amt2) {
        if (block.timestamp < startTime) {
            return (0,0);
        }

        uint256 claimableAmount = 0;
        uint256 initialAmount = 0;


        initialAmount = totalAmount.mul(initialPercentage).div(100);

        (uint256 totalPeriods, uint256 maxPeriods) = _getTotalPeriodsSinceStart();
        claimableAmount = initialAmount.add((totalAmount.sub(initialAmount)).mul(totalPeriods).div(maxPeriods));
        claimableAmount = claimableAmount.sub(lastClaimedAmt[user]);

        return (claimableAmount, lastClaimedAmt[user]);
    }

    function _getTotalPeriodsSinceStart() internal view returns (uint256 totalPeriods, uint256 maxPeriods) {
        uint256 elapsedTimeSinceStart  = block.timestamp.sub(startTime);
        uint256 maxPossiblePeriods = duration.div(vestingPeriod);
        uint256 totalPeriodsSinceStart = elapsedTimeSinceStart.div(vestingPeriod);

        if (totalPeriodsSinceStart > maxPossiblePeriods) {
            totalPeriodsSinceStart = maxPossiblePeriods;
        }

        return (totalPeriodsSinceStart, maxPossiblePeriods);

    }

    function timeForNextGlobalClaim() public view returns (uint256) {
        if (block.timestamp < startTime) {
            return startTime; // If the vesting hasn't started, the next claim time is the start time
        }

        uint256 elapsedTimeSinceStart = block.timestamp.sub(startTime);
        uint256 remainingTimeForNextPeriod = vestingPeriod.sub(elapsedTimeSinceStart % vestingPeriod);

        return block.timestamp.add(remainingTimeForNextPeriod);
    }

}
"
