    "contracts/Verifier.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract Verifier is Ownable {
    bytes32 private root;

    event RootSet(bytes32 root);

    constructor(bytes32 _root) {
        root = _root;
        emit RootSet(root);
    }

    function verify(bytes32[] memory proof, address addr, uint256 amount) public view {
        bytes32 leaf = keccak256(bytes.concat(keccak256(abi.encode(addr, amount))));
        bool isVerified = MerkleProof.verify(proof, root, leaf);
        require(isVerified, "Invalid proof");
    }

    function getRoot() public view returns (bytes32) {
        return root;
    }

    function setRoot(bytes32 _newRoot) public onlyOwner {
        root = _newRoot;
        emit RootSet(root);
    }
}
"
