pragma solidity ^0.8.19;

// v5
library HelperAutoPOOL {



    struct CONFIG {
        uint32 restrictCurrBlock;
        uint32 blockDelayQty;
        uint32 blockDelayQtyDistr;
        uint32[] probRates; 
        uint32 affiliateRate; //0...10000
        uint256 weiAmount;//ETH
        uint256 baseLimit;//END
    }




    struct CFG_RATE_END {
        uint32 nextFundRate; //0...10000        
        uint32 jpRate; //0...10000        
        uint32 minRate; //0...10000        
        uint32 loanEndRate; //0...10000
        uint32 loanEndLottoRate; //0...10000
        uint32 edyEndLottoRate; //0...10000
    }


    struct CURRENT {
        address last_account;
        uint256 initBlock;
        uint256 initBlockDistr;
        uint256 currentPoolAmount;

    }

    struct EXERNAL {
        address payable distributor;
        address payable operation;
        address jackpot;
        address loanEnd;
        address edyEnd;
    }


    struct DISTR_END {
        uint256 jackpot;
        uint256 min;
        uint256 loanEnd;
        uint256 reward;
        uint256 nextFund;
        uint256 base;
        uint256 loanEndLotto;
        uint256 edyEndLotto;
    }


    struct STATUS {
            State status;
            address lastAccount;            
            uint256 blLeft;
            uint256 blLeftDistr;
            uint256 reward;
            uint256 nextFund;
    }

    struct PREVSTATUS {
            address lastAccount;
            uint256 reward;
            uint256 nextFund;
            uint256 loanEndLotto;
            uint256 edyEndLotto;
    }



    enum State {
        ACTIVE,
        CLOSED_WINNER,
        CLOSED_DISTRIBUTION,
        CLOSED_NOWINNERS
    }


    function getRemainingBlocks(
        CONFIG memory config,
        CURRENT memory current,
        uint256 blockNumber
        ) internal pure returns (uint256 blockLeft, uint256 blockLeftDistr) {
        if (blockNumber > current.initBlock) {
            if (config.blockDelayQty > (blockNumber - current.initBlock)  ) {
                blockLeft = config.blockDelayQty - (blockNumber - current.initBlock);
            } else {
                blockLeft = 0;
            }
        } else {
            blockLeft = config.blockDelayQty +  (current.initBlock - blockNumber);
        }

        if (blockNumber > current.initBlockDistr) {

            if (config.blockDelayQtyDistr > (blockNumber - current.initBlockDistr) ) {
                blockLeftDistr = config.blockDelayQtyDistr - (blockNumber - current.initBlockDistr);
            } else {
                blockLeftDistr = 0;
            }
        } else {
            blockLeftDistr = config.blockDelayQtyDistr + (current.initBlockDistr - blockNumber);
        }
    }



    function getStatus(
        CONFIG memory config,
        CURRENT memory current,
        uint256 blockNumber
        ) internal pure returns (State) {
        (uint256 blLeft, uint256 blLeftDistr) = getRemainingBlocks(
            config,
            current,
            blockNumber
        );

        if (blLeft == 0) {
            if (current.last_account == payable(address(0))) {
                return State.CLOSED_NOWINNERS;
            }

            if (
                (current.initBlockDistr + config.blockDelayQtyDistr) <
                (current.initBlock + config.blockDelayQty)
            ) {
                return State.CLOSED_DISTRIBUTION;
            }

            return State.CLOSED_WINNER;
        }

        if (blLeftDistr == 0) {
            if (current.last_account == payable(address(0))) {
                return State.CLOSED_NOWINNERS;
            }

            return State.CLOSED_DISTRIBUTION;
        }

        return State.ACTIVE;
    }






    function rnd(uint max,bytes32 seed) internal view returns (uint256) {

        return uint256(keccak256(abi.encodePacked(block.number,  block.prevrandao, block.timestamp,seed )))%max;
    }



    function calcAffiliateAmount(uint32 rate, uint256 deposit)
        internal
        pure
        returns (uint256 amount)  {

        amount =  (deposit * rate)/10000;
        
    }





    function splitBaseEND(CFG_RATE_END memory config, uint256 base)
        internal
        pure
        returns (
            uint256 reward,
            uint256 nextFund
            )
      {

        nextFund = (base * config.nextFundRate) / 10000;
        reward = base - nextFund;
    }


    function splitDistributionEND(CFG_RATE_END memory config, uint256 deposit)
        internal
        pure
        returns (DISTR_END memory distr)  {

        distr.jackpot = (deposit * config.jpRate)/10000;   
        distr.min = (deposit * config.minRate)/10000;   
        distr.loanEnd = (deposit * config.loanEndRate)/10000;   

        //distr.jackpot  or  distr.min + (distr.jackpot - distr.min) = distr.jackpot
        //remainder va al bote del autopool
        distr.base = deposit - (distr.jackpot + distr.loanEnd) ;

        (distr.reward,distr.nextFund)=splitBaseEND(config, distr.base);

        distr.loanEndLotto = (distr.reward * config.loanEndLottoRate)/10000;   
        distr.edyEndLotto = (distr.reward * config.edyEndLottoRate)/10000;   

    }




    function calculateRewardDistrEND(CFG_RATE_END memory config, uint256 deposit)
        internal
        pure
        returns (DISTR_END memory distr)
        {
        DISTR_END memory distr1 = splitDistributionEND(config, deposit);

        distr.jackpot = distr1.jackpot;
        distr.min = distr1.min;
        distr.loanEnd = distr1.loanEnd;
        distr.nextFund = distr1.nextFund;
        distr.reward = distr1.reward;
        distr.base = distr1.base;

    }




    function getCurrentValuesEND(CONFIG memory config,CFG_RATE_END memory cfg_rate,CURRENT memory current,uint256 blockNumber)
        internal
        pure
        returns (
            STATUS memory st
        )
        {
        (st.blLeft, st.blLeftDistr) = getRemainingBlocks(
            config,
            current,
            blockNumber
        );
        st.lastAccount = current.last_account;
        st.status = getStatus(config, current, blockNumber);

        ( st.reward, st.nextFund)=splitBaseEND(cfg_rate, current.currentPoolAmount);

        if (st.status != State.ACTIVE) {
            st.blLeft = config.blockDelayQty;
            st.blLeftDistr = config.blockDelayQtyDistr;
            st.lastAccount = address(0);
            ( st.reward, st.nextFund)=splitBaseEND(cfg_rate, st.nextFund);
        }
        

    }

}





