pragma solidity ^0.8.19;

// v5
contract AutoPOOL is Pausable, AccessControl {


    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
 

    IERC_20 public endtoken;
    IAffiliate public affiliates;
    HelperAutoPOOL.CONFIG public config;
    HelperAutoPOOL.CFG_RATE_END public cfg_rate_end;
    HelperAutoPOOL.CURRENT public current;
    HelperAutoPOOL.EXERNAL public extern;


    mapping(address => uint256) private _pendingAwards;
    mapping(address => uint256) private _lastBlock;


    event NewDeposit(
        address indexed account,
        uint256 amount,
        address indexed affiliator,
        uint256 feeAfiliate,
        uint256 deposit
    );
    event Withdrawed(address indexed account, uint256 coins);
    event Awarded(
        address indexed account,
        uint256 coins,
        uint32 reward,
        uint32 state
    );

    event StateChanged( address indexed poolID, uint256 blockNumber,uint32 status);


    constructor(
        uint32 initBlockDelayQty,
        uint32 initBlockDelayQtyDistr,
        uint256 _weiAmount,
        uint32[] memory probRates,
        IERC_20 end_token,
        IAffiliate _affiliate

        ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        affiliates = (IAffiliate)(_affiliate);
        endtoken = (IERC_20)(end_token);
        config.blockDelayQty = initBlockDelayQty;
        config.blockDelayQtyDistr = initBlockDelayQtyDistr;
        config.affiliateRate =500; //5%
        config.probRates =probRates;

        //delivery end
        cfg_rate_end.jpRate = 5000; //50 %
        cfg_rate_end.loanEndRate = 1000; //10 %
        //remainder 40 % to autoPOOL price
        //split lotto mode (40%) no 1 winner
        cfg_rate_end.nextFundRate = 1000; // 10%
        cfg_rate_end.loanEndLottoRate = 4500; // 45%
        cfg_rate_end.edyEndLottoRate = 4500; // 45%
        
        cfg_rate_end.minRate = 1000; //10 %

        config.weiAmount = _weiAmount; 
        config.baseLimit = 4000000000000000000; 

        current.initBlock = block.number;
        current.initBlockDistr = block.number;



    }


    function initAddress(
        address payable _distributor,
        address payable _operation,
        address _jackpot,
        address _loanEnd,
        address  _edyEND
        )
        external
        onlyRole(ADMIN_ROLE)
        {

        extern.jackpot =_jackpot;
        extern.loanEnd = _loanEnd;
        extern.edyEnd = _edyEND;
        extern.distributor = _distributor;
        extern.operation = _operation;

    }






    // This function is called for all messages sent to
    // this contract, except plain Ether transfers
    // (there is no other function except the receive function).
    // Any call with non-empty calldata to this contract will execute
    // the fallback function (even if Ether is sent along with the call).
    fallback() external payable {}

    // This function is called for plain Ether transfers, i.e.
    // for every call with empty calldata.
    receive() external payable {


        _transfer(extern.operation, msg.value);
    }


    function receiveFromContract() public payable {
        update(block.number);        
        
        HelperAutoPOOL.DISTR_END memory distrEnd = HelperAutoPOOL.calculateRewardDistrEND(
            cfg_rate_end, msg.value
        );
        current.currentPoolAmount += distrEnd.base  ;


        
        (IDISTRIBUTORETH)(extern.distributor).distribute{value: msg.value}(extern.operation, config.affiliateRate );

        (IJACKPOOLEND)(extern.jackpot).updateFromContract(distrEnd.jackpot);
        (IUPDATETK)(extern.loanEnd).updateFromContract(distrEnd.loanEnd);
    }


    function updateFromContract(uint256 amount) external onlyRole(ADMIN_ROLE) {
        current.currentPoolAmount += amount;
    }

    function getProbRates() external view returns(uint32[]memory){
        return config.probRates;
    }



    function changeWeiAmount(uint256 newWeiAmount)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newWeiAmount > 0, "AutoPOOL: new amount must be greater than 0");
        config.weiAmount = newWeiAmount;

    }

    function changeBaseLimit(uint256 newlimit)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newlimit > 0, "AutoPOOL: new amount must be greater than 0");
        config.baseLimit = newlimit;
    }


    function changeProbRates(uint32[] memory newrates)
        external
        onlyRole(ADMIN_ROLE)
        {

            require(newrates.length == 6, "AutoPOOL: invalid newrates length" );

            for(uint i;i<newrates.length;i++){
                require(newrates[i] > 1 &&  newrates[i] <= 1000000, "AutoPOOL: Invalid newrate value");
            }

        config.probRates = newrates;
    }


    function setRestrictCurrBlock(bool enabled)
        external
        onlyRole(ADMIN_ROLE)
        {

        config.restrictCurrBlock = enabled ? 1 : 0;
    }


    function changeBlockDelayQty(uint32 newBlockDelayQty)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(
            newBlockDelayQty > 0,
            "AutoPOOL: new quantity must be greater than 0"
        );

        config.blockDelayQty = newBlockDelayQty;
    }

    function changeBlockDelayQtyDistr(uint32 newBlockDelayQty)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(
            newBlockDelayQty > 0,
            "AutoPOOL: new quantity must be greater than 0"
        );

        require(newBlockDelayQty > config.blockDelayQty,"AutoPOOL: new quantity must be greater than blockDelayQty" );
        config.blockDelayQtyDistr = newBlockDelayQty;
    }

    function changeOperation(address payable newOper)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newOper != address(0), "AutoPOOL: Invalid address");
        extern.operation = newOper;
   
    }



    function changeAffiliate(address  newafil)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newafil != address(0), "AutoPOOL: Invalid address");

        affiliates = (IAffiliate)(newafil);
    }

    function changeJackPOOL(address newJP)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newJP != address(0), "AutoPOOL: Invalid address");

        extern.jackpot = newJP;
   
    }


    function changeLoanEnd(address newLo)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newLo != address(0), "AutoPOOL: Invalid address");

        extern.loanEnd = newLo;
   
    }



    function changeEndegyEND(address newst)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newst != address(0), "AutoPOOL: Invalid address");

        extern.edyEnd = newst;
   
    }

    function changeDistributorETH(address payable newdistr)
        external
        onlyRole(ADMIN_ROLE)
        {
        require(newdistr != address(0), "AutoPOOL: Invalid address");

        extern.distributor = newdistr;
   
    }





    function changeAffiliateRateETH(
        uint32 affiliateRate //0...10000
        )
        external
        onlyRole(ADMIN_ROLE)
        {
        require(affiliateRate <= 10000, "AutoPOOL: Invalid affiliateRate");


        config.affiliateRate = affiliateRate;
    }


    function changeDistrRateEND(
        uint32 nextFundRate, //0...10000        
        uint32 jpRate, //0...10000        
        uint32 minRate, //0...10000        
        uint32 loanEndRate, //0...10000
        uint32 loanEndLottoRate, //0...10000
        uint32 edyEndLottoRate //0...10000
        )
        external
        onlyRole(ADMIN_ROLE)
        {
        require(nextFundRate <= 10000, "AutoPOOL: Invalid nextFundRate");
        require(jpRate <= 10000, "AutoPOOL: Invalid jpRate");
        require(minRate <= 10000, "AutoPOOL: Invalid minRate");
        require(loanEndRate <= 10000, "AutoPOOL: Invalid loanEndRate");
        require(loanEndLottoRate <= 10000, "AutoPOOL: Invalid loanEndLottoRate");
        require(edyEndLottoRate <= 10000, "AutoPOOL: Invalid edyEndLottoRate");


        uint totRate= jpRate + loanEndRate;
        require( totRate <= 10000, "AutoPOOL: sum exceeds 100% ");

        require(minRate <= jpRate, "AutoPOOL: Invalid minRate > jpRate");

        totRate = nextFundRate + loanEndLottoRate + edyEndLottoRate;

        require( totRate <= 10000, "AutoPOOL: sum lotto mode exceeds 100% ");

        cfg_rate_end.nextFundRate = nextFundRate;
        cfg_rate_end.jpRate = jpRate;
        cfg_rate_end.minRate = minRate;
        cfg_rate_end.loanEndRate = loanEndRate;
        cfg_rate_end.loanEndLottoRate = loanEndLottoRate;
        cfg_rate_end.edyEndLottoRate = edyEndLottoRate;

    }





    function withdraw() external whenNotPaused {
        update(block.number);

        require(_pendingAwards[_msgSender()] > 0, "AutoPOOL: no pending awards!");

        uint256 aw = _pendingAwards[_msgSender()];

        _pendingAwards[_msgSender()] = 0;

        if (aw > 0) {
            endtoken.mint(_msgSender(), aw);
        }

        emit Withdrawed(_msgSender(), aw);
    }



    function getPendingAwards(address account)
        external
        view
        returns (uint256 balance)
        {
        balance = _pendingAwards[account];
    }



    function getCurrentValues()
        external
        view
        returns (
            HelperAutoPOOL.STATUS memory st
        )

        {

        (st)=HelperAutoPOOL.getCurrentValuesEND( config, cfg_rate_end, current,block.number);

    }


    function getPreviousRound()
        public 
        view
        returns (
            HelperAutoPOOL.PREVSTATUS memory st
        )
        {

        st.lastAccount = current.last_account;
        ( st.reward, st.nextFund)=HelperAutoPOOL.splitBaseEND(cfg_rate_end, current.currentPoolAmount);
        st.loanEndLotto = (st.reward * cfg_rate_end.loanEndLottoRate)/10000;   
        st.edyEndLotto = (st.reward * cfg_rate_end.edyEndLottoRate)/10000;   

    }

    function updatePool() external {
        update(block.number);
        
    }




    function update(uint256 blockNumber) internal {

        HelperAutoPOOL.State st = HelperAutoPOOL.getStatus(config, current, blockNumber);

        if (st == HelperAutoPOOL.State.ACTIVE) {
            return;
        }

       (HelperAutoPOOL.PREVSTATUS memory pst)=getPreviousRound();

        if (st == HelperAutoPOOL.State.CLOSED_WINNER) {
            _pendingAwards[pst.lastAccount] +=  pst.reward;
            emit Awarded(pst.lastAccount, pst.reward, 0, (uint32)(st));
        }

        if (st == HelperAutoPOOL.State.CLOSED_DISTRIBUTION) {


            (IUPDATETK)(extern.edyEnd).updateFromContract(pst.edyEndLotto);
            (IUPDATETK)(extern.loanEnd).updateFromContract(pst.loanEndLotto);

        }


        current.last_account = address(0);


        current.initBlockDistr = blockNumber;

        current.initBlock = blockNumber;

        emit StateChanged( address(this), block.number,(uint32)(st));
        
        if (st == HelperAutoPOOL.State.CLOSED_NOWINNERS) {
            return;
        }

        current.currentPoolAmount = pst.nextFund ;


    }


    function playAutoPOOL(address account , uint256 blockNmber) internal  {

        current.initBlock = blockNmber;
        if (current.initBlockDistr == 0) {
            current.initBlockDistr = blockNmber;
        }

        current.last_account = account;
    }



    function isLocked() public view returns(bool){

        HelperAutoPOOL.State status = HelperAutoPOOL.getStatus(config, current, block.number);

        ( uint256 net, uint256  nextFund)=HelperAutoPOOL.splitBaseEND(cfg_rate_end, current.currentPoolAmount);

        uint256 base =config.baseLimit;

        if (status == HelperAutoPOOL.State.ACTIVE || status == HelperAutoPOOL.State.CLOSED_NOWINNERS) {
            return base > net ? true:false;

        }else{
            return base > nextFund ? true:false;
        }
    }




    function deposit() external payable whenNotPaused {

        require(!Address.isContract(msg.sender), "AutoPOOL: Invalid sender Address"  );

        require( msg.value >= config.weiAmount , "AutoPOOL: amount must be greater than weiAmount  "  );


        if(config.restrictCurrBlock ==1 ){
            require( _lastBlock[_msgSender()] != block.number , "AutoPOOL: restricted to 1 block per deposit  "  );
        }
        
        
        update(block.number);

        _lastBlock[_msgSender()] = block.number;
        
        HelperAutoPOOL.DISTR_END memory distrEnd = HelperAutoPOOL.calculateRewardDistrEND(
            cfg_rate_end, msg.value
        );

        current.currentPoolAmount += distrEnd.base  ;

        if(!isLocked()){
            playAutoPOOL(_msgSender() , block.number);
        }


        (IUPDATETK)(extern.loanEnd).updateFromContract(distrEnd.loanEnd);

        (,bool win)=(IJACKPOOLEND)(extern.jackpot).playExtProb(_msgSender(),config.probRates,distrEnd.min);

        if(win==false){
            //no jackPOOL win
            //min minimun end
            endtoken.mint(_msgSender(), distrEnd.min);
            // send remainder to jackPOOL
            (IJACKPOOLEND)(extern.jackpot).updateFromContract( distrEnd.jackpot - distrEnd.min );

        }else{
            (IJACKPOOLEND)(extern.jackpot).updateFromContract(distrEnd.jackpot);
        }
        

        if (!affiliates.isAffiliator(_msgSender())) {
            affiliates.setAffiliator(_msgSender(), true);
        }

        address affiliator = affiliates.getAffiliator(_msgSender());

        if (affiliator == address(0)) {
            (IDISTRIBUTORETH)(extern.distributor).distribute{value: msg.value}(extern.operation, config.affiliateRate );

        } else {
            (IDISTRIBUTORETH)(extern.distributor).distribute{value: msg.value}(payable(affiliator), config.affiliateRate );
        }
       



        emit NewDeposit(
            _msgSender(),
            distrEnd.base,
            affiliator,
            HelperAutoPOOL.calcAffiliateAmount(config.affiliateRate, msg.value),
            msg.value
        );

    }











    function _transfer(address payable to, uint256 amount) internal {
        if (amount > 0) {
            if (Address.isContract(to)) {
                (ITRANSFER)(to).receiveFromContract{value: amount}();
            } else {
                (bool sent, ) = to.call{value: amount}("");
                require(sent, "AutoPOOL:Failed to send Ether");

            }

        }

    }




    function pause() public onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    
    function emergencyWithdraw(address destination,uint256 amount) external onlyRole(ADMIN_ROLE){
        payable(destination).transfer(amount);
    }

}
