pragma solidity ^0.8.0;

contract Pizza is ERC20, ERC20Burnable, Ownable {
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableMap for EnumerableMap.UintToAddressMap;

    EnumerableSet.AddressSet private _controllers;

    uint256 private _totalSupply;
    uint256 private MAXSUP;
    uint256 constant MAXIMUMSUPPLY = 10000000000*10**18;
    uint256 public percentage;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _excludedFromFee;
    mapping (address => bool) private _isExcludedFromFee;

    address private PizzaHut;
    address private StakingRewardsWallet;
    address private BurnWallet;

    constructor() ERC20("Pizza", "PZZA") {}

    function mint(address to, uint256 amount) external {
        require(_controllers.contains(msg.sender) || msg.sender == owner(), "Only controllers and owner can mint");
        require((MAXSUP+amount)<=MAXIMUMSUPPLY,"Maximum supply has been reached");
        _totalSupply = _totalSupply.add(amount);
        MAXSUP=MAXSUP.add(amount);
        super._mint(to, amount);
    }

    function addController(address controller) external onlyOwner {
        _controllers.add(controller);
    }

    function removeController(address controller) external onlyOwner {
        _controllers.remove(controller);
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function maxSupply() public pure returns (uint256) {
        return MAXIMUMSUPPLY;
    }

    function _calculateFee(uint256 amount) private view returns (uint256) {
        return amount.mul(percentage).div(10000);
    }

    function _calculateSplitFee(uint256 fee) private pure returns (uint256) {
        return fee.div(3);
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        address sender = _msgSender();
        if (sender != address(0) && (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient])) {
             _transfer(sender, recipient, amount);
        } else {
            uint256 fee = _calculateFee(amount);
            uint256 splitFee = _calculateSplitFee(fee);
            _transfer(sender, PizzaHut, splitFee);
            _transfer(sender, StakingRewardsWallet, splitFee);
            _transfer(sender, BurnWallet, splitFee);
            _transfer(sender, recipient, amount.sub(fee));
        }
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        if (sender != address(0) && (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient])) {
            _transfer(sender, recipient, amount);
        } else {
            uint256 fee = _calculateFee(amount);
            uint256 splitFee = _calculateSplitFee(fee);
            _approve(sender, _msgSender(), allowance(sender, _msgSender()).sub(amount, "ERC20: transfer amount exceeds allowance"));
            _transfer(sender, PizzaHut, splitFee);
            _transfer(sender, StakingRewardsWallet, splitFee);
            _transfer(sender, BurnWallet, splitFee);
            _transfer(sender, recipient, amount.sub(fee));
        }
        return true;
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function setPercentageFee(uint256 fee) external onlyOwner {
        percentage = fee;
    }

    function setPizzaHut(address wallet) external onlyOwner {
        PizzaHut = wallet;
    }

    function setStakingRewardsWallet(address wallet) external onlyOwner {
        StakingRewardsWallet = wallet;
    }

    function setBurnWallet(address wallet) external onlyOwner {
        BurnWallet = wallet;
    }

    function withdrawTokens(address token, address to, uint256 amount) external onlyOwner {
        IERC20(token).transfer(to, amount);
    }
    
}
