A lottery smart contract where users can buy lotto tickets and participate 
in an ERC20 Jackpot draw.
*/

pragma solidity 0.8.4;






contract OctoLotto is ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    struct RNumbers {
        uint256[] arrayNumbers;
    }

    mapping(uint256 => RNumbers) internal rNumbers;

    // Lottery Settings
    IERC20 paytoken;
    uint256 public currentLotteryId;
    uint256 public currentTicketId;
    uint256 public ticketPrice = 10 ether; // 10 tokens
    uint256 public serviceFee = 3000; // BASIS POINTS 3000 is 30%
    uint256 public fundJackpot = 200 ether; // 200 tokens
    uint256 public lotteryDuration = 1 days;
    uint256 public numberWinner;
    uint256 public totalTicketsPurchased;
    uint256 public totalTicketLimit = 100; // maximum number of tickets that can be purchased in a single lottery

    enum Status {
        Open,
        Close,
        Claimable
    }

    struct Lottery {
        Status status;
        uint256 startTime;
        uint256 endTime;
        uint256 firstTicketId;
        uint256 transferJackpot;
        uint256 lastTicketId;
        uint256[6] winningNumbers;
        uint256 totalPayout;
        uint256 commision;
        uint256 winnerCount;
    }

    struct Ticket {
        uint256 ticketId;
        address owner;
        uint256[6] chooseNumbers;
    }

    mapping(uint256 => Lottery) private _lotteries;
    mapping(uint256 => Ticket) private _tickets;
    mapping(address => mapping(uint256 => uint256[]))
        private _userTicketIdsPerLotteryId;
    mapping(address => mapping(uint256 => uint256)) public _winnersPerLotteryId;

    event LotteryWinnerNumber(
        uint256 indexed lotteryId,
        uint256[6] finalNumber,
        uint256 numberWinner,
        uint256 totalPayout,
        uint256 totalTicketsPurchased
    );

    event LotteryClose(uint256 indexed lotteryId, uint256 lastTicketId);

    event LotteryOpen(
        uint256 indexed lotteryId,
        uint256 startTime,
        uint256 endTime,
        uint256 ticketPrice,
        uint256 firstTicketId,
        uint256 transferJackpot,
        uint256 lastTicketId,
        uint256 totalPayout,
        uint256 totalTicketLimit
    );

    event TicketsPurchase(
        address indexed buyer,
        uint256 indexed lotteryId,
        uint256[6] chooseNumbers
    );

    constructor(IERC20 _paytoken) {
        paytoken = _paytoken;
    }

    function openLottery() external onlyOwner nonReentrant {
        totalTicketsPurchased = 0;
        currentLotteryId++;
        currentTicketId++;
        uint256 fund = (_lotteries[currentLotteryId].transferJackpot).add(
            fundJackpot
        );
        uint256 lastTicketId;
        _lotteries[currentLotteryId] = Lottery({
            status: Status.Open,
            startTime: block.timestamp,
            endTime: (block.timestamp).add(lotteryDuration),
            firstTicketId: currentTicketId,
            transferJackpot: fund,
            winningNumbers: [
                uint256(0),
                uint256(0),
                uint256(0),
                uint256(0),
                uint256(0),
                uint256(0)
            ],
            lastTicketId: currentTicketId,
            totalPayout: 0,
            commision: 0,
            winnerCount: 0
        });
        emit LotteryOpen(
            currentLotteryId,
            block.timestamp,
            _lotteries[currentLotteryId].endTime,
            ticketPrice,
            currentTicketId,
            _lotteries[currentLotteryId].transferJackpot,
            lastTicketId,
            _lotteries[currentLotteryId].totalPayout,
            totalTicketLimit
        );
    }

    function buyTickets(uint256[6] calldata numbers)
        public
        payable
        nonReentrant
    {
        uint256 walletBalance = paytoken.balanceOf(msg.sender);

        require(
            walletBalance >= ticketPrice,
            "Funds not available to complete transaction"
        );
        require(
            _lotteries[currentLotteryId].status == Status.Open,
            "Lottery is not opened"
        );
        require(
            totalTicketsPurchased < totalTicketLimit,
            "Total ticket purchase limit reached"
        );

        paytoken.transferFrom(address(msg.sender), address(this), ticketPrice);

        // Calculate Commision Fee
        uint256 commisionFee = (ticketPrice.mul(serviceFee)).div(10000);

        /**
         * Lets obtain the platform commision per ticket sale.
         * Lets assume:  Ticket Price is 10 ether,
         * Service Fee is 30% (or 3000) Find the variable at the beginning.
         *
         *  Formula is :
         *      ticket Price x serviceFee / 10000
         *
         *      10 x 3000 / 10000 = 3
         *
         *      3 is the token amount earned in the platform from each ticket sale.
         *
         */

        _lotteries[currentLotteryId].commision += commisionFee;
        uint256 netEarn = ticketPrice - commisionFee;
        _lotteries[currentLotteryId].transferJackpot += netEarn;

        /**
        Lets store each ticket number array referenced to the buyer's wallet.
        mapping(address => mapping(uint256 => uint256[])) private _userTicketIdsPerLotteryId;
        */
        _userTicketIdsPerLotteryId[msg.sender][currentLotteryId].push(
            currentTicketId
        );

        _tickets[currentTicketId] = Ticket({
            ticketId: currentTicketId,
            owner: msg.sender,
            chooseNumbers: numbers
        });
        currentTicketId++;
        _lotteries[currentLotteryId].lastTicketId = currentTicketId;
        totalTicketsPurchased++;
        emit TicketsPurchase(msg.sender, currentLotteryId, numbers);
    }

    function closeLottery() external onlyOwner {
        require(
            _lotteries[currentLotteryId].status == Status.Open,
            "Lottery is not opened"
        );
        require(
            block.timestamp > _lotteries[currentLotteryId].endTime,
            "Lottery is not over"
        );
        _lotteries[currentLotteryId].lastTicketId = currentTicketId;
        _lotteries[currentLotteryId].status = Status.Close;

        rNumbers[currentLotteryId].arrayNumbers = getRNumbers(20);

        emit LotteryClose(currentLotteryId, currentTicketId);
    }

    function drawNumbers() external onlyOwner nonReentrant {
        require(
            _lotteries[currentLotteryId].status == Status.Close,
            "Lottery is not closed"
        );
        uint256[] memory numArray = rNumbers[currentLotteryId].arrayNumbers;
        uint256 num1 = (numArray[0] % 20) + 1;
        uint256 num2 = (numArray[1] % 20) + 1;
        uint256 num3 = (numArray[2] % 20) + 1;
        uint256 num4 = (numArray[3] % 20) + 1;
        uint256 num5 = (numArray[4] % 20) + 1;
        uint256 num6 = (numArray[5] % 20) + 1;
        uint256[6] memory finalNumbers = [num1, num2, num3, num4, num5, num6];
        for (uint256 i = 0; i < finalNumbers.length; i++) {
            if (finalNumbers[i] == 0) {
                finalNumbers[i] = 1;
            }
        }
        _lotteries[currentLotteryId].winningNumbers = finalNumbers;
        _lotteries[currentLotteryId].totalPayout = _lotteries[currentLotteryId]
            .transferJackpot;
    }

    function sortArrays(uint256[6] memory numbers)
        internal
        pure
        returns (uint256[6] memory)
    {
        bool swapped;
        for (uint256 i = 1; i < numbers.length; i++) {
            swapped = false;
            for (uint256 j = 0; j < numbers.length - i; j++) {
                uint256 next = numbers[j + 1];
                uint256 actual = numbers[j];
                if (next < actual) {
                    numbers[j] = next;
                    numbers[j + 1] = actual;
                    swapped = true;
                }
            }
            if (!swapped) {
                return numbers;
            }
        }
        return numbers;
    }

    function countWinners() external onlyOwner {
        require(
            _lotteries[currentLotteryId].status == Status.Close,
            "Lottery not close"
        );
        require(
            _lotteries[currentLotteryId].status != Status.Claimable,
            "Lottery Already Counted"
        );

        delete numberWinner;

        uint256 firstTicketId = _lotteries[currentLotteryId].firstTicketId;
        uint256 lastTicketId = _lotteries[currentLotteryId].lastTicketId;

        uint256[6] memory winOrder;
        winOrder = sortArrays(_lotteries[currentLotteryId].winningNumbers);
        bytes32 encodeWin = keccak256(abi.encodePacked(winOrder));

        uint256 i = firstTicketId;

        uint256 _totalTicketsPurchased = lastTicketId - firstTicketId;

        for (i; i < lastTicketId; i++) {
            address buyer = _tickets[i].owner;
            require(buyer != address(0), "Zero address");
            uint256[6] memory userNum = _tickets[i].chooseNumbers;
            bytes32 encodeUser = keccak256(abi.encodePacked(userNum));
            if (encodeUser == encodeWin) {
                numberWinner++;
                _winnersPerLotteryId[buyer][currentLotteryId] = 1;
            }
        }
        if (numberWinner == 0) {
            uint256 nextLottoId = (currentLotteryId).add(1);
            _lotteries[nextLottoId].transferJackpot = _lotteries[
                currentLotteryId
            ].totalPayout;
        }

        _lotteries[currentLotteryId].winnerCount = numberWinner;
        _lotteries[currentLotteryId].status = Status.Claimable;

        emit LotteryWinnerNumber(
            currentLotteryId,
            _lotteries[currentLotteryId].winningNumbers,
            numberWinner,
            _lotteries[currentLotteryId].totalPayout,
            _totalTicketsPurchased
        );
    }

    function claimPrize(uint256 _lottoId) external nonReentrant {
        require(_lotteries[_lottoId].status == Status.Claimable, "Not Payable");
        require(_lotteries[_lottoId].winnerCount > 0, "Not Payable");
        require(_winnersPerLotteryId[msg.sender][_lottoId] == 1, "Not Payable");
        uint256 winners = _lotteries[_lottoId].winnerCount;
        uint256 payout = (_lotteries[_lottoId].totalPayout).div(winners);
        paytoken.safeTransfer(msg.sender, payout);
        _winnersPerLotteryId[msg.sender][_lottoId] = 0;
    }

    function viewTickets(uint256 ticketId)
        external
        view
        returns (address, uint256[6] memory)
    {
        address buyer;
        buyer = _tickets[ticketId].owner;
        uint256[6] memory numbers;
        numbers = _tickets[ticketId].chooseNumbers;
        return (buyer, numbers);
    }

    function viewLottery(uint256 _lotteryId)
        external
        view
        returns (Lottery memory)
    {
        return _lotteries[_lotteryId];
    }

    function getBalance() external view onlyOwner returns (uint256) {
        return paytoken.balanceOf(address(this));
    }

    function fundContract(uint256 amount) external onlyOwner {
        paytoken.safeTransferFrom(address(msg.sender), address(this), amount);
    }

    // Lottery additional helper functions
    function canClaimPrize(uint256 _lottoId) external view returns (bool) {
        if (_lotteries[_lottoId].status != Status.Claimable) {
            return false;
        }
        if (_lotteries[_lottoId].winnerCount == 0) {
            return false;
        }
        if (_winnersPerLotteryId[msg.sender][_lottoId] != 1) {
            return false;
        }
        return true;
    }

    function getRNumbers(uint256 modulus)
        internal
        view
        returns (uint256[6] memory)
    {
        uint256[6] memory localRNumbers;

        for (uint256 i = 0; i < 6; i++) {
            uint256 combinedHash = uint256(
                keccak256(
                    abi.encodePacked(
                        blockhash(block.number - 1 - i),
                        block.timestamp,
                        block.difficulty
                    )
                )
            );

            localRNumbers[i] = (combinedHash % modulus);
        }

        return localRNumbers;
    }

    function getUserTicketIdsPerLotteryId(address user, uint256 lotteryId)
        public
        view
        returns (uint256[] memory)
    {
        return _userTicketIdsPerLotteryId[user][lotteryId];
    }

    function withdraw() public onlyOwner {
        paytoken.safeTransfer(
            address(msg.sender),
            (paytoken.balanceOf(address(this)))
        );
    }

    // Lottery core variable change functions.
    function setPayToken(IERC20 _paytoken) public onlyOwner {
        paytoken = _paytoken;
    }

    function setTicketPrice(uint256 _ticketPrice) public onlyOwner {
        ticketPrice = _ticketPrice * 1e18;
    }

    function setServiceFee(uint256 _serviceFee) public onlyOwner {
        require(_serviceFee <= 10000, "Service fee cannot be more than 100%");
        serviceFee = _serviceFee;
    }

    function setLotteryDuration(uint256 _lotteryDuration) public onlyOwner {
        lotteryDuration = _lotteryDuration * 1 minutes; // 1 day = 1440 minutes
    }

    function setFundJackpot(uint256 _fundJackpot) public onlyOwner {
        fundJackpot = _fundJackpot * 1 ether;
    }

    function setTotalTicketLimit(uint256 _totalTicketLimit) public onlyOwner {
        totalTicketLimit = _totalTicketLimit;
    }
}
