      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@bitflips/common/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@bitflips/common/contracts/token/ERC1155/extensions/ERC1155AuthorizedAgent.sol";

contract DBCCollectiblesEditions is ERC1155Burnable, ERC1155AuthorizedAgent {
    address private constant BITFLIPS = 0x5db929ec2f27cFb6988cF739cae05E95cbD0feCd;
    address private constant A7X = 0x5f64884dfDf8455180De72A561E85068097916ed;

    string public name = "Deathbats Club Collectibles Editions";
    string public symbol = "DBC-CE";

    constructor(string memory _initialBaseURI)
    ERC1155Core(_initialBaseURI,
        OperatorFiltererConfiguration({
            registry : 0x000000000000AAeB6D7670E522A718067333cd4E,
            subscription : 0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6,
            filtered : true
        })
    ){
        setAuthor(BITFLIPS);
        addContributor(A7X);
    }

    function airdrop(address [] calldata holders, uint256 id) external onlyContributor {
        for (uint256 i = 0; i < holders.length; i++) {
            _mint(holders[i], id, 1, "");
        }
    }

    function redeem(uint256 tokenId) external {
        burn(_msgSender(), tokenId, 1);
    }

    function payoutContributors() external onlyContributor {
        uint256 balance = address(this).balance;
        _sendContributorPayment(author(), balance, 10);
        _sendContributorPayment(A7X, balance, 90);
    }

    function owner() public view override (ERC1155Core, ERC1155AuthorizedAgent) returns (address) {
        return ERC1155AuthorizedAgent.owner();
    }

    function isApprovedForAll(
        address ownerAddress,
        address operatorAddress
    ) public view override (ERC1155, ERC1155AuthorizedAgent) returns (bool isOperator) {
        return ERC1155AuthorizedAgent.isApprovedForAll(ownerAddress, operatorAddress);
    }
}
