    "contracts/EnterERC721.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol';
import '@openzeppelin/contracts/token/common/ERC2981.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Strings.sol';
import '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';

contract EnterERC721 is ERC721Enumerable, ERC2981, Ownable, AccessControl {
    using Strings for uint256;
    using ECDSA for bytes32;

    /**
     * @notice mapping with from tokenId => uri
     */
    mapping(uint256 => string) private fullURI;

    /**
     * @notice Mapping between an address(token owner) with a token id with a
     * nonce. It's used to prevent replaying a signature generated for a token
     * transfer
     */
    mapping(address => mapping(uint256 => uint256)) public transferNonces;

    /**
     * @notice baseURI which is applicable to all the minted tokens
     */
    string public baseURI;
    /**
     * @notice token id which is automatically incremented and starts from zero
     */
    uint256 public currentIndex;
    /**
     * @notice global toggle setting which determines whether tokens are transferable
     */
    uint256 public isTransferable;

    bytes32 private constant MINTER_ROLE = keccak256('MINTER_ROLE');
    bytes32 private constant TRANSFER_ROLE = keccak256('TRANSFER_ROLE');

    /* -------------------------------------------------------------------------- */
    /*                                   CONSTRUCTOR                              */
    /* -------------------------------------------------------------------------- */

    /**
     * @dev Initializes the contract
     * @param _name - Contract name
     * @param _symbol - Contract symbol
     * @param _minter - Minter role address
     * @param _transferOperator - Transfer role address
     * @param _royaltyRecipient - Royalty receiver address of the token
     * @param _royaltyBps - Royalty BPS of the token
     */
    constructor(
        string memory _name,
        string memory _symbol,
        address _minter,
        address _transferOperator,
        address _royaltyRecipient,
        uint96 _royaltyBps
    ) ERC721(_name, _symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, _minter);
        _grantRole(TRANSFER_ROLE, _transferOperator);

        // setup default royalty settings specific to erc2981
        _setDefaultRoyalty(_royaltyRecipient, _royaltyBps);
        // all tokens are transferable by default
        isTransferable = 1;
    }

    /* -------------------------------------------------------------------------- */
    /*                                   Internal functions                        */
    /* -------------------------------------------------------------------------- */

    /**
     * @notice internal function which wraps safe mint, sets the token uri, and auto-increments the token id
     * @param _to - token receiver address
     * @param _tokenURI - new token URI
     */
    function _mintTo(address _to, string memory _tokenURI) internal {
        uint256 nextTokenId = _nextTokenIdToMint();
        _safeMint(_to, nextTokenId);
        _setTokenURI(nextTokenId, _tokenURI);

        currentIndex = _increment(nextTokenId);
    }

    /**
     * @dev efficiently increments an unsigned integer by skipping a few opcodes using an unchecked bracket
     * @param i - unsigned integer to be incremented by 1
     */
    function _increment(uint256 i) internal pure returns (uint256) {
        unchecked {
            return i + 1;
        }
    }

    /**
     * @notice returns the current index
     * @dev the function simply wraps the current index to avoid confusion in code clarity
     */
    function _nextTokenIdToMint() internal view returns (uint256) {
        return currentIndex;
    }

    /**
     * @notice internal function which requires the message sender to either be a default admin or a minter
     */
    function _requireMinterRole() internal view {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender) || hasRole(MINTER_ROLE, msg.sender),
            'Enter: Unauthorized to set URI'
        );
    }

    /**
     * @notice sets the token uri and requires the message sender to fulfill the internal minter role as well
     * as requires the token id, to which the uri is being set, to be already minted
     * @param _tokenId - token id
     * @param _tokenURI - new token URI
     */
    function _setTokenURI(uint256 _tokenId, string memory _tokenURI) internal {
        _requireMinterRole();
        _requireMinted(_tokenId);

        fullURI[_tokenId] = _tokenURI;
    }

    /**
     * @notice overrides the before token transfer hook from OpenZeppelin by extending it with a super call
     * @param from - token holder address
     * @param to - receiver address
     * @param tokenId - token id
     * @param batchSize - non-zero unsigned integer indicating batch size
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);

        if (from != address(0)) {
            require(isTransferable == 1, 'Enter: Non-transferable');
        }
    }

    /* -------------------------------------------------------------------------- */
    /*                                   External functions                       */
    /* -------------------------------------------------------------------------- */

    /**
     * @notice function which is only accessible to minter roles and is responsible for the
     * creation of new erc721 tokens
     * @param _to - token receiver address
     * @param _tokenURI - new token URI
     */
    function mintTo(address _to, string memory _tokenURI) external onlyRole(MINTER_ROLE) {
        _mintTo(_to, _tokenURI);
    }

    /**
     * @notice function which is only accessible to minter roles and is responsible for the
     * creation of new erc721 tokens in batch amounts
     * @param _to - token receiver address
     * @param _quantity - quantity of tokens to be minted
     * @param _tokenURI - new token URI
     */
    function batchMintTo(
        address _to,
        uint256 _quantity,
        string memory _tokenURI
    ) external onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < _quantity; i = _increment(i)) {
            _mintTo(_to, _tokenURI);
        }
    }

    /**
     * @notice function responsible for setting a new tokenURI
     * @param _tokenId - token id
     * @param _tokenURI - new token URI
     */
    function setTokenURI(
        uint256 _tokenId,
        string memory _tokenURI
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _setTokenURI(_tokenId, _tokenURI);
    }

    /**
     * @notice function responsible for setting new tokenURIs for tokenIds.
     * the elements of both _tokenIds and _tokenURIs need to match by index.
     * that means element from index 0 from _tokenURIs array is assigned to element
     * of index 0 from _tokenIds array
     * @param _tokenIds - token id
     * @param _tokenURIs - new token URI
     */
    function setTokensURIs(
        uint256[] memory _tokenIds,
        string[] memory _tokenURIs
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_tokenIds.length == _tokenURIs.length, 'Enter: Array sizes must match');

        for (uint256 i = 0; i < _tokenIds.length; i = _increment(i)) {
            _setTokenURI(_tokenIds[i], _tokenURIs[i]);
        }
    }

    /**
     * @notice function responsible for setting a new base URI.
     * It allows empty values in case we want to fetch the tokenURI back from the fullTokenURI mapping
     * @param _baseTokenURI - new baseURI
     */
    function setBaseTokenURI(string memory _baseTokenURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = _baseTokenURI;
    }

    /**
     * @notice Function which toggles whether a token is transferable between holders
     * toggling off (0 setting) does not affect minting
     * @param _isTransferable - is either a 0 or a 1
     */
    function setTransferable(uint256 _isTransferable) public onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_isTransferable == 0 || _isTransferable == 1, 'Enter: Invalid option');

        isTransferable = _isTransferable;
    }

    /**
     * @notice Function responsible for returning a tokenURI given a tokenId.
     * If baseURI is set, it concatenates the value from baseURI + tokenId.
     * Otherwise, it gets the value from fullTokenURI
     * @param _tokenId - token id
     */
    function tokenURI(uint256 _tokenId) public view override returns (string memory) {
        _requireMinted(_tokenId);

        string memory _baseURI = baseURI;
        if (bytes(_baseURI).length > 0) {
            return string(abi.encodePacked(_baseURI, _tokenId.toString()));
        }

        string memory fullTokenURI = fullURI[_tokenId];
        return fullTokenURI;
    }

    /**
     * @notice Overrides the inherited ERC721Royalty and AccessControl contracts from OpenZeppelin
     * @param interfaceId The ID of an ERC standard to be tested
     */
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721Enumerable, ERC2981, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @notice Returns the hash to be signed and passed to the transferFromSignedMessageTo function
     * @param _sender The address that owns the token id to be sent
     * @param _tokenId The tokenId to be transferred
     * @return hash A bytes32 hash that is signed by the signVerifier
     * @dev transferFromSignedMessageTo uses this to get the expected hash to have been signed - hashes cannot be replayed since transferFromSignedMessageTo verifies using the current hash and increments the hash on a successful transfer.
     */
    function getTransferSigningHash(
        address _sender,
        uint256 _tokenId
    ) public view returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    _sender,
                    _tokenId,
                    transferNonces[_sender][_tokenId],
                    address(this)
                )
            );
    }

    /**
     * @notice Transfers a token from its owner to the recipient. It authorizes an address
     * that contains a transfer role to execute transfers of tokens signed by the token owner to anyone else
     * @param _sig The result of getTransferSigningHash signed by the token owner
     * @param _recipient The address that receives the token
     * @param _tokenId The tokenId being transferred
     * @dev ECDSA signatures are used to verify the permission to transfer a NFT. ECDSA.recover will revert if the signer (signVerifier) is the zero address.
     */
    function transferFromSignedMessageTo(
        bytes memory _sig,
        address _recipient,
        uint256 _tokenId
    ) public onlyRole(TRANSFER_ROLE) {
        address sender = ownerOf(_tokenId);
        require(sender != address(0), 'Enter: Cannot be address 0');

        bytes32 messageHash = getTransferSigningHash(sender, _tokenId).toEthSignedMessageHash();
        require(ECDSA.recover(messageHash, _sig) == sender, 'Enter: Signature is invalid');

        transferNonces[sender][_tokenId]++;

        _safeTransfer(sender, _recipient, _tokenId, '');
    }
}
