    struct StargateArgs {
        uint16 dstChainId;
        uint16 srcPoolId;
        uint16 dstPoolId;
        uint256 minAmountOut;
        IStargateRouter.lzTxObj lzTxObj;
        address receiver;
        bytes data;
    }

    struct Payload {
        address user;
        address swapRouter;
        bytes swapArguments;
        Types.ICall[] calls;
    }

    function swapERC20AndCall(
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        address user,
        address swapRouter,
        bytes calldata swapArguments,
        Types.ICall[] calldata calls
    ) external;

    function swapNativeAndCall(
        address tokenOut,
        address user,
        address swapRouter,
        bytes calldata swapArguments,
        Types.ICall[] calldata calls
    ) external payable;

    function swapERC20AndSend(
        uint amountIn,
        uint amountUSDC,
        address user,
        address tokenIn,
        address swapRouter,
        bytes calldata swapArguments,
        StargateArgs memory stargateArgs
    ) external payable;

    function swapNativeAndSend(
        uint amountIn,
        uint amountUSDC,
        uint lzFee,
        address user,
        address swapRouter,
        bytes calldata swapArguments,
        IMultichainPortal.StargateArgs memory stargateArgs
    ) external payable;
}
