        string memory name,
        uint256 nameLength,
        uint256 yearsPeriod,
        string memory tokenURI,
        address authorityAddress,
        bytes calldata signature
    ) internal virtual override returns (uint256) {
        if (!usedVIPMembership[to] && (brozoNFT.balanceOf(to) >= 25)) {
            usedVIPMembership[to] = true;
        }

        uint256 tokenId = SoulStore._mintSoulName(
            to,
            name,
            nameLength,
            yearsPeriod,
            tokenURI,
            authorityAddress,
            signature
        );

        return tokenId;
    }

