pragma solidity ^0.8.19;

contract ScaraboGhoul is ERC721, ERC721URIStorageA, Pausable, Ownable {

    using Strings for uint256;
    string baseURIInitial;
    string baseURITransformed;
    string baseExtension = ".json";
    uint8 isTokenTransferred;

    uint256 newTokenIdAugment;
    uint256 middleWhitelistVariable;
    uint256 upperWhitelistVariable;

    uint256[] private allTokenIds;
    mapping(uint256 => uint256) private allTokenIdIndex;
    mapping(address => uint256[]) private ownedTokens;
    mapping(uint256 => uint256) private ownedTokenIndex;
    mapping(uint256 => string) private shapeCodes;

    struct RoyaltyInfo {
        address royaltyHolder;
        uint96 royaltyPercent;
    }
    mapping(uint256 => RoyaltyInfo) private royaltyList;

    struct TokenOnSale {
        string tokenShapeCode;
        uint256 tokenValue;
    }
    uint256[] private onSaleTokenIds;
    mapping(uint256 => uint256) private onSaleTokenIdIndex;
    string[] private onSaleTokenStrings;
    mapping(string => uint256) private onSaleTokenStringIndex;
    mapping(uint256 => TokenOnSale) private tokenOnSaleList;
    mapping(uint256 => string[3]) private tokenOnSaleFlatList;

    mapping(address => uint256[]) private individualOnSaleList;
    mapping(uint256 => uint256) private individualOnSaleIndex;


    constructor() ERC721("ScaraboGhoul", "SGT") Ownable(_msgSender()) {
        baseURIInitial = "ipfs://bafybeic5ty2wjeerc5hasm3eh2jqx6avyomsgc2ojxtlfrnzmvdcdpowbe/";
        baseURITransformed = "ipfs://bafybeic5ifwu5u5scsgw6nop6h5bja3lccb4amy4ake3xnvj5drjbttegq/";
        isTokenTransferred = 0;
        newTokenIdAugment = 50000;
        middleWhitelistVariable = 5;
        upperWhitelistVariable = 10;
    }

    modifier checkAuthorized(address _owner) {
        require(_msgSender() == _owner || _msgSender() == owner(), "The requester is unauthorized to get this information");
        _;
    }

    modifier checkTokenIdExists(uint256 tokenId) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");
        _;
    }

    // Internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURIInitial;
    }

    function _baseURITransformed() internal view returns (string memory) {
        return baseURITransformed;
    }

    function transformedMint(address to, uint256 tokenId, string memory newShapeCode, uint256 numberOfOwnedTokens) internal {
        require(bytes(newShapeCode).length > 0, "New base URI cannot be an empty string");

        bytes memory breedingResult = bytes(newShapeCode);
        bytes memory subBreedingResult = new bytes(8);
        for (uint8 i = 8; i < breedingResult.length; i++) {
            subBreedingResult[i - 8] = breedingResult[i];
        }
        require(breedingResult[6] == "0", "This token has been transformed already");
        require(Strings.equal(string(subBreedingResult), "99999999") , "Shape code's press count result for this token is invalid, can not transform");

        uint256 newTokenId;
        unchecked {
            newTokenId = tokenId + newTokenIdAugment;
        }
        if (bytes(shapeCodes[newTokenId]).length > 0)
            delete shapeCodes[newTokenId];
        breedingResult[6] = "1";

        allTokenIdIndex[newTokenId] = allTokenIds.length;
        allTokenIds.push(newTokenId);
        ownedTokenIndex[newTokenId] = ownedTokens[to].length;
        ownedTokens[to].push(newTokenId);

        _safeMint(to, newTokenId);
        string memory transformedTokenURI = tokenURI(newTokenId);
        _setTokenURI(newTokenId, transformedTokenURI);

        // Enable benefits for users in whitelist
        uint96 royalityInPercent;
        address receiver;
        if (numberOfOwnedTokens >= 2 && numberOfOwnedTokens < middleWhitelistVariable) {
            breedingResult[7] = "0";
            royalityInPercent = 0;
            receiver = to;
        } else if (numberOfOwnedTokens >= middleWhitelistVariable && numberOfOwnedTokens < upperWhitelistVariable) {
            breedingResult[7] = "2";
            royalityInPercent = 200;
            receiver = to;
        } else if (numberOfOwnedTokens >= upperWhitelistVariable) {
            breedingResult[7] = "5";
            royalityInPercent = 500;
            receiver = to;
        } else {
            breedingResult[7] = "1";
            royalityInPercent = 100;
            receiver = owner();
        }

        setRoyaltyInfo(newTokenId, receiver, royalityInPercent);
        
        shapeCodes[newTokenId] = string(breedingResult);
    }

    function constructNewFlatToken(uint256 tokenId, string memory shapeCode, uint256 value) internal {
        string memory tokenString = Strings.toString(tokenId);
        string memory subTokenString;
        if (bytes(tokenString).length == 1)
            subTokenString = string.concat("0000", tokenString);
        else if (bytes(tokenString).length == 2)
            subTokenString = string.concat("000", tokenString);
        else if (bytes(tokenString).length == 3)
            subTokenString = string.concat("00", tokenString);
        else if (bytes(tokenString).length == 4)
            subTokenString = string.concat("0", tokenString);
        else
            subTokenString = tokenString;

        // On sale token only need number of feces from shape code
        bytes memory subShapeCode = new bytes(10);
        for (uint256 i = 0; i < 10; i++) {
            subShapeCode[i] = bytes(shapeCode)[6 + i];
        }

        // Construct value to have unit of GWei
        bytes memory valueString = bytes(Strings.toString(value));
        uint256 subValueLength = valueString.length - 9;
        bytes memory subValueString = new bytes(subValueLength);
        for (uint256 i = 0; i < subValueLength; i++) {
            subValueString[i] = valueString[i];
        }

        tokenOnSaleFlatList[tokenId][0] = subTokenString;
        tokenOnSaleFlatList[tokenId][1] = string(subShapeCode);
        tokenOnSaleFlatList[tokenId][2] = string(subValueString);
    }

    function checkValidShapeCodeId(uint256 tokenId, string memory shapeCode) internal view {
        bytes memory breedingResult = bytes(shapeCode);
        bytes memory defaultShapeCode = bytes(shapeCodes[tokenId]);
        require(defaultShapeCode.length == 16 && breedingResult.length == 16, "Shape code for this token has an invalid length");
        
        bytes memory subSentShapeCodeId = new bytes(8);
        for (uint8 i = 0; i < 8; i++) {
            subSentShapeCodeId[i] = breedingResult[i];
        }
        bytes memory subDefaultShapeCodeId = new bytes(8);
        for (uint8 i = 0; i < 8; i++) {
            subDefaultShapeCodeId[i] = defaultShapeCode[i];
        }
        require(Strings.equal(string(subSentShapeCodeId), string(subDefaultShapeCodeId)), "Shape code's Id for this token is invalid, can not update it");
    }

    function checkTransformLogic(uint256 tokenId, string memory shapeCode) internal view {
        bytes memory breedingResult = bytes(shapeCode);
        bytes memory defaultShapeCode = bytes(shapeCodes[tokenId]);
        bytes memory subBreedingResult = new bytes(8);
        bytes memory subDefaultShapeCode = new bytes(8);

        for (uint8 i = 8; i < 16; i++) {
            subBreedingResult[i - 8] = breedingResult[i];
            subDefaultShapeCode[i - 8] = defaultShapeCode[i];
        }

        uint subDefaultShapeCodeInInt = bytesToUint(subDefaultShapeCode);
        uint subBreedingResultInInt = bytesToUint(subBreedingResult);
        require(
            subDefaultShapeCodeInInt <= subBreedingResultInInt
            && breedingResult[6] == defaultShapeCode[6], 
            "Invalid transform logic code."
        );

        if (breedingResult[6] == "1") {
            require(keccak256(abi.encodePacked(string(subBreedingResult))) <= keccak256(abi.encodePacked("99999999")), "Invalid transformed token");
        }
    }

    function removeTokenInAll(uint256 tokenId) internal {
        uint256 oldIndexOfRemovedToken = allTokenIdIndex[tokenId];
        uint256 tokenIdOfLastIndex = allTokenIds[allTokenIds.length - 1];
        if (allTokenIds[oldIndexOfRemovedToken] != tokenIdOfLastIndex) {
            allTokenIds[oldIndexOfRemovedToken] = tokenIdOfLastIndex;
            allTokenIdIndex[tokenIdOfLastIndex] = oldIndexOfRemovedToken;
        }
        allTokenIds.pop();
        delete allTokenIdIndex[tokenId];
    }

    function removeOwnedToken(address tokenOwner, uint256 tokenId) internal {
        uint256 oldIndexOfRemovedToken = ownedTokenIndex[tokenId];
        uint256 tokenIdOfLastIndex = ownedTokens[tokenOwner][ownedTokens[tokenOwner].length - 1];
        if (ownedTokens[tokenOwner][oldIndexOfRemovedToken] != tokenIdOfLastIndex) {
            ownedTokens[tokenOwner][oldIndexOfRemovedToken] = tokenIdOfLastIndex;
            ownedTokenIndex[tokenIdOfLastIndex] = oldIndexOfRemovedToken;
        }
        ownedTokens[tokenOwner].pop();
        delete ownedTokenIndex[tokenId];
    }

    function removeOnSaleToken(uint256 tokenId) internal {
        // In general sale list
        string memory oldSaleTokenString = string.concat(tokenOnSaleFlatList[tokenId][0], tokenOnSaleFlatList[tokenId][1], tokenOnSaleFlatList[tokenId][2]);
        uint256 oldIndexOfRemovedTokenString = onSaleTokenStringIndex[oldSaleTokenString];
        string memory tokenStringOfLastIndex = onSaleTokenStrings[onSaleTokenStrings.length - 1];
        if (!Strings.equal(onSaleTokenStrings[oldIndexOfRemovedTokenString], tokenStringOfLastIndex)) {
            onSaleTokenStrings[oldIndexOfRemovedTokenString] = tokenStringOfLastIndex;
            onSaleTokenStringIndex[tokenStringOfLastIndex] = oldIndexOfRemovedTokenString;
        }

        uint256 oldIndexOfRemovedToken = onSaleTokenIdIndex[tokenId];
        uint256 tokenIdOfLastIndex = onSaleTokenIds[onSaleTokenIds.length - 1];
        if (onSaleTokenIds[oldIndexOfRemovedToken] != tokenIdOfLastIndex) {
            onSaleTokenIds[oldIndexOfRemovedToken] = tokenIdOfLastIndex;
            onSaleTokenIdIndex[tokenIdOfLastIndex] = oldIndexOfRemovedToken;
        }
        
        onSaleTokenStrings.pop();
        onSaleTokenIds.pop();
        delete onSaleTokenIdIndex[tokenId];

        // In individual sale list
        uint256 oldIndexOfTokenInIndivIndex = individualOnSaleIndex[tokenId];
        uint256 tokenIdOfLastIndexInIndivList = individualOnSaleList[ownerOf(tokenId)][individualOnSaleList[ownerOf(tokenId)].length - 1];
        if (tokenIdOfLastIndexInIndivList != tokenId) {
            individualOnSaleList[ownerOf(tokenId)][oldIndexOfTokenInIndivIndex] = tokenIdOfLastIndexInIndivList;
            individualOnSaleIndex[tokenIdOfLastIndexInIndivList] = oldIndexOfTokenInIndivIndex;
        }
        individualOnSaleList[ownerOf(tokenId)].pop();
        delete individualOnSaleIndex[tokenId];
    }

    function _burn(uint256 tokenId) internal virtual override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    function bytesToUint(bytes memory b) internal pure returns (uint256) {
        uint256 number;
        for(uint i = 0; i < b.length; i++){
            number = number + uint(uint8(b[i]))*(2**(8*(b.length - (i + 1))));
        }
        return number;
    }

    function setRoyaltyInfo(uint256 tokenId, address receiver, uint96 feeNumerator) internal {
        royaltyList[tokenId] = RoyaltyInfo(receiver, feeNumerator);
    }

    function royaltyCalculation(uint256 tokenId, uint256 salePrice) internal view returns (address, uint256) {
        RoyaltyInfo memory royalty = royaltyList[tokenId];
        uint256 royaltyAmount = (salePrice * royalty.royaltyPercent) / 10000;
        return (royalty.royaltyHolder, royaltyAmount);
    }

    // Public
    function walletOfOwner(address _owner) public view checkAuthorized(_owner) returns (uint256[] memory) {
        return ownedTokens[_owner];
    }

    function getNewTokenInfo(uint256 tokenId) public view checkAuthorized(ownerOf(tokenId)) returns (string memory) {
        string memory tokenString = Strings.toString(tokenId);
        string memory subTokenString;
        if (bytes(tokenString).length == 1)
            subTokenString = string.concat("0000", tokenString);
        else if (bytes(tokenString).length == 2)
            subTokenString = string.concat("000", tokenString);
        else if (bytes(tokenString).length == 3)
            subTokenString = string.concat("00", tokenString);
        else if (bytes(tokenString).length == 4)
            subTokenString = string.concat("0", tokenString);
        else
            subTokenString = tokenString;

        string memory resultString = string.concat(subTokenString, shapeCodes[tokenId]);
        return resultString;
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function safeMint(address to, uint256 tokenId, string memory shapeCode) public onlyOwner {
        require(bytes(shapeCodes[tokenId]).length == 0, "This token is currently existing");
        require(bytes(shapeCode).length > 0, "New base URI cannot be an empty string");
        
        allTokenIdIndex[tokenId] = allTokenIds.length;
        allTokenIds.push(tokenId);
        ownedTokenIndex[tokenId] = ownedTokens[to].length;
        ownedTokens[to].push(tokenId);
        if (bytes(shapeCodes[tokenId]).length > 0)
            delete shapeCodes[tokenId];
        shapeCodes[tokenId] = shapeCode;

        _safeMint(to, tokenId);
        string memory initialTokenURI = tokenURI(tokenId);
        _setTokenURI(tokenId, initialTokenURI);

        uint8 royalityInInt = uint8(bytes(shapeCode)[7]) - 48;
        uint96 royalityInPercent = uint96(royalityInInt) * 100;
        setRoyaltyInfo(tokenId, owner(), royalityInPercent);
    }

    function putTokenUpForSale(uint256 tokenId, string memory shapeCode, uint256 value) 
        public 
        whenNotPaused 
        checkAuthorized(ownerOf(tokenId)) 
        checkTokenIdExists(tokenId)
    {
        require(bytes(tokenOnSaleList[tokenId].tokenShapeCode).length == 0, "This token is already on the sale list, can not sell again");
        require(value > 0 && value < type(uint256).max, "Value is out of bound");
        checkValidShapeCodeId(tokenId, shapeCode);
        checkTransformLogic(tokenId, shapeCode);

        delete shapeCodes[tokenId];
        shapeCodes[tokenId] = shapeCode;
        onSaleTokenIdIndex[tokenId] = onSaleTokenIds.length;
        onSaleTokenIds.push(tokenId);

        TokenOnSale storage tempStruct = tokenOnSaleList[tokenId];
        tempStruct.tokenShapeCode = shapeCode;
        tempStruct.tokenValue = value;

        constructNewFlatToken(tokenId, shapeCode, value);

        string memory newSaleTokenString = string.concat(tokenOnSaleFlatList[tokenId][0], tokenOnSaleFlatList[tokenId][1], tokenOnSaleFlatList[tokenId][2]);
        onSaleTokenStringIndex[newSaleTokenString] = onSaleTokenStrings.length;
        onSaleTokenStrings.push(newSaleTokenString);

        address onwerOfTokenId = ownerOf(tokenId);
        individualOnSaleIndex[tokenId] = individualOnSaleList[onwerOfTokenId].length;
        individualOnSaleList[onwerOfTokenId].push(tokenId);
    }

    function getTokenSaleList() public view returns (uint256[] memory) {
        return onSaleTokenIds;
    }

    function getSaleDetailList() 
        public 
        view 
        returns (string[] memory) 
    {
        return onSaleTokenStrings;
    }

    function getSubOwnerOfSaleItem(uint256 tokenId)
        public 
        view 
        returns (string memory)
    {
        address ownerOfTokenId = ownerOf(tokenId);
        bytes memory tempTokenOwner = bytes(Strings.toHexString(ownerOfTokenId));
        bytes memory tempTokenOwnerHead = new bytes(9);
        bytes memory tempTokenOwnerTail = new bytes(9);
        for (uint256 i = 0; i < 9; i++) {
            tempTokenOwnerHead[i] = tempTokenOwner[i];
            tempTokenOwnerTail[i] = tempTokenOwner[33 + i];
        }
        string memory subOwnerofTokenId = string.concat(string(tempTokenOwnerHead), "...", string(tempTokenOwnerTail));
        return subOwnerofTokenId;
    }

    function getIndividualOnSaleList(address walletAddress)
        public 
        view 
        returns (uint256[] memory)
    {
        return individualOnSaleList[walletAddress];
    }

    function adjustSalePrice(uint256 tokenId, uint256 newPrice) 
        public 
        whenNotPaused 
        checkAuthorized(ownerOf(tokenId)) 
        checkTokenIdExists(tokenId)
    {
        require(bytes(tokenOnSaleList[tokenId].tokenShapeCode).length > 0, "This token is not on the sale list");
        require(newPrice > 0 && newPrice < type(uint256).max, "New price is out of bound");

        delete tokenOnSaleList[tokenId].tokenValue;
        tokenOnSaleList[tokenId].tokenValue = newPrice;
        delete tokenOnSaleFlatList[tokenId][2];
        bytes memory valueString = bytes(Strings.toString(newPrice));
        uint256 subValueLength = valueString.length - 9;
        bytes memory subValueString = new bytes(subValueLength);
        for (uint256 i = 0; i < subValueLength; i++) {
            subValueString[i] = valueString[i];
        }
        tokenOnSaleFlatList[tokenId][2] = string(subValueString);

        bytes memory saleTokenString = bytes(onSaleTokenStrings[onSaleTokenIdIndex[tokenId]]);
        bytes memory subSaleTokenString = new bytes(15);
        for (uint256 i = 0; i < 15; i++) {
            subSaleTokenString[i] = saleTokenString[i];
        }
        string memory newSaleTokenString = string.concat(string(subSaleTokenString), string(subValueString));
        onSaleTokenStrings[onSaleTokenIdIndex[tokenId]] = newSaleTokenString;
    }

    function putTokenBackToStore(uint256 tokenId) 
        public 
        whenNotPaused 
        checkAuthorized(ownerOf(tokenId)) 
        checkTokenIdExists(tokenId)
    {
        require(bytes(tokenOnSaleList[tokenId].tokenShapeCode).length > 0, "This token is not on the sale list");
        removeOnSaleToken(tokenId);
        delete tokenOnSaleList[tokenId];
        delete tokenOnSaleFlatList[tokenId];
    }

    function pickUpSaleToken(uint256 tokenId) 
        public 
        whenNotPaused 
        checkTokenIdExists(tokenId) 
        payable 
    {
        address msgSender = _msgSender();
        address tokenOldOwner = ownerOf(tokenId);
        require(msgSender != tokenOldOwner && msgSender != address(0), "The requester is invalid.");
        require(bytes(tokenOnSaleList[tokenId].tokenShapeCode).length > 0, "This token is not on the sale list");
        uint256 pickUpValue = tokenOnSaleList[tokenId].tokenValue;
        require(msg.value < msgSender.balance && msg.value == pickUpValue, "Insufficient funds.");

        removeOnSaleToken(tokenId);
        delete tokenOnSaleList[tokenId];
        delete tokenOnSaleFlatList[tokenId];

        removeOwnedToken(tokenOldOwner, tokenId);
        ownedTokenIndex[tokenId] = ownedTokens[msgSender].length;
        ownedTokens[msgSender].push(tokenId);

        isTokenTransferred = 1;
        _transfer(tokenOldOwner, msgSender, tokenId);
        isTokenTransferred = 0;

        (address receiver, uint256 amount) = royaltyCalculation(tokenId, pickUpValue);
        if (amount != 0)
        {
            (bool success1, ) = payable(receiver).call{value: amount}("");
            require(success1, "Royalty payment failed.");
        }

        uint256 pickUpValueRemaining = pickUpValue - amount;
        payable(tokenOldOwner).transfer(pickUpValueRemaining);
    }

    function _transfer(address from, address to, uint256 tokenId) 
        internal 
        checkTokenIdExists(tokenId) 
        override 
    {
        // Used for purchase outside the game
        if (isTokenTransferred == 0) {
            if (bytes(tokenOnSaleList[tokenId].tokenShapeCode).length > 0) {
                removeOnSaleToken(tokenId);
                delete tokenOnSaleList[tokenId];
                delete tokenOnSaleFlatList[tokenId];

                removeOwnedToken(from, tokenId);
                ownedTokenIndex[tokenId] = ownedTokens[to].length;
                ownedTokens[to].push(tokenId);

                (address receiver, uint256 amount) = royaltyCalculation(tokenId, msg.value);
                if (amount != 0)
                {
                    (bool success1, ) = payable(receiver).call{value: amount}("");
                    require(success1, "Royalty payment failed.");
                }
            }
        }

        super._transfer(from, to, tokenId);
    }

    function transformToken(uint256 tokenId, string memory shapeCode) 
        public 
        whenNotPaused 
        checkAuthorized(ownerOf(tokenId)) 
        checkTokenIdExists(tokenId)
    {
        require(bytes(tokenOnSaleList[tokenId].tokenShapeCode).length == 0, "This token is on the sale list, can not transform");
        checkValidShapeCodeId(tokenId, shapeCode);

        address tokenOwner = ownerOf(tokenId);
        uint256 numberOfOwnedTokens = ownedTokens[tokenOwner].length;
        destroyToken(tokenId);
        transformedMint(tokenOwner, tokenId, shapeCode, numberOfOwnedTokens);
    }

    function destroyToken(uint256 tokenId) 
        public 
        checkAuthorized(ownerOf(tokenId)) 
        checkTokenIdExists(tokenId) 
    {
        address tokenOwner = ownerOf(tokenId);
        if (bytes(tokenOnSaleList[tokenId].tokenShapeCode).length > 0) {
            removeOnSaleToken(tokenId);
            delete tokenOnSaleList[tokenId];
            delete tokenOnSaleFlatList[tokenId];
        }
        removeOwnedToken(tokenOwner, tokenId);
        removeTokenInAll(tokenId);
        _burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        checkTokenIdExists(tokenId)
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        string memory currentBaseURI;
        if (tokenId > newTokenIdAugment) {
            currentBaseURI = _baseURITransformed();
        } else {
            currentBaseURI = _baseURI();
        }

        string memory tokenIdInString;
        if (tokenId < newTokenIdAugment) {
            if (tokenId <= 1584) {
                uint256 remainder1 = tokenId % 16;
                if (remainder1 != 0) {
                    uint256 base = remainder1 / 2;
                    uint256 remainder2 = remainder1 % 2;
                    if (remainder2 == 1) {
                        tokenIdInString = (base + 1).toString();
                    } else {
                        tokenIdInString = base.toString();
                    }
                } else {
                    tokenIdInString = (remainder1 + 8).toString();
                }
                
            } else {
                uint256 remainder1 = tokenId % 16;
                if (remainder1 != 0) {
                    uint256 base = remainder1 / 2;
                    uint256 remainder2 = remainder1 % 2;
                    if (remainder2 == 1) {
                        tokenIdInString = (base + 9).toString();
                    } else {
                        tokenIdInString = (base + 8).toString();
                    }
                } else {
                    tokenIdInString = (remainder1 + 16).toString();
                }
            }
        } else {
            tokenIdInString = tokenId.toString();
        }

        return bytes(currentBaseURI).length > 0 ? string(abi.encodePacked(currentBaseURI, tokenIdInString, baseExtension)): "";
    }

