// File: contracts/nft-airdrop/CryptoGogosPolygon.sol


pragma solidity ^0.8.4;








interface IERC20 {
	function transfer(address recipient, uint256 amount)
		external
		returns (bool);

	function transferFrom(
		address sender,
		address recipient,
		uint256 amount
	) external returns (bool);

	function balanceOf(address account) external view returns (uint256);
}

contract CryptoGogosPolygon is
	ERC721,
	ERC721Enumerable,
	ERC721Burnable,
	ERC721Pausable,
	Ownable
{
	uint256 private _tokenIds;
	uint256 public maxSupply = 0;
	uint256 public price = 0;
	uint256 public packPrice = 0;
	uint256 public premintPrice = 0;
	uint256 public premintPackPrice = 0;
	uint256 public priceGogo = 0;
	uint256 public packPriceGogo = 0;
	uint256 public premintPriceGogo = 0;
	uint256 public premintPackPriceGogo = 0;

	uint256 public oldCollectionAmount = 0;

	bool public isInitialized = false;
	string baseURI;

	IERC20 gogo;
	IERC20 usdc;
	mapping(address => bool) public whitelisted;
	mapping(uint256 => uint256) public tiers;

	uint256 premintStartDate;
	uint256 premintEndDate;
	uint256 pubmintStartDate;

	struct OldCollection {
		address account;
		uint256 tokenId;
	}

	struct Tier {
		uint256 tokenId;
		uint256 tier;
	}

	address public tierUpdater;
	IUniswapV2Router02 public immutable router;

	constructor(
		string memory _name,
		string memory _symbol,
		string memory _newBaseURI,
		address _tierUpdater,
		address _gogo,
		address _usdc,
		uint256 _maxSupply,
		address _router
	) ERC721(_name, _symbol) {
		baseURI = _newBaseURI;
		gogo = IERC20(_gogo);
		usdc = IERC20(_usdc);
		tierUpdater = _tierUpdater;
		maxSupply = _maxSupply;
		router = IUniswapV2Router02(_router);
		_pause(); // contract must be first initialized
	}

	function init(
		uint256 _mintedAmount,
		uint256 _price,
		uint256 _packPrice,
		uint256 _premintPrice,
		uint256 _premintPackPrice,
		uint256 _priceGogo,
		uint256 _packPriceGogo,
		uint256 _premintPriceGogo,
		uint256 _premintPackPriceGogo,
		uint256 _premintStartDate,
		uint256 _premintPeriodDays,
		uint256 _pubmintStartDate
	) public onlyOwner {
		require(!isInitialized, "Contract is already initialized");
		require(_mintedAmount > 0, "invalid amount");
		require(
			_price > 0 &&
				_packPrice > 0 &&
				_premintPrice > 0 &&
				_premintPackPrice > 0 &&
				_priceGogo > 0 &&
				_packPriceGogo > 0 &&
				_premintPriceGogo > 0 &&
				_premintPackPriceGogo > 0,
			"invalid price"
		);

		_tokenIds = _mintedAmount;
		oldCollectionAmount = _mintedAmount;
		price = _price;
		packPrice = _packPrice;
		premintPrice = _premintPrice;
		premintPackPrice = _premintPackPrice;
		priceGogo = _priceGogo;
		packPriceGogo = _packPriceGogo;
		premintPriceGogo = _premintPriceGogo;
		premintPackPriceGogo = _premintPackPriceGogo;
		premintStartDate = _premintStartDate;
		premintEndDate = _premintStartDate + (_premintPeriodDays * 1 days);
		pubmintStartDate = _pubmintStartDate;

		require(premintEndDate <= pubmintStartDate);

		_unpause();

		isInitialized = true;

		emit PriceUpdated(_price, _packPrice, _premintPrice, _premintPackPrice);
	}

	function mint() external payable isMintActive isSenderHuman {
		if (isPremintActive()) {
			require(
				whitelisted[msg.sender] && balanceOf(msg.sender) < 3,
				"user is not allowed to mint"
			);
			require(msg.value >= premintPrice, "ether value too low");
		} else {
			require(msg.value >= price, "ether value too low");
		}
		swap(msg.value);
		_mintNew(msg.sender);
	}

	function mintPack() external payable isMintActive isSenderHuman {
		if (isPremintActive()) {
			require(
				whitelisted[msg.sender] && balanceOf(msg.sender) + 3 <= 3,
				"user is not allowed to mint"
			);
			require(msg.value >= premintPackPrice, "ether value too low");
		} else {
			require(msg.value >= packPrice, "ether value too low");
		}
		swap(msg.value);
		for (uint256 i = 0; i < 3; i++) {
			_mintNew(msg.sender);
		}
	}

	function mintGogo() external isMintActive isSenderHuman {
		if (isPremintActive()) {
			require(
				whitelisted[msg.sender] && balanceOf(msg.sender) < 3,
				"user is not allowed to mint"
			);
			gogo.transferFrom(msg.sender, address(this), premintPriceGogo);
		} else {
			gogo.transferFrom(msg.sender, address(this), priceGogo);
		}
		_mintNew(msg.sender);
	}

	function mintPackGogo() external isMintActive isSenderHuman {
		if (isPremintActive()) {
			require(
				whitelisted[msg.sender] && balanceOf(msg.sender) + 3 <= 3,
				"user is not allowed to mint"
			);
			gogo.transferFrom(msg.sender, address(this), premintPackPriceGogo);
		} else {
			gogo.transferFrom(msg.sender, address(this), packPriceGogo);
		}
		for (uint256 i = 0; i < 3; i++) {
			_mintNew(msg.sender);
		}
	}

	function _mintNew(address to) internal whenNotPaused wasInitialized {
		_tokenIds++;
		require(_tokenIds <= maxSupply, "supply limit reached");
		_mint(to, _tokenIds);
	}

	function supportsInterface(bytes4 interfaceId)
		public
		view
		override(ERC721, ERC721Enumerable)
		returns (bool)
	{
		return super.supportsInterface(interfaceId);
	}

	// ============ Views ============

	function isPremintActive() public view returns (bool) {
		return ((block.timestamp >= premintStartDate) &&
			(block.timestamp <= premintEndDate) &&
			(premintStartDate > 0));
	}

	function isPubmintActive() public view returns (bool) {
		return (block.timestamp >= pubmintStartDate) && (pubmintStartDate > 0);
	}

	function getCurrentSupply() public view returns (uint256) {
		return _tokenIds;
	}

	function getSinglePrice() public view returns (uint256) {
		if (isPremintActive()) return premintPrice;
		return price;
	}

	function getPackPrice() public view returns (uint256) {
		if (isPremintActive()) return premintPackPrice;
		return packPrice;
	}

	function getSinglePriceGogo() public view returns (uint256) {
		if (isPremintActive()) return premintPriceGogo;
		return priceGogo;
	}

	function getPackPriceGogo() public view returns (uint256) {
		if (isPremintActive()) return premintPackPriceGogo;
		return packPriceGogo;
	}

	function _baseURI() internal view override returns (string memory) {
		return baseURI;
	}

	function getTier(uint256 tokenId) public view returns (uint256) {
		return tiers[tokenId];
	}

	// =========== Modifier ===========
	modifier wasInitialized {
		require(isInitialized, "contract needs to be initialized");
		_;
	}

	modifier isMintActive {
		require(isPremintActive() || isPubmintActive(), "Mint is inactive");
		_;
	}

	modifier isSenderHuman {
		require(
			isHuman(msg.sender),
			"sender account has to be an external owned"
		);
		_;
	}

	// ============ Restricted ==========

	function airdropOldCollection(
		OldCollection[] memory data,
		uint256 gogoAmount
	) external onlyOwner wasInitialized {
		for (uint256 i = 0; i < data.length; i++) {
			require(
				data[i].tokenId > 0 && data[i].tokenId <= oldCollectionAmount,
				"invalid token id"
			);
			_mint(data[i].account, data[i].tokenId);
			if (gogoAmount > 0) gogoAirdrop(data[i].account, gogoAmount);
		}
	}

	function airdrop(address[] memory accounts) external onlyOwner {
		for (uint256 i = 0; i < accounts.length; i++) {
			_mintNew(accounts[i]);
		}
	}

	function airdropNftAndGogo(address[] memory accounts, uint256 gogoAmount)
		external
		onlyOwner
	{
		for (uint256 i = 0; i < accounts.length; i++) {
			_mintNew(accounts[i]);
			if (gogoAmount > 0) gogoAirdrop(accounts[i], gogoAmount);
		}
	}

	function gogoAirdrop(address to, uint256 amount) internal {
		gogo.transfer(to, amount);
	}

	function mintByAdmin(address to, uint256 amount) external onlyOwner {
		for (uint256 i = 0; i < amount; i++) {
			_mintNew(to);
		}
	}

	function updatePrice(
		uint256 newSinglePrice,
		uint256 newPackPrice,
		uint256 newPremintPrice,
		uint256 newPremintPackPrice
	) external onlyOwner {
		price = newSinglePrice;
		packPrice = newPackPrice;
		premintPrice = newPremintPrice;
		premintPackPrice = newPremintPackPrice;
		emit PriceUpdated(
			newSinglePrice,
			newPackPrice,
			newPremintPrice,
			newPremintPackPrice
		);
	}

	function updatePriceGogo(
		uint256 newSinglePriceGogo,
		uint256 newPackPriceGogo,
		uint256 newPremintPriceGogo,
		uint256 newPremintPackPriceGogo
	) external onlyOwner {
		priceGogo = newSinglePriceGogo;
		packPriceGogo = newPackPriceGogo;
		premintPriceGogo = newPremintPriceGogo;
		premintPackPriceGogo = newPremintPackPriceGogo;
		emit GogoPriceUpdated(
			newSinglePriceGogo,
			newPackPriceGogo,
			newPremintPriceGogo,
			newPremintPackPriceGogo
		);
	}

	function updateMaxSupply(uint256 newSupply) external onlyOwner {
		maxSupply = newSupply;
		emit MaxSupplyUpdated(newSupply);
	}

	function updateBaseURI(string memory _newBaseURI) external onlyOwner {
		baseURI = _newBaseURI;
	}

	function erc20Withdraw(address token) external onlyOwner {
		IERC20(token).transfer(
			msg.sender,
			IERC20(token).balanceOf(address(this))
		);
	}

	function withdraw() external onlyOwner {
		uint256 balance = address(this).balance;
		payable(msg.sender).transfer(balance);
	}

	function withdrawGogo() external onlyOwner {
		uint256 balance = gogo.balanceOf(address(this));
		gogo.transfer(msg.sender, balance);
	}

	function withdrawAll() external onlyOwner {
		uint256 ethBalance = address(this).balance;
		payable(msg.sender).transfer(ethBalance);
		uint256 balance = gogo.balanceOf(address(this));
		gogo.transfer(msg.sender, balance);
	}

	function pause() external onlyOwner {
		_pause();
	}

	function unpause() external onlyOwner {
		_unpause();
	}

	function isHuman(address account) internal view returns (bool) {
		uint256 size;
		address addr = account;
		assembly {
			size := extcodesize(addr)
		}
		return size == 0 && tx.origin == account;
	}

	function whitelistMultipleUsers(address[] memory _userlist)
		external
		onlyOwner
	{
		for (uint256 i = 0; i < _userlist.length; i++) {
			whitelisted[_userlist[i]] = true;
		}
	}

	function whitelistUser(address _user) external onlyOwner {
		whitelisted[_user] = true;
	}

	function removeWhitelistUser(address _user) external onlyOwner {
		whitelisted[_user] = false;
	}

	function setPremintStartDate(uint256 _premintStartDate) external onlyOwner {
		premintStartDate = _premintStartDate;
	}

	function setPremintEndDate(uint256 _premintEndDate) external onlyOwner {
		premintEndDate = _premintEndDate;
	}

	function setPubmintStartDate(uint256 _pubmintStartDate) external onlyOwner {
		pubmintStartDate = _pubmintStartDate;
	}

	function setMaxSupply(uint256 _maxSupply) external onlyOwner {
		maxSupply = _maxSupply;
	}

	function setTierUpdater(address _tierUpdater) external onlyOwner {
		tierUpdater = _tierUpdater;
	}

	function addTier(uint256 tokenId, uint256 tier) external {
		require(
			msg.sender == tierUpdater || msg.sender == owner(),
			"sender is not allowed to call this function"
		);
		tiers[tokenId] = tier;
	}

	function addMultipleTiers(Tier[] memory _tiers) external {
		require(
			msg.sender == tierUpdater || msg.sender == owner(),
			"sender is not allowed to call this function"
		);

		for (uint256 i; i < _tiers.length; i++) {
			tiers[_tiers[i].tokenId] = _tiers[i].tier;
		}
	}

	// swap gogo

	function swap(uint256 amount) internal {
		address[] memory path = new address[](3);
		path[0] = router.WETH();
		path[1] = address(usdc);
		path[2] = address(gogo);

		router.swapExactETHForTokensSupportingFeeOnTransferTokens{
			value: amount
		}(
			0, //amountOutMin
			path,
			address(this),
			block.timestamp
		);
	}

	// ============ Events ============

	event MaxSupplyUpdated(uint256 maxSupply);
	event PriceUpdated(
		uint256 newSinglePrice,
		uint256 newPackPrice,
		uint256 newPremintPrice,
		uint256 newPremintPackPrice
	);
	event GogoPriceUpdated(
		uint256 newSinglePriceGogo,
		uint256 newPackPriceGogo,
		uint256 newPremintPriceGogo,
		uint256 newPremintPackPriceGogo
	);

	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 tokenId
	) internal virtual override(ERC721Pausable, ERC721, ERC721Enumerable) {
		super._beforeTokenTransfer(from, to, tokenId);
		require(!paused(), "cannot transfer when contract is paused");
	}
}
