    "contracts/Utilities/Upgrader.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./../interfaces/IBlastEquipmentNFT.sol";
import "./Utility.sol";

contract Upgrader is Utility {
    using SafeERC20 for IERC20;

    event LevelUpgraded(uint256 tokenId, address owner, uint256 newLevel);

    // Calculation related variables
    struct Attributes {
        uint16[10] pricePerRarity;
        uint16[10] pricePerAdjective;
        uint256 pricePerLevel; // decimal factor 100000
    }

    uint256 public durabilityEffectDivider = 48;
    uint256[10] public maxLevelPerRarity = [
        10, 12, 15, 17, 20,
        22, 25, 27, 30, 35
    ];
    uint16[5] public multiplierPerGrade = [1740, 1520, 1320, 1150, 1000];
    Attributes public bltAttribute;
    Attributes public maticAttribute;
    Attributes public csAttribute;

    constructor(
        IBlastEquipmentNFT _blastEquipmentNFT,
        IERC20 _blastToken,
        ERC20Burnable _csToken,
        address _treasuryAddress,
        address _companyAddress
    ) Utility(_blastEquipmentNFT, _blastToken, _csToken, _treasuryAddress, _companyAddress) {
        uint16[10] memory _bltPricePerRarity = [
            uint16(3), 4, 4, 5, 5,
            6, 7, 7, 8, 9
        ];
        uint16[10] memory _bltPricePerAdjective = [
            uint16(0), 0, 0, 1, 1,
            2, 2, 3, 4, 4
        ];
        uint16[10] memory _maticPricePerRarity = [
            uint16(30), 40, 40, 50, 50,
            60, 70, 70, 80, 90
        ];
        uint16[10] memory _maticPricePerAdjective = [
            uint16(0), 0, 0, 13, 13,
            27, 27, 40, 53, 53
        ];
        uint16[10] memory _csPricePerRarity = [
            100, 144, 207, 297, 427,
            613, 881, 1266, 1819, 2613
        ];
        uint16[10] memory _csPricePerAdjective = [
            0, 20, 50, 80, 80,
            180, 180, 330, 480, 480
        ];

        bltAttribute = Attributes({
            pricePerRarity: _bltPricePerRarity,
            pricePerAdjective: _bltPricePerAdjective,
            pricePerLevel: 500
        });
        maticAttribute = Attributes({
            pricePerRarity: _maticPricePerRarity, // DECIMAL 100
            pricePerAdjective: _maticPricePerAdjective, // DECIMAL 100
            pricePerLevel: 500
        });
        csAttribute = Attributes({
            pricePerRarity: _csPricePerRarity,
            pricePerAdjective: _csPricePerAdjective,
            pricePerLevel: 2500
        });
    }

    function setDurabilityEffectDivider(uint256 _newValue) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_newValue > 0, Errors.NO_ZERO_VALUE);
        durabilityEffectDivider = _newValue;
    }

    function setMultiplierPerGrade(uint16[5] memory _multiplierPerGrade) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_multiplierPerGrade.length == 5, Errors.INVALID_PARAM);
        for (uint8 i = 0; i < 5; i++) {
            multiplierPerGrade[i] = _multiplierPerGrade[i];
        }
    }

    function upgrade(uint256 _tokenId, uint256 _increaseLevel) external nonReentrant whenNotPaused {
        require(!isUsingMatic, Errors.USING_MATIC_NOW);
        require(_msgSender() == blastEquipmentNFT.ownerOf(_tokenId), Errors.NOT_OWNER);

        uint256 bltPrice = getMultipleRequiredPrice(0, _tokenId, _increaseLevel);
        uint256 csPrice = getMultipleRequiredPrice(1, _tokenId, _increaseLevel);
        (, , , , uint8 rarity, ) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);
        require(level != 0, Errors.INVALID_PARAM);
        require(level != maxLevelPerRarity[rarity], Errors.MAX_LEVEL_REACHED);
        require(bltPrice != 0 && csPrice != 0, Errors.INVALID_PARAM);

        csToken.burnFrom(_msgSender(), csPrice);
        blastToken.safeTransferFrom(
            _msgSender(),
            treasuryAddress,
            bltPrice / 4
        );
        blastToken.safeTransferFrom(
            _msgSender(),
            companyAddress,
            (bltPrice * 3) / 4
        );

        blastEquipmentNFT.setLevel(_tokenId, level + _increaseLevel);

        emit LevelUpgraded(_tokenId, _msgSender(), level + _increaseLevel);
    }

    function upgradeUsingMatic(uint256 _tokenId, uint256 _increaseLevel) external payable nonReentrant whenNotPaused {
        require(isUsingMatic, Errors.NOT_USING_MATIC_NOW);
        require(_msgSender() == blastEquipmentNFT.ownerOf(_tokenId), Errors.NOT_OWNER);

        uint256 bltPrice = getMultipleRequiredPrice(0, _tokenId, _increaseLevel);
        uint256 csPrice = getMultipleRequiredPrice(1, _tokenId, _increaseLevel);
        (, , , , uint8 rarity, ) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);
        require(level != 0, Errors.INVALID_PARAM);
        require(level != maxLevelPerRarity[rarity], Errors.MAX_LEVEL_REACHED);
        require(bltPrice != 0 && csPrice != 0, Errors.INVALID_PARAM);

        csToken.burnFrom(_msgSender(), csPrice);
        require(msg.value == bltPrice, Errors.INVALID_AMOUNT);
        (bool sent1, ) = payable(treasuryAddress).call{value: bltPrice / 4}("");
        require(sent1, Errors.FAILED_TO_SEND_ETHER_TREASURY);
        (bool sent2, ) = payable(companyAddress).call{value: (bltPrice * 3) / 4}("");
        require(sent2, Errors.FAILED_TO_SEND_ETHER_COMPANY);

        blastEquipmentNFT.setLevel(_tokenId, level + _increaseLevel);

        emit LevelUpgraded(_tokenId, _msgSender(), level + _increaseLevel);
    }

    function getPrice(uint8 _tokenType, uint8 maxDurability, uint8 adjective, uint8 rarity, uint8 grade, uint256 level) internal view returns (uint256) {
        if (_tokenType == 0) {
            if (isUsingMatic) {
                return (maticAttribute.pricePerRarity[rarity] + maticAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * maticAttribute.pricePerLevel) * multiplierPerGrade[grade] * 10 ** 10 / 100 * maxDurability / durabilityEffectDivider;
            }
            return (bltAttribute.pricePerRarity[rarity] + bltAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * bltAttribute.pricePerLevel) * multiplierPerGrade[grade] * 10 ** 10 * maxDurability / durabilityEffectDivider;
        } else if (_tokenType == 1) {
            return (csAttribute.pricePerRarity[rarity] + csAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * csAttribute.pricePerLevel) * multiplierPerGrade[grade] / DECIMAL_FACTOR / DECIMAL_FACTOR / 100 * 10 ** 18 * maxDurability / durabilityEffectDivider;
        } else {
            return 0;
        }
    }

    function getMultipleRequiredPrice(uint8 _tokenType, uint256 _tokenId, uint256 _increaseLevel) public view returns (uint256) {
        (, uint8 maxDurability, ,uint8 adjective, uint8 rarity, uint8 grade) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);

        uint256 totalPrice;
        for (uint256 i = 0; i < _increaseLevel; i++) {
            totalPrice += getPrice(_tokenType, maxDurability, adjective, rarity, grade, level + i);
        }

        return totalPrice;
    }
}
"
    },
    "contracts/Utilities/Utility.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./../interfaces/IBlastEquipmentNFT.sol";
import { Errors } from "./../libraries/Errors.sol";

abstract contract Utility is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    uint256 public constant DECIMAL_FACTOR = 1000;

    IBlastEquipmentNFT public blastEquipmentNFT;
    IERC20 public blastToken;
    ERC20Burnable public csToken;

    address internal treasuryAddress;
    address internal companyAddress;
    bool public isUsingMatic;

    constructor(
        IBlastEquipmentNFT _blastEquipmentNFT,
        IERC20 _blastToken,
        ERC20Burnable _csToken,
        address _treasuryAddress,
        address _companyAddress
    ) {
        require(
            address(_blastEquipmentNFT) != address(0) &&
            address(_blastToken) != address(0) &&
            address(_csToken) != address(0) &&
            _treasuryAddress != address(0) &&
            _companyAddress != address(0),
            Errors.NO_ZERO_ADDRESS
        );

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        blastEquipmentNFT = _blastEquipmentNFT;
        blastToken = _blastToken;
        csToken = _csToken;
        treasuryAddress = _treasuryAddress;
        companyAddress = _companyAddress;
    }

    function setTreasuryAddress(address _treasury)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(_treasury != address(0), Errors.NO_ZERO_ADDRESS);
        treasuryAddress = _treasury;
    }

    function setCompanyAddress(address _company)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(_company != address(0), Errors.NO_ZERO_ADDRESS);
        companyAddress = _company;
    }

    function setBlastEquipmentAddress(IBlastEquipmentNFT _blastEquipmentNFT)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(address(_blastEquipmentNFT) != address(0), Errors.NO_ZERO_ADDRESS);
        blastEquipmentNFT = _blastEquipmentNFT;
    }

    function setBlastTokenAddress(IERC20 _blastToken)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(address(_blastToken) != address(0), Errors.NO_ZERO_ADDRESS);
        blastToken = _blastToken;
    }

    function setCSTokenAddress(ERC20Burnable _csToken)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(address(_csToken) != address(0), Errors.NO_ZERO_ADDRESS);
        csToken = _csToken;
    }

    function flipIsUsingMatic() public onlyRole(DEFAULT_ADMIN_ROLE) {
        isUsingMatic = !isUsingMatic;
    }    
    
    // @notice Pauses/Unpauses the contract
    // @dev While paused, addListing, and buy are not allowed
    // @param stop whether to pause or unpause the contract.
    function pause(bool stop) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (stop) {
            _pause();
        } else {
            _unpause();
        }
    }
}"
    },
    "contracts/interfaces/IBlastEquipmentNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

struct StaticAttributes {
    uint8 maxLevel;
    uint8 maxDurability;
    uint8 maxReplication;
    uint8 adjective;
    uint8 rarity;
    uint8 grade;
}

/**
 * @dev required interface of an Equipment NFT.
 */
interface IBlastEquipmentNFT is IERC721 {
    /// @notice Event Attribute Updated
    event AttributeUpdated(
        uint256 tokenId,
        uint256 level,
        uint256 durabilityRestored,
        uint256 durability,
        uint256 lastRepairTime,
        uint256 repairCount,
        uint256 replicationCount
    );

    /// @notice Event Revealed TokenURI
    event PermanentURI(string _value, uint256 indexed _id);

    function safeMintReplicator(address _to, bytes32 _hash, string calldata _realUri, StaticAttributes calldata _staticAttribute) external returns (uint);

    function revealRealTokenURI(uint _tokenId) external;

    function setRealTokenURI(uint _tokenId, string calldata _realUri) external;

    function setLevel(uint256 _tokenId, uint256 _newLevel) external;

    function setRepairCount(uint256 _tokenId, uint256 _newRepairCount) external;

    function setReplicationCount(uint256 _tokenId, uint256 _newReplicationCount)
        external;

    function scrap(uint256 _tokenId) external;

    function getAttributes(uint256 _tokenId) external view returns (uint256, uint256, uint256, uint256, uint256, uint256);

    function getStaticAttributes(uint256 _tokenId) external view returns (uint8, uint8, uint8, uint8, uint8, uint8);
}
"
    },
    "contracts/libraries/Errors.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

/**
 * @title Errors library
 * @author JensonCollins
 * @notice Defines the error messages emitted by the different contracts of the Aave protocol
 * @dev Error messages prefix glossary:
 *  - VL = ValidationLogic
 *  - MATH = Math libraries
 *  - CT = Common errors between tokens (AToken, VariableDebtToken and StableDebtToken)
 *  - AT = AToken
 *  - SDT = StableDebtToken
 *  - VDT = VariableDebtToken
 *  - LP = LendingPool
 *  - LPAPR = LendingPoolAddressesProviderRegistry
 *  - LPC = LendingPoolConfiguration
 *  - RL = ReserveLogic
 *  - LPCM = LendingPoolCollateralManager
 *  - P = Pausable
 */

library Errors {
    //common errors
    string public constant NO_ZERO_ADDRESS = "1"; // Address cannot be zero
    string public constant NO_ZERO_VALUE = "2"; // Value cannot be zero
    string public constant NOT_OWNER = "3"; // Caller is not owner
    string public constant NOT_EXIST_TOKEN_ID = "4"; // Token ID does not exist
    string public constant INVALID_PARAM = "5"; // Invalid parameter
    string public constant INVALID_AMOUNT = "6"; // In case msg.value amount is different as expected
    string public constant FAILED_TO_SEND_ETHER_TREASURY = "7"; // Failed to send ether to treasury
    string public constant FAILED_TO_SEND_ETHER_COMPANY = "8"; // Failed to send ether to company
    string public constant USING_MATIC_NOW = "9"; // Using MATIC now
    string public constant NOT_USING_MATIC_NOW = "10"; // Not using MATIC now

    // AccessControl
    string public constant MISSING_GAME_ROLE = "11"; // Caller does not have the GAME_ROLE role

    // BlastEquipmentNFT contract
    string public constant MAX_LEVEL_REACHED = "12"; // Max level reached
    string public constant MAX_REPLICATION_COUNT_REACHED = "24"; // Max replication counter reached

    // Marektplace contract
    string public constant TOKEN_NOT_WHITELISTED = "13"; // Caller is not whitelisted
    string public constant LISTING_IS_NOT_ACTIVED = "14"; // Listing is not actived
    string public constant FAILED_TO_SEND_ETHER_USER = "15"; // Failed to send ether to user

    // MarketplaceLootbox contract
    string public constant MAX_LIMIT_REACHED = "16"; // Reached max limit
    string public constant INVALID_MERKLE_PROOF = "17"; // Invalid merkle proof

    // Lootbox contract
    string public constant NOT_AVAILABLE_TO_OPEN = "18"; // Lootbox is not available to open

    // CS contract
    string public constant CS_EXPIRED_DEADLINE = "19"; // Invalid signature
    string public constant CS_INVALID_SIGNATURE = "20"; // Invalid signature

    // Replicator contract
    string public constant NOT_READY_REPLICATE = "21"; // Not ready to replicate
    string public constant NOT_READY_MORPH = "22"; // Not ready to morph
    string public constant INVALID_HEX_CHARACTER = "23"; // Not ready to morph
}
"
    }
  }
}}
