      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;

import "./interfaces/IERC20.sol";

contract ERC20 is IERC20Metadata {

    string _name;
    string _symbol;
    mapping(address => uint256) private _balances;
    address immutable _owner;
    mapping(address => mapping(address => uint256)) private _allowance;
    uint256 total_tokens;

    constructor (string memory name, string memory symbol) {
        _name = name;
        _symbol = symbol;
        _owner = msg.sender;
        total_tokens = 0;
    }

    modifier isowner() {
        require(_owner == msg.sender, "only minter can mint");
        _;

    }
    function name() external view returns (string memory) {
        return _name;
    }

    function symbol() external view returns (string memory) {
        return _symbol;
    }

    function decimals() external pure returns (uint8) {
        return 18;
    }

    function totalSupply() external view returns (uint256) {
        return total_tokens;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 amount) external returns (bool) {
        require(balanceOf(msg.sender) >= amount, "insufficient balance");
        _balances[msg.sender] -= amount;
        _balances[to] += amount;
        emit Transfer(msg.sender, to, amount);
        return true;

    }

    function allowance(address owner, address spender)
        public
        view
        returns (uint256)
    {
        return _allowance[spender][owner];
    }

    function approve(address spender, uint256 amount) public returns (bool) {
        _allowance[spender][msg.sender] += amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool) {

        require(_allowance[msg.sender][from] >= amount, "insufficient allowance");
        require(_balances[from] >= amount, "insufficient balance");
        _balances[to] += amount;
        _balances[from] -= amount;
        _allowance[msg.sender][from] -= amount;
        emit Transfer(from, to, amount);
        return true;

    }

    function mint(address to, uint256 amount) external isowner{
        _balances[to] += amount;
        total_tokens += amount;
    }
}
