pragma solidity ^0.8.0;

import "./Ownable.sol";
import "./ERC20Pausable.sol";
import "./ERC20Burnable.sol";
import "./ERC20Freezable.sol";

contract ELIS is Ownable, ERC20Pausable, ERC20Burnable, ERC20Freezable {
    /**
     * @dev Returns the address of the current owner.
     */
    constructor(string memory name, string memory symbol) ERC20(name, symbol) {
        _mint(msg.sender, 100000000 ether / 1 wei);
    }

    /**
     * @dev Admin function to mint tokens to an address.
     *
     * See {ERC20-_mint}
     */
    function mint(address to, uint256 amount) public onlyAdmin {
        _mint(to, amount);
    }

    /**
     * @dev See {ERC20-_beforeTokenTransfer}.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Pausable, ERC20Freezable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    fallback() external {
        revert();
    }
}
