        //1 block every 3 sec
        //28800 block per day
        //201600 block per 7 days
        //864000  block per 30 days

        blocksScroll = _blocksScroll;

        rewardPerBlock = accRewardNextTrigger / blocksScroll;

    }



    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        view
        returns (uint256)
    {
        if (_to <= bonusEndBlock) {
            return _to - _from;
        } else if (_from >= bonusEndBlock) {
            return 0;
        } else {
            return bonusEndBlock - _from;
        }
    }

    // View function to see pending Reward on frontend.
    function pendingReward(address _user) external view returns (uint256) {

        UserInfo storage user = userInfo[_user];
        uint256 accTokenPerShare = poolInfo.accTokenPerShare;

        if (block.number > poolInfo.lastRewardBlock && totalStakedAmount != 0) {
            uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);
            uint256 wtkReward = ( multiplier * rewardPerBlock * poolInfo.allocPoint ) / totalAllocPoint ;
            accTokenPerShare += (wtkReward * 1e12) / totalStakedAmount ;
        }
        return ((user.amount * accTokenPerShare) / 1e12) - user.rewardDebt;
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {

        if (block.number <= poolInfo.lastRewardBlock) {
            return;
        }

        if (totalStakedAmount == 0) {
            poolInfo.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);
        uint256 wtkReward =  (multiplier * rewardPerBlock * poolInfo.allocPoint) / totalAllocPoint ;

        poolInfo.accTokenPerShare += (wtkReward * 1e12) / totalStakedAmount;
        poolInfo.lastRewardBlock = block.number;
    }



    function deposit(uint256 _amount) external whenNotPaused {
     
        UserInfo storage user = userInfo[msg.sender];

        uint256 pending = 0;

        updatePool();
        if (user.amount > 0) {
            pending = ((user.amount * poolInfo.accTokenPerShare) / 1e12) -  user.rewardDebt;
            if (pending > 0) {

                totRewDelivered += pending;

                if(currentRewardAmount > pending){
                    currentRewardAmount -=pending;
                }else{
                    currentRewardAmount =0;
                }

                totalStakedAmount -=user.amount;
                user.amount =0;

                tokenERC20.mint(address(msg.sender), pending);

            }

        }


        if (_amount > 0) {

            user.amount +=_amount;

            totalStakedAmount += _amount;

            tokenERC20.burnFrom(address(msg.sender), _amount);

        }
        user.rewardDebt = (user.amount * poolInfo.accTokenPerShare) / 1e12;

        emit Deposit(msg.sender, _amount, pending);
    }





    function withdraw() external whenNotPaused {

        UserInfo storage user = userInfo[msg.sender];
        require(user.amount > 0, "Stk: withdraw: not good user deposit = 0");
        
        uint256 withdrawn =0;
        updatePool();
        uint256 pending = ((user.amount * poolInfo.accTokenPerShare) / 1e12) - user.rewardDebt ;

        if (pending > 0) {

            totRewDelivered += pending;
            if(currentRewardAmount > pending){
                currentRewardAmount -=pending;
            }else{
                currentRewardAmount =0;
            }

            withdrawn = user.amount;
            totalStakedAmount -=withdrawn;
            user.amount =0;

            tokenERC20.mint(address(msg.sender), pending);

        }

        user.rewardDebt = (user.amount * poolInfo.accTokenPerShare) / 1e12;

        emit Withdraw(msg.sender, withdrawn, pending);
    }








    
    function updateEmissionRate(uint256 _rewardPerBlock) external onlyRole(ADMIN_ROLE) {
        updatePool();
        rewardPerBlock = _rewardPerBlock;
    }

    // Update the given pool's WDEFI allocation point and deposit fee. Can only be called by the owner.
    function set(uint256 _allocPoint, bool _withUpdate) external onlyRole(ADMIN_ROLE) {

        require(_allocPoint > 0 && _allocPoint <= totalAllocPoint,"Stk: Invalid value!");

        if (_withUpdate) {
            updatePool();
        }


        poolInfo.allocPoint = _allocPoint;
    }


    function recoverStolenFund(address _user) external onlyRole(ADMIN_ROLE) {
       
        UserInfo storage user = userInfo[_user];

        totalStakedAmount -= user.amount;
        uint256 oldAmount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        emit StolenFundRecovered(msg.sender, oldAmount);
    }



    function getRewardBalance() external view returns(uint256){

        return currentRewardAmount;
    }


//********************************************************************************************************************************

    function stopReward() external onlyRole(ADMIN_ROLE) {
        bonusEndBlock = block.number - 1;
    }



    function extendRange(uint256 newBonusEndNlock) external onlyRole(ADMIN_ROLE) {
        require(newBonusEndNlock > poolInfo.lastRewardBlock,"Invalid block number" );

        bonusEndBlock = newBonusEndNlock;
    }


    function setNewTriggerValue(uint256 newValue) external onlyRole(ADMIN_ROLE){

        require(newValue>1e12,"Invalid block number" );

        accRewardNextTrigger = newValue;

    }

    function setNewBlocksScrollValue(uint256 newValue) external onlyRole(ADMIN_ROLE){

        require(newValue>1,"Stk: Invalid block number" );

        blocksScroll = newValue;

    }


    function _receiveForReward(uint256 amount) private {
        accRewardNextPeriod += amount;
        currentRewardAmount += amount;

        if(  accRewardNextPeriod >= accRewardNextTrigger ){
            moveToNextPeriod();
        }

    }

    function updateFromContract(uint256 amount) external onlyRole(ADMIN_ROLE)  {
        
        _receiveForReward( amount);
    }


    function receiveFromContract(uint256 amount) external {

        updatePool();

        _receiveForReward( amount);
        

        tokenERC20.burnFrom(address(msg.sender), amount);


    }


    function moveToNextPeriod() internal {

        updatePool();
        
        uint256 pend_bl_qty=0;

        if(bonusEndBlock > poolInfo.lastRewardBlock){
            pend_bl_qty = bonusEndBlock - poolInfo.lastRewardBlock;
        }

        uint256 tot_pend_reward= accRewardNextPeriod + ( rewardPerBlock * pend_bl_qty  )  ;


        rewardPerBlock = tot_pend_reward / blocksScroll;

        bonusEndBlock = poolInfo.lastRewardBlock + blocksScroll;

       emit Moved(poolInfo.lastRewardBlock, bonusEndBlock,accRewardNextPeriod, rewardPerBlock); 

       accRewardNextPeriod = 0;


    }


    function pause() public onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(ADMIN_ROLE) {
        _unpause();
    }

}
