    "src/v1/modules/FairMysteryBoxModule.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {ERC1155Mtx, ERC1155TokenReceiver} from "channels/v1/utils/ERC1155Mtx.sol";
import {NativeMetaTransaction} from "channels/v1/utils/NativeMetaTransaction.sol";
import {ModuleManager, Enum} from "@safe-contracts/contracts/base/ModuleManager.sol";
import {IMasterfileToken} from "channels/v1/drops/MasterfileToken.i.sol";
import {IFairMysteryBoxModule, MysteryBox, Item, VRFRequest} from "./FairMysteryBoxModule.i.sol";
import {IMasterfileFulfiller} from "../fulfiller/MasterfileFulfiller.i.sol";

contract FairMysteryBoxModule is
    ERC1155Mtx,
    ERC1155TokenReceiver,
    NativeMetaTransaction,
    IFairMysteryBoxModule
{
    using Strings for uint256;

    address immutable _channel;
    IERC20 immutable _usdc;
    address _fulfiller;

    string _baseUri;

    mapping(uint256 => MysteryBox) _mysteryBoxes;
    mapping(uint256 => mapping(uint256 => Item)) _items;
    mapping(uint256 => VRFRequest) _vrfRequests;
    mapping(address => uint256) _userNonces;

    constructor(
        address channel_,
        address usdc_,
        address fulfiller_,
        string memory baseUri_
    ) {
        _channel = channel_;
        _usdc = IERC20(usdc_);
        _fulfiller = fulfiller_;
        _baseUri = baseUri_;
        __NativeMetaTransaction_init("FAIR_MYSTERY_BOX_MODULE");
    }

    modifier onlyChannel() {
        if (msg.sender != _channel) revert Unauthorized();
        _;
    }

    modifier onlyFulfiller() {
        if (msg.sender != _fulfiller) revert Unauthorized();
        _;
    }

    // =============================================================
    //                          MINTING
    // =============================================================

    function createMysteryBox(
        uint256 id_,
        uint256 quantity_,
        MysteryBox memory mysteryBox_,
        Item[] memory items_
    ) public onlyChannel {
        if (_mysteryBoxes[id_].initialized == 1) revert InvalidId();

        if (mysteryBox_.startDate > mysteryBox_.endDate)
            revert InvalidDropWindow();

        mysteryBox_.initialized = 1;
        _mysteryBoxes[id_] = mysteryBox_;

        emit MysteryBoxSet(
            id_,
            mysteryBox_.recipient,
            mysteryBox_.price,
            mysteryBox_.startDate,
            mysteryBox_.endDate,
            mysteryBox_.redemptions
        );

        uint256 totalProbability;
        for (uint256 i = 0; i < items_.length; i++) {
            // Not checking drop address or edition number
            // Assume they are valid since we are providing them
            totalProbability += items_[i].probability;
            _items[id_][i] = items_[i];

            emit MysteryBoxItemSet(
                id_,
                i,
                items_[i].drop,
                items_[i].tokenIdentifier,
                items_[i].probability,
                items_[i].quantity
            );
        }
        if (totalProbability != 100_00) revert InvalidProbabilities();

        _mint(address(this), id_, quantity_, "");
    }

    function mintAdditional(uint256 id_, uint256 quantity_) public onlyChannel {
        if (_mysteryBoxes[id_].initialized == 0) revert InvalidId();
        _mint(address(this), id_, quantity_, "");
    }

    function burnAdditional(uint256 id_, uint256 quantity_) public onlyChannel {
        if (_mysteryBoxes[id_].initialized == 0) revert InvalidId();
        _burn(address(this), id_, quantity_);
    }

    // =============================================================
    //                          BUYING
    // =============================================================

    function gift(
        address recipient_,
        uint256 id_,
        uint256 quantity_
    ) public onlyChannel {
        if (_mysteryBoxes[id_].initialized == 0) revert InvalidId();
        _internalTransfer(recipient_, id_, quantity_, "");
    }

    function purchaseFor(
        address recipient_,
        uint256 id_,
        uint256 quantity_
    ) public {
        MysteryBox storage _mysteryBox = _mysteryBoxes[id_];

        if (_mysteryBox.initialized == 0) revert InvalidId();
        if (block.timestamp < _mysteryBox.startDate) revert DropNotStarted();
        if (block.timestamp > _mysteryBox.endDate) revert DropConcluded();

        if (balanceOf[address(this)][id_] < quantity_) revert SoldOut();

        uint256 totalPrice = _mysteryBox.price * quantity_;

        emit MysteryBoxPurchased(
            id_,
            _msgSender(),
            recipient_,
            quantity_,
            totalPrice
        );

        _usdc.transferFrom(_msgSender(), _mysteryBox.recipient, totalPrice);

        _internalTransfer(recipient_, id_, quantity_, "");
    }

    function purchase(uint256 id_, uint256 quantity_) public {
        purchaseFor(_msgSender(), id_, quantity_);
    }

    // =============================================================
    //                          REVEALING
    // =============================================================

    function reveal(uint256 id_, uint256 nonce_) external {
        address recipient = _msgSender();

        if (balanceOf[recipient][id_] == 0) revert InvalidId();
        MysteryBox storage _mysteryBox = _mysteryBoxes[id_];

        if (nonce_ <= _userNonces[recipient]) revert InvalidNonce();

        _burn(recipient, id_, 1);

        _userNonces[recipient] = nonce_;

        uint256 requestId = IMasterfileFulfiller(_fulfiller).requestRandomWords(
            id_,
            recipient,
            nonce_,
            _mysteryBox.redemptions
        );

        _vrfRequests[requestId] = VRFRequest(id_, recipient, nonce_, 1);

        emit MysteryBoxRevealed(id_, requestId, recipient);
    }

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) external onlyFulfiller {
        VRFRequest storage _vrfRequest = _vrfRequests[requestId];
        if (_vrfRequest.status != 1) revert InvalidVRFRequest();

        MysteryBox storage _mysteryBox = _mysteryBoxes[
            _vrfRequest.mysteryBoxId
        ];

        // assumption that mysteryBox.redemptions hasn't changed since reveal
        for (uint256 i = 0; i < _mysteryBox.redemptions; i++) {
            uint256 randomness = randomWords[i] % 100_00;

            Item memory _item;
            uint256 itemIndex;
            uint256 cumulativeProbability;

            while (cumulativeProbability <= randomness) {
                _item = _items[_vrfRequest.mysteryBoxId][itemIndex];
                cumulativeProbability += _item.probability;
                itemIndex++;
            }

            emit MysteryBoxItemRedeemed(
                _vrfRequest.mysteryBoxId,
                requestId,
                _vrfRequest.recipient,
                itemIndex - 1 // -1 because we increment before checking
            );

            bytes memory data = abi.encodeWithSelector(
                IMasterfileToken.mint.selector,
                _vrfRequest.recipient,
                _item.tokenIdentifier,
                _item.quantity
            );
            _vrfRequest.status = 2; // fulfilled
            executeTransaction(_item.drop, data);
        }
    }

    // =============================================================
    //                          GETTERS
    // =============================================================

    function uri(uint256 id) public view override returns (string memory) {
        if (_mysteryBoxes[id].initialized == 0) revert InvalidId();
        return string.concat(_baseUri, id.toString());
    }

    function mysteryBox(
        uint256 id
    ) public view returns (MysteryBox memory box) {
        box = _mysteryBoxes[id];

        if (box.initialized == 0) revert InvalidId();
    }

    function userNonce(address recipient) public view returns (uint256 nonce) {
        nonce = _userNonces[recipient];
    }

    function fulfiller() public view returns (address) {
        return _fulfiller;
    }

    function vrfRequest(
        uint256 requestId
    ) public view returns (VRFRequest memory) {
        return _vrfRequests[requestId];
    }

    // =============================================================
    //                        ADMIN SETTERS
    // =============================================================

    function updateMysteryBox(
        uint256 id_,
        MysteryBox memory mysteryBox_
    ) public onlyChannel {
        if (_mysteryBoxes[id_].initialized == 0) revert InvalidId();

        if (mysteryBox_.startDate > mysteryBox_.endDate)
            revert InvalidDropWindow();

        mysteryBox_.initialized = 1;
        _mysteryBoxes[id_] = mysteryBox_;

        emit MysteryBoxSet(
            id_,
            mysteryBox_.recipient,
            mysteryBox_.price,
            mysteryBox_.startDate,
            mysteryBox_.endDate,
            mysteryBox_.redemptions
        );
    }

    function updateItems(uint256 id_, Item[] memory items_) public onlyChannel {
        // Need to check probability here

        if (_mysteryBoxes[id_].initialized == 0) revert InvalidId();

        uint256 totalProbability;
        for (uint256 i = 0; i < items_.length; i++) {
            // Not checking drop address or edition number
            // Assume they are valid since we are providing them
            totalProbability += items_[i].probability;
            _items[id_][i] = items_[i];

            emit MysteryBoxItemSet(
                id_,
                i,
                items_[i].drop,
                items_[i].tokenIdentifier,
                items_[i].probability,
                items_[i].quantity
            );
        }
        if (totalProbability != 100_00) revert InvalidProbabilities();
    }

    function updateFulfiller(address newFulfiller) public onlyChannel {
        _fulfiller = newFulfiller;
    }

    function updateBaseUri(string memory baseUri_) public onlyChannel {
        _baseUri = baseUri_;
    }

    // =============================================================
    //                          INTERNAL
    // =============================================================

    // TODO: Moved to shared library
    function executeTransaction(address dest, bytes memory data) internal {
        if (
            !ModuleManager(_channel).execTransactionFromModule(
                dest,
                0,
                data,
                Enum.Operation.Call
            )
        ) revert TransactionFailed();
    }

    function _internalTransfer(
        address to_,
        uint256 id_,
        uint256 amount_,
        bytes memory data_
    ) internal {
        balanceOf[address(this)][id_] -= amount_;
        balanceOf[to_][id_] += amount_;

        emit TransferSingle(msg.sender, address(this), to_, id_, amount_);

        require(
            to_.code.length == 0
                ? to_ != address(0)
                : ERC1155TokenReceiver(to_).onERC1155Received(
                    msg.sender,
                    address(this),
                    id_,
                    amount_,
                    data_
                ) == ERC1155TokenReceiver.onERC1155Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    // Receiver so we can mint to ourselves, rejects outside transfers

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) public view override returns (bytes4) {
        if (msg.sender != address(this)) return bytes4(0);

        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address,
        uint256[] calldata,
        uint256[] calldata,
        bytes calldata
    ) public view override returns (bytes4) {
        if (msg.sender != address(this)) return bytes4(0);

        return this.onERC1155BatchReceived.selector;
    }

    function _msgSenderERC1155Mtx() internal view override returns (address) {
        return _msgSender();
    }
}
"
