    "src/v1/utils/NativeMetaTransaction.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {NativeMetaTransactionStorage} from "./NativeMetaTransactionStorage.sol";

abstract contract NativeMetaTransaction {
    using NativeMetaTransactionStorage for NativeMetaTransactionStorage.Layout;

    // keccak256("MetaTransaction(uint256 nonce,address from,bytes functionSignature)")
    bytes32 public constant META_TRANSACTION_TYPEHASH =
        0x23d10def3caacba2e4042e0c75d44a42d2558aabcf5ce951d0642a8032e1e653;

    error Initialized();
    error InvalidSignature();
    error MetaTransactionFailed();

    event MetaTransactionExecuted(
        address userAddress,
        address payable relayerAddress,
        bytes functionSignature
    );

    struct MetaTransaction {
        uint256 nonce;
        address from;
        bytes functionSignature;
    }

    function __NativeMetaTransaction_init(string memory name) internal {
        if (
            keccak256(bytes(NativeMetaTransactionStorage.layout()._name)) !=
            keccak256(bytes(""))
        ) revert Initialized();
        NativeMetaTransactionStorage.layout()._name = name;
        NativeMetaTransactionStorage.layout()._INITIAL_CHAIN_ID = block.chainid;
        NativeMetaTransactionStorage
            .layout()
            ._DOMAIN_SEPARATOR = _computeDomainSeparator();
    }

    function getNonce(address user) public view returns (uint256 nonce) {
        return NativeMetaTransactionStorage.layout()._nonce[user];
    }

    function executeMetaTransaction(
        address user,
        bytes memory functionSignature,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) external payable returns (bytes memory) {
        uint256 _nonce = NativeMetaTransactionStorage.layout()._nonce[user]++;

        MetaTransaction memory metaTx = MetaTransaction({
            nonce: _nonce,
            from: user,
            functionSignature: functionSignature
        });

        // Verify signature
        if (!_verify(user, metaTx, r, s, v)) revert InvalidSignature();

        emit MetaTransactionExecuted(
            user,
            payable(msg.sender),
            functionSignature
        );

        // Append userAddress and relayer address at the end to extract it from calling context
        // solhint-disable-next-line avoid-low-level-calls
        (bool success, bytes memory returnData) = address(this).call(
            abi.encodePacked(functionSignature, user)
        );

        if (!success) revert MetaTransactionFailed();

        return returnData;
    }

    function _verify(
        address signer,
        MetaTransaction memory metaTx,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) internal view returns (bool) {
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR(),
                keccak256(
                    abi.encode(
                        META_TRANSACTION_TYPEHASH,
                        metaTx.nonce,
                        metaTx.from,
                        keccak256(metaTx.functionSignature)
                    )
                )
            )
        );

        address recovered = ecrecover(digest, v, r, s);

        return signer == recovered;
    }

    function _computeDomainSeparator() internal view virtual returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    keccak256(
                        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                    ),
                    keccak256(
                        bytes(NativeMetaTransactionStorage.layout()._name)
                    ),
                    keccak256("1"),
                    block.chainid,
                    address(this)
                )
            );
    }

    function _msgSender() internal view virtual returns (address sender) {
        if (msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(
                    mload(add(array, index)),
                    0xffffffffffffffffffffffffffffffffffffffff
                )
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }

    function DOMAIN_SEPARATOR() public view virtual returns (bytes32) {
        // Check to see if chainid has updated due to fork
        return
            block.chainid ==
                NativeMetaTransactionStorage.layout()._INITIAL_CHAIN_ID
                ? NativeMetaTransactionStorage.layout()._DOMAIN_SEPARATOR
                : _computeDomainSeparator();
    }
}
"
