    "src/v1/modules/FairMysteryBoxModule.i.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {ERC1155, ERC1155TokenReceiver} from "@rari-capital/solmate/src/tokens/ERC1155.sol";
import {ModuleManager, Enum} from "@safe-contracts/contracts/base/ModuleManager.sol";
import {VRFConsumerBaseV2} from "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";
import {VRFCoordinatorV2Interface} from "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";

import {NativeMetaTransaction} from "channels/v1/utils/NativeMetaTransaction.sol";
import {IMasterfileToken} from "channels/v1/drops/MasterfileToken.i.sol";

struct MysteryBox {
    address recipient;
    uint256 price;
    /// Start date of the drop (unix seconds timestamp)
    uint64 startDate;
    /// End date of the drop (unix seconds timestamp)
    uint64 endDate;
    uint24 redemptions;
    uint8 initialized;
}

struct Item {
    address drop;
    uint24 tokenIdentifier;
    uint24 probability;
    uint24 quantity;
}

struct VRFConsumerProps {
    uint32 callbackGasLimit;
    bytes32 keyHash;
    uint16 requestConfirmations;
    uint64 subscriptionId;
    address vrfCoordinator;
}

struct VRFRequest {
    uint256 mysteryBoxId;
    address recipient;
    uint256 nonce;
    // 0 - not-requested, 1 - requested, 2 - fulfilled
    uint8 status;
}

interface FairMysteryBoxModuleEvents {
    event MysteryBoxSet(
        uint256 indexed id,
        address indexed recipient,
        uint256 price,
        uint64 startDate,
        uint64 endDate,
        uint24 redemptions
    );
    event MysteryBoxItemSet(
        uint256 indexed id,
        uint256 indexed index,
        address indexed drop,
        uint24 tokenIdentifier,
        uint24 probability,
        uint24 quantity
    );
    event MysteryBoxPurchased(
        uint256 indexed id,
        address indexed funder,
        address indexed recipient,
        uint256 quantity,
        uint256 totalPrice
    );
    event MysteryBoxRevealed(
        uint256 indexed id,
        uint256 indexed requestId,
        address indexed recipient
    );
    event MysteryBoxItemRedeemed(
        uint256 indexed id,
        uint256 indexed requestId,
        address indexed recipient,
        uint256 itemIndex
    );
}

interface IFairMysteryBoxModule is FairMysteryBoxModuleEvents {
    error Unauthorized();
    error InvalidId();
    error InvalidItem();
    error InvalidProbabilities();
    error InvalidDropWindow();
    error SoldOut();
    error DropNotStarted();
    error DropConcluded();
    error TransactionFailed();
    error InvalidNonce();
    error InvalidVRFRequest();

    function mysteryBox(
        uint256 id
    ) external view returns (MysteryBox memory box);

    function createMysteryBox(
        uint256 id_,
        uint256 quantity_,
        MysteryBox memory mysteryBox_,
        Item[] memory items_
    ) external;

    function mintAdditional(uint256 id_, uint256 quantity_) external;

    function burnAdditional(uint256 id_, uint256 quantity_) external;

    function updateMysteryBox(
        uint256 id_,
        MysteryBox memory mysteryBox_
    ) external;

    function updateItems(uint256 id_, Item[] memory items_) external;

    function updateBaseUri(string memory baseUri_) external;

    function gift(address recipient_, uint256 id_, uint256 quantity_) external;

    function purchaseFor(
        address recipient_,
        uint256 id_,
        uint256 quantity_
    ) external;

    function purchase(uint256 id_, uint256 quantity_) external;

    function reveal(uint256 id_, uint256 nonce_) external;

    function fulfillRandomWords(
        uint256 requestId,
        uint256[] memory randomWords
    ) external;
}
"
    },
